// Serve the frontend (simplified)
async fn serve_frontend() -> Result<String, StatusCode> {
    Ok(r#"<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8">
    <title>BNB Chain Multi-Wallet Bundler</title>
    <style>
        body { 
            font-family: Arial, sans-serif; 
            margin: 0; 
            padding: 20px; 
            background: #0a0a0a; 
            color: #fff;
        }
