use leptos::*;
use leptos_meta::*;
use leptos_router::*;

mod components;
mod api;

use components::{WalletTable, WalletGenerator};
use bnb_bundler_shared::*;

#[component]
pub fn App() -> impl IntoView {
    // Provides context that manages the app's metadata
    provide_meta_context();

    view! {
        <Html lang="en"/>
        <Title text="BNB Chain Multi-Wallet Bundler"/>
        <Meta charset="utf-8"/>
        <Meta name="viewport" content="width=device-width, initial-scale=1"/>

        <Link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css"/>

        <Style>
            {include_str!("../styles/main.css")}
        </Style>

        <Router>
            <div class="app-container">
                <Sidebar/>
                <main class="main-content">
                    <Routes>
                        <Route path="" view=DashboardPage/>
                        <Route path="/bundler" view=BundlerPage/>
                    </Routes>
                </main>
            </div>
        </Router>
    }
}

#[component]
fn Sidebar() -> impl IntoView {
    view! {
        <aside class="sidebar">
            <div class="logo">
                <i class="fas fa-rocket"></i>
                <h1>"BNB Bundler"</h1>
                <span class="version">"v1.0"</span>
            </div>

            <div class="search-bar">
                <i class="fas fa-search"></i>
                <input type="text" placeholder="Search"/>
                <span class="shortcut">"‚åòK"</span>
            </div>

            <nav>
                <div class="nav-section">
                    <A class="nav-item" href="">
                        <i class="fas fa-chart-bar"></i>
                        <span>"Dashboard"</span>
                    </A>
                    <A class="nav-item" href="/bundler">
                        <i class="fas fa-layer-group"></i>
                        <span>"Bundler"</span>
                    </A>
                </div>

                <div class="nav-section">
                    <h3>"Features"</h3>
                    <div class="nav-item">
                        <i class="fas fa-wallet"></i>
                        <span>"Wallets"</span>
                        <span class="badge">"0"</span>
                    </div>
                    <div class="nav-item">
                        <i class="fas fa-shield-alt"></i>
                        <span>"Security"</span>
                        <span class="status">"Active"</span>
                    </div>
                </div>
            </nav>
        </aside>
    }
}

#[component]
fn DashboardPage() -> impl IntoView {
    let (health_status, set_health_status) = create_signal(None::<HealthResponse>);

    // Check health status on mount
    create_effect(move |_| {
        spawn_local(async move {
            match api::get_health().await {
                Ok(response) => {
                    if response.success {
                        set_health_status(response.data);
                    }
                },
                Err(e) => {
                    logging::error!("Failed to get health status: {}", e);
                }
            }
        });
    });

    view! {
        <header class="page-header">
            <h1 class="page-title">"Dashboard"</h1>
            <div class="status-indicators">
                <div class="status-dot"></div>
                <span>
                    {move || match health_status() {
                        Some(health) => health.status,
                        None => "Connecting...".to_string()
                    }}
                </span>
            </div>
        </header>

        <div class="stats-grid">
            <div class="stat-card">
                <div class="stat-value">"0"</div>
                <div class="stat-label">"Wallets Generated"</div>
            </div>
            <div class="stat-card">
                <div class="stat-value green">"0.0 BNB"</div>
                <div class="stat-label">"Total Balance"</div>
            </div>
            <div class="stat-card">
                <div class="stat-value orange">"0"</div>
                <div class="stat-label">"Transactions"</div>
            </div>
        </div>

        <div class="dashboard-content">
            <div class="network-info">
                <h3>"Network Information"</h3>
                {move || match health_status() {
                    Some(health) => view! {
                        <div class="network-details">
                            <p><strong>"Network: "</strong>{health.network}</p>
                            <p><strong>"Server: "</strong>{health.server}</p>
                            {health.block_number.map(|block| view! {
                                <p><strong>"Block Number: "</strong>{block.to_string()}</p>
                            })}
                            {health.gas_price_gwei.map(|gas| view! {
                                <p><strong>"Gas Price: "</strong>{gas.to_string()}" gwei"</p>
                            })}
                        </div>
                    }.into_view(),
                    None => view! {
                        <p>"Loading network information..."</p>
                    }.into_view()
                }}
            </div>
        </div>
    }
}

#[component]
fn BundlerPage() -> impl IntoView {
    view! {
        <header class="page-header">
            <h1 class="page-title">"Multi-Wallet Bundler"</h1>
        </header>

        <div class="bundler-grid">
            <div class="bundler-card">
                <WalletGenerator/>
            </div>
            <div class="bundler-card">
                <WalletTable/>
            </div>
        </div>
    }
}

#[wasm_bindgen::prelude::wasm_bindgen(start)]
pub fn main() {
    console_error_panic_hook::set_once();
    mount_to_body(|| view! { <App/> })
}

frontend/styles/main.css.content:
:root {
    --bg-primary: #0a0a0a;
    --bg-secondary: #1a1a1a;
    --bg-tertiary: #2a2a2a;
    --border-color: #333;
    --text-primary: #fff;
    --text-secondary: #888;
    --accent-green: #00ff88;
    --accent-orange: #ff6b35;
    --accent-red: #ff4444;
    --accent-blue: #3b82f6;
}

* { 
    margin: 0; 
    padding: 0; 
    box-sizing: border-box; 
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background: var(--bg-primary);
    color: var(--text-primary);
    height: 100vh;
    overflow: hidden;
}

.app-container {
    display: flex;
    height: 100vh;
}

/* Sidebar */
.sidebar {
    width: 280px;
    background: var(--bg-secondary);
    border-right: 1px solid var(--border-color);
    display: flex;
    flex-direction: column;
    padding: 20px;
}

.logo {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 30px;
    padding-bottom: 20px;
    border-bottom: 1px solid var(--border-color);
}

.logo i {
    width: 28px;
    height: 28px;
    background: var(--accent-green);
    color: var(--bg-primary);
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    font-size: 16px;
}

.logo h1 {
    font-size: 18px;
    font-weight: 600;
}

.version {
    background: var(--bg-tertiary);
    padding: 2px 8px;
    border-radius: 12px;
    font-size: 12px;
    color: var(--text-secondary);
    margin-left: auto;
}

.search-bar {
    position: relative;
    margin-bottom: 30px;
}

.search-bar input {
    width: 100%;
    background: var(--bg-tertiary);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    padding: 10px 15px 10px 40px;
    color: var(--text-primary);
    font-size: 14px;
}

.search-bar i {
    position: absolute;
    left: 15px;
    top: 50%;
    transform: translateY(-50%);
    color: var(--text-secondary);
}

.shortcut {
    position: absolute;
    right: 15px;
    top: 50%;
    transform: translateY(-50%);
    background: var(--border-color);
    padding: 2px 6px;
    border-radius: 4px;
    font-size: 10px;
    color: var(--text-secondary);
}

.nav-section {
    margin-bottom: 30px;
}

.nav-section h3 {
    font-size: 12px;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 1px;
    margin-bottom: 15px;
}

.nav-item {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 10px 12px;
    border-radius: 8px;
    cursor: pointer;
    transition: all 0.2s;
    margin-bottom: 5px;
    text-decoration: none;
    color: var(--text-primary);
}

.nav-item:hover {
    background: var(--bg-tertiary);
}

.nav-item.active {
    background: var(--accent-green);
    color: var(--bg-primary);
}

.nav-item i {
    width: 16px;
    font-size: 14px;
}

.badge {
    background: var(--accent-green);
    color: var(--bg-primary);
    padding: 2px 8px;
    border-radius: 10px;
    font-size: 12px;
    font-weight: 600;
    margin-left: auto;
}

.status {
    background: var(--accent-green);
    color: var(--bg-primary);
    padding: 2px 8px;
    border-radius: 10px;
    font-size: 10px;
    margin-left: auto;
}

/* Main Content */
.main-content {
    flex: 1;
    overflow-y: auto;
    padding: 30px;
}

.page-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 30px;
}

.page-title {
    font-size: 24px;
    font-weight: 600;
}

.status-indicators {
    display: flex;
    align-items: center;
    gap: 15px;
}

.status-dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: var(--accent-green);
    animation: pulse 2s infinite;
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

/* Stats Grid */
.stats-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 20px;
    margin-bottom: 30px;
}

.stat-card {
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 12px;
    padding: 20px;
    text-align: center;
}

.stat-value {
    font-size: 36px;
    font-weight: 700;
    margin-bottom: 8px;
}

.stat-value.green { color: var(--accent-green); }
.stat-value.orange { color: var(--accent-orange); }

.stat-label {
    color: var(--text-secondary);
    font-size: 14px;
}

/* Bundler Grid */
.bundler-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 20px;
    margin-bottom: 30px;
}

.bundler-card {
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 12px;
    padding: 20px;
}

.bundler-card h3 {
    color: var(--accent-green);
    margin-bottom: 20px;
    display: flex;
    align-items: center;
    gap: 8px;
}

/* Forms */
.form-group {
    margin-bottom: 15px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    color: var(--text-primary);
    font-size: 14px;
}

.form-group input {
    width: 100%;
    background: var(--bg-tertiary);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    padding: 12px;
    color: var(--text-primary);
    font-size: 14px;
}

.form-group input:focus {
    outline: none;
    border-color: var(--accent-green);
}

/* Buttons */
.button-group {
    display: flex;
    gap: 10px;
    margin-bottom: 20px;
}

.btn-primary {
    background: var(--accent-green);
    color: var(--bg-primary);
    border: none;
    padding: 12px 20px;
    border-radius: 8px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.2s;
    flex: 1;
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 20px rgba(0, 255, 136, 0.3);
}

.btn-primary:disabled {
    background: var(--bg-tertiary);
    color: var(--text-secondary);
    cursor: not-allowed;
    transform: none;
}

.btn-secondary {
    background: var(--bg-tertiary);
    color: var(--text-primary);
    border: 1px solid var(--border-color);
    padding: 12px 20px;
    border-radius: 8px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.2s;
    flex: 1;
}

.btn-secondary:hover {
    background: var(--border-color);
}

.btn-small {
    background: var(--bg-tertiary);
    color: var(--text-secondary);
    border: 1px solid var(--border-color);
    padding: 6px 8px;
    border-radius: 4px;
    cursor: pointer;
    font-size: 12px;
    margin-right: 5px;
}

.btn-small:hover {
    color: var(--text-primary);
    border-color: var(--accent-green);
}

/* Wallet Components */
.wallets-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 12px;
    margin-top: 16px;
}

.wallet-item {
    background: var(--bg-tertiary);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    padding: 12px;
}

.wallet-address {
    font-family: monospace;
    font-size: 12px;
    color: var(--text-secondary);
    margin-bottom: 4px;
    word-break: break-all;
}

.wallet-balance {
    color: var(--accent-green);
    font-weight: 600;
}

/* Table */
.wallet-table-container {
    background: var(--bg-tertiary);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    overflow: hidden;
    margin-bottom: 20px;
}

.wallet-table {
    width: 100%;
    border-collapse: collapse;
}

.wallet-table th,
.wallet-table td {
    padding: 12px;
    text-align: left;
    border-bottom: 1px solid var(--border-color);
}

.wallet-table th {
    background: var(--bg-secondary);
    font-weight: 600;
    color: var(--text-primary);
}

.wallet-table tr:hover {
    background: var(--bg-secondary);
}

.wallet-table tr.selected {
    background: rgba(0, 255, 136, 0.1);
}

.wallet-address-short {
    font-family: monospace;
    font-size: 12px;
}

.balance-cell {
    font-family: monospace;
}

.balance-positive {
    color: var(--accent-green);
}

.balance-zero {
    color: var(--text-secondary);
}

.date-cell {
    font-size: 12px;
    color: var(--text-secondary);
}

.selection-actions {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 15px;
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 8px;
}

.selection-count {
    color: var(--accent-green);
    font-weight: 600;
}

/* Alerts */
.alert {
    padding: 12px 16px;
    border-radius: 8px;
    margin-bottom: 16px;
    display: flex;
    align-items: center;
    gap: 8px;
}

.alert-error {
    background: rgba(255, 68, 68, 0.1);
    border: 1px solid var(--accent-red);
    color: var(--accent-red);
}

.alert-success {
    background: rgba(0, 255, 136, 0.1);
    border: 1px solid var(--accent-green);
    color: var(--accent-green);
}

/* Network Info */
.network-info {
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 12px;
    padding: 20px;
    margin-bottom: 20px;
}

.network-info h3 {
    margin-bottom: 15px;
    color: var(--accent-green);
}

.network-details p {
    margin-bottom: 8px;
    color: var(--text-secondary);
}

.network-details strong {
    color: var(--text-primary);
}

/* Responsive */
@media (max-width: 768px) {
    .sidebar { display: none; }
    .stats-grid { grid-template-columns: 1fr; }
    .bundler-grid { grid-template-columns: 1fr; }
    .button-group { flex-direction: column; }
    .wallet-table-container { overflow-x: auto; }
}

