/**
 * Comprehensive TypeScript types for the Multi-Wallet Bundler
 */

// Wallet Role Enum
export enum Role {
  DEV = 'dev',
  MEV = 'mev', 
  FUNDER = 'funder',
  NUMBERED = 'numbered'
}

// Wallet Interface
export interface Wallet {
  id: string;
  publicKey: string;
  privateKey?: string; // Only stored encrypted client-side
  address: string;
  balance: number;
  role: Role;
  createdAt: string;
  isActive: boolean;
  transactions?: Transaction[];
}

// Transaction Interface
export interface Transaction {
  id: string;
  hash: string;
  from: string;
  to: string;
  value: string;
  gasUsed: string;
  gasPrice: string;
  status: 'pending' | 'confirmed' | 'failed';
  timestamp: string;
  blockNumber?: number;
}

// Bundle Configuration
export interface BundleConfiguration {
  id?: string;
  name?: string;
  tokenName: string;
  tokenSymbol: string;
  tokenDescription: string;
  totalSupply: string;
  platform: 'pancakeswap' | 'uniswap' | 'other';
  fundingPlan: FundingPlan;
  sellPlan: SellPlan;
  walletCount: number;
  gasSettings: GasSettings;
  // Extended properties for enhanced configuration
  purchaseAmount?: {
    totalBnb: number;
    perWalletMin: number;
    perWalletMax: number;
    allocation: Record<string, number>;
  };
  strategy?: {
    buyStrategy: 'immediate' | 'staggered' | 'scaled';
    sellStrategy: 'hold' | 'gradual' | 'dump';
    sellDelay: number;
    sellPercentage: number;
    retainPercentage: number;
  };
  token?: {
    address: string;
    name: string;
    symbol: string;
    decimals: number;
    totalSupply: string;
    verified: boolean;
    contractValidated: boolean;
  };
  transactionSettings?: any;
  executionParams?: any;
}

// Funding Plan
export interface FundingPlan {
  totalFunding: number;
  fundingPerWallet: number;
  fundingCurrency: 'BNB' | 'ETH';
  distributionMethod: 'equal' | 'weighted' | 'random';
}

// Sell Plan
export interface SellPlan {
  sellPercentage: number;
  sellDelay: number; // seconds
  sellMethod: 'gradual' | 'immediate' | 'random';
  maxSlippage: number;
  retainPercentage: number;
}

// Gas Settings
export interface GasSettings {
  gasLimit: string;
  maxFeePerGas: string;
  maxPriorityFeePerGas: string;
  gasMultiplier: number;
}

// Bundle Result
export interface BundleResult {
  id: string;
  success: boolean;
  transactions: Transaction[];
  totalGasUsed: string;
  totalCost: string;
  profitLoss: string;
  executionTime: number; // milliseconds
  errors: string[];
  createdAt: string;
}

// API Response Wrappers
export interface ApiResponse<T> {
  success: boolean;
  data?: T;
  error?: string;
  timestamp: string;
}

export interface ApiError {
  code: string;
  message: string;
  details?: string;
}

// Health Check Response
export interface HealthResponse {
  status: string;
  timestamp: string;
  network: string;
  server: string;
  features: string[];
}

// Wallet Generation Request/Response
export interface GenerateWalletsRequest {
  count: number;
  roles?: Role[];
}

export interface GenerateWalletsResponse {
  wallets: Wallet[];
  seed?: string; // Optional seed for reproducibility
}

// Wallet Funding Request/Response
export interface FundWalletsRequest {
  wallets: string[]; // Array of wallet addresses
  amount: number;
