/**
 * GenerateWalletsDialog Component
 * Dialog for generating new BNB wallets with role assignment
 */

import React, { useState, useCallback } from 'react';
import { createPortal } from 'react-dom';
import { useForm } from 'react-hook-form';
import { useWalletStore } from '../../store/wallets';
import { useSessionStore } from '../../store/session';
import { Role } from '../../types';

interface GenerateWalletsFormData {
  count: number;
  passphrase: string;
  confirmPassphrase: string;
  assignRoles: boolean;
  defaultRole: Role;
  customRoles: Role[];
}

interface GenerateWalletsDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: (count: number) => void;
}

export const GenerateWalletsDialog: React.FC<GenerateWalletsDialogProps> = ({
  isOpen,
  onClose,
  onSuccess,
}) => {
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showAdvanced, setShowAdvanced] = useState(false);

  const generateWallets = useWalletStore(state => state.generateWallets);
  const { isUnlocked } = useSessionStore();

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors },
    reset,
  } = useForm<GenerateWalletsFormData>({
    defaultValues: {
      count: 5,
      passphrase: '',
      confirmPassphrase: '',
      assignRoles: false,
      defaultRole: Role.NUMBERED,
      customRoles: [],
    },
  });

  const watchCount = watch('count');
  const watchAssignRoles = watch('assignRoles');

  const roleOptions = [
    { value: Role.DEV, label: 'Developer', icon: 'üë®‚Äçüíª' },
    { value: Role.MEV, label: 'MEV Bot', icon: 'ü§ñ' },
    { value: Role.FUNDER, label: 'Funder', icon: 'üí∞' },
    { value: Role.NUMBERED, label: 'Numbered', icon: 'üî¢' },
  ];

  const handleClose = useCallback(() => {
    if (isGenerating) return;
    reset();
    setError(null);
    setShowAdvanced(false);
    onClose();
  }, [isGenerating, reset, onClose]);

  const onSubmit = useCallback(async (data: GenerateWalletsFormData) => {
    // No authentication required once logged in with access key
    const defaultPassphrase = 'justjewit2025'; // Use default passphrase for encryption

    setIsGenerating(true);
    setError(null);

    try {
      let roles: Role[] | undefined;

      if (data.assignRoles) {
        if (data.customRoles.length > 0) {
          // Use custom role distribution
          roles = [];
          for (let i = 0; i < data.count; i++) {
            roles.push(data.customRoles[i % data.customRoles.length]);
          }
        } else {
          // Use default role for all wallets
          roles = Array(data.count).fill(data.defaultRole);
        }
      }

      await generateWallets(data.count, defaultPassphrase, roles);

      onSuccess?.(data.count);
      handleClose();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to generate wallets';
      setError(errorMessage);
    } finally {
      setIsGenerating(false);
    }
  }, [isUnlocked, generateWallets, onSuccess, handleClose]);

  const handleOverlayClick = useCallback((event: React.MouseEvent) => {
    if (event.target === event.currentTarget) {
      handleClose();
    }
  }, [handleClose]);

  const addCustomRole = useCallback((role: Role) => {
    const currentRoles = watch('customRoles');
    if (!currentRoles.includes(role)) {
      setValue('customRoles', [...currentRoles, role]);
    }
  }, [watch, setValue]);

  const removeCustomRole = useCallback((roleToRemove: Role) => {
    const currentRoles = watch('customRoles');
    setValue('customRoles', currentRoles.filter(role => role !== roleToRemove));
  }, [watch, setValue]);

  if (!isOpen) return null;

  const dialogContent = (
    <div className="generate-wallets-overlay" onClick={handleOverlayClick}>
      <div className="generate-wallets-dialog">
        <div className="dialog-header">
          <h3 className="dialog-title">
            <span className="title-icon">‚ú®</span>
            Generate New Wallets
          </h3>
          {!isGenerating && (
            <button 
              className="dialog-close"
              onClick={handleClose}
              aria-label="Close dialog"
            >
              ‚úï
            </button>
          )}
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="generate-form">
          <div className="form-content">
            {/* Wallet Count */}
            <div className="form-group">
              <label htmlFor="count" className="form-label">
                Number of Wallets
                <span className="form-hint">How many wallets to generate (1-100)</span>
              </label>
              <input
                {...register('count', {
                  required: 'Number of wallets is required',
                  min: { value: 1, message: 'Must generate at least 1 wallet' },
                  max: { value: 100, message: 'Cannot generate more than 100 wallets at once' },
                  valueAsNumber: true,
                })}
                type="number"
                id="count"
                className="form-input"
                min="1"
                max="100"
                disabled={isGenerating}
              />
              {errors.count && (
                <span className="form-error">{errors.count.message}</span>
              )}
            </div>

            {/* Security Notice */}
            <div className="security-warning">
              <div className="warning-icon">‚úÖ</div>
              <div className="warning-content">
                <strong>Automatic Security:</strong> Wallets are automatically encrypted and secured with your access key authentication.
              </div>
            </div>

            {/* Role Assignment */}
            <div className="form-group">
              <div className="checkbox-group">
                <input
                  {...register('assignRoles')}
                  type="checkbox"
                  id="assignRoles"
                  className="form-checkbox"
                  disabled={isGenerating}
                />
                <label htmlFor="assignRoles" className="checkbox-label">
                  Assign specific roles to wallets
                  <span className="form-hint">Otherwise, all wallets will be numbered</span>
                </label>
              </div>
            </div>

            {/* Role Configuration */}
            {watchAssignRoles && (
              <div className="role-configuration">
                <div className="form-group">
                  <label className="form-label">Default Role</label>
                  <select
                    {...register('defaultRole')}
                    className="form-select"
                    disabled={isGenerating}
                  >
                    {roleOptions.map(option => (
                      <option key={option.value} value={option.value}>
                        {option.icon} {option.label}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="advanced-toggle">
                  <button
                    type="button"
                    className="toggle-advanced"
                    onClick={() => setShowAdvanced(!showAdvanced)}
                  >
                    {showAdvanced ? '‚ñº' : '‚ñ∂'} Advanced Role Distribution
                  </button>
                </div>

                {showAdvanced && (
                  <div className="custom-roles">
                    <label className="form-label">
                      Custom Role Pattern
                      <span className="form-hint">
                        Roles will be assigned in the order you select, repeating as needed
                      </span>
                    </label>

                    <div className="role-selector-grid">
                      {roleOptions.map(option => (
                        <button
                          key={option.value}
                          type="button"
                          className="role-option-btn"
                          onClick={() => addCustomRole(option.value)}
                          disabled={isGenerating}
                        >
                          {option.icon} {option.label}
                        </button>
                      ))}
                    </div>

                    {watch('customRoles').length > 0 && (
                      <div className="selected-roles">
                        <label className="form-label">Selected Pattern:</label>
                        <div className="role-tags">
                          {watch('customRoles').map((role, index) => (
                            <div key={`${role}-${index}`} className="role-tag">
                              <span>{roleOptions.find(opt => opt.value === role)?.icon} {role}</span>
                              <button
                                type="button"
                                className="remove-role"
                                onClick={() => removeCustomRole(role)}
                                disabled={isGenerating}
                              >
                                ‚úï
                              </button>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>
            )}

            {/* Generation Preview */}
            <div className="generation-preview">
              <h4>Generation Preview</h4>
              <div className="preview-stats">
                <div className="preview-stat">
                  <strong>Wallets to generate:</strong> {watchCount}
                </div>
                <div className="preview-stat">
                  <strong>Role assignment:</strong> {watchAssignRoles ? 'Custom' : 'All numbered'}
                </div>
                <div className="preview-stat">
                  <strong>Security:</strong> Client-side encrypted private keys
                </div>
              </div>
            </div>

            {/* Error Message */}
            {error && (
              <div className="form-error-banner">
                <span className="error-icon">‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            {/* Session Status */}
            {!isUnlocked && (
              <div className="session-warning">
                <span className="warning-icon">üîí</span>
                Session must be unlocked to generate wallets
              </div>
            )}
          </div>

          <div className="dialog-actions">
            <button
              type="button"
              className="btn-secondary"
              onClick={handleClose}
              disabled={isGenerating}
            >
              Cancel
            </button>

            <button
              type="submit"
              className="btn-primary"
              disabled={isGenerating || !isUnlocked}
            >
              {isGenerating ? (
                <>
                  <span className="loading-spinner"></span>
                  Generating {watchCount} wallets...
                </>
              ) : (
                <>
                  ‚ú® Generate {watchCount} Wallet{watchCount !== 1 ? 's' : ''}
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );

  return createPortal(dialogContent, document.body);
};

src/components/Dialogs/ImportWalletsDial.content:
/**
 * ImportWalletsDialog Component
 * Dialog for importing existing wallets via private keys with validation
 */

import React, { useState, useCallback } from 'react';
import { createPortal } from 'react-dom';
import { useForm, useFieldArray } from 'react-hook-form';
import { useWalletStore } from '../../store/wallets';
import { useSessionStore } from '../../store/session';
import { encryptPrivateKey, secureStore } from '../../utils/crypto';
import { Role } from '../../types';

interface ImportWalletEntry {
  privateKey: string;
  role: Role;
  label?: string;
}

interface ImportWalletsFormData {
  importMethod: 'single' | 'bulk' | 'csv';
  wallets: ImportWalletEntry[];
  csvContent?: string;
}

interface ImportWalletsDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: (count: number) => void;
}

export const ImportWalletsDialog: React.FC<ImportWalletsDialogProps> = ({
  isOpen,
  onClose,
  onSuccess,
}) => {
  const [isImporting, setIsImporting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [validationErrors, setValidationErrors] = useState<string[]>([]);

  const addWallets = useWalletStore(state => state.addWallets);
  const { isUnlocked } = useSessionStore();

  const {
    register,
    control,
    handleSubmit,
    watch,
    setValue,
    formState: { errors },
    reset,
  } = useForm<ImportWalletsFormData>({
    defaultValues: {
      importMethod: 'single',
      wallets: [{ privateKey: '', role: Role.NUMBERED, label: '' }],
      csvContent: '',
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'wallets',
  });

  const watchImportMethod = watch('importMethod');

  const validatePrivateKey = useCallback((privateKey: string): boolean => {
    // Basic validation for Ethereum-style private keys
    const cleanKey = privateKey.trim();

    // Check if it's a valid hex string of correct length
    if (!/^(0x)?[a-fA-F0-9]{64}$/.test(cleanKey)) {
      return false;
    }

    return true;
  }, []);

  const deriveAddressFromPrivateKey = useCallback((privateKey: string): string => {
    // In a real implementation, this would use ethers.js to derive the address
    // For now, we'll create a mock address based on the private key
    const cleanKey = privateKey.replace('0x', '');
    const hash = Array.from(cleanKey).reduce((acc, char) => {
      return ((acc << 5) - acc + char.charCodeAt(0)) & 0xffffffff;
    }, 0);

    return `0x${Math.abs(hash).toString(16).padStart(40, '0')}`;
  }, []);

  const parseCsvContent = useCallback((csvContent: string): ImportWalletEntry[] => {
    const lines = csvContent.trim().split('\n');
    const entries: ImportWalletEntry[] = [];
    const errors: string[] = [];

    lines.forEach((line, index) => {
      const trimmedLine = line.trim();
      if (!trimmedLine || trimmedLine.startsWith('#')) return; // Skip empty lines and comments

      const parts = trimmedLine.split(',').map(part => part.trim());

      if (parts.length < 1) {
        errors.push(`Line ${index + 1}: Missing private key`);
        return;
      }

      const privateKey = parts[0];
      const role = (parts[1] as Role) || Role.NUMBERED;
      const label = parts[2] || '';

      if (!validatePrivateKey(privateKey)) {
        errors.push(`Line ${index + 1}: Invalid private key format`);
        return;
      }

      entries.push({ privateKey, role, label });
    });

    setValidationErrors(errors);
    return entries;
  }, [validatePrivateKey]);

  const handleClose = useCallback(() => {
    if (isImporting) return;
    reset();
    setError(null);
    setValidationErrors([]);
    onClose();
  }, [isImporting, reset, onClose]);

  const onSubmit = useCallback(async (data: ImportWalletsFormData) => {
    // No authentication required once logged in with access key

    setIsImporting(true);
    setError(null);
    setValidationErrors([]);

    try {
      let walletsToImport: ImportWalletEntry[] = [];

      if (data.importMethod === 'csv' && data.csvContent) {
        walletsToImport = parseCsvContent(data.csvContent);
      } else {
        walletsToImport = data.wallets.filter(w => w.privateKey.trim());
      }

      // Validate all private keys
      const invalidKeys: string[] = [];
      walletsToImport.forEach((wallet, index) => {
        if (!validatePrivateKey(wallet.privateKey)) {
          invalidKeys.push(`Wallet ${index + 1}: Invalid private key format`);
        }
      });

      if (invalidKeys.length > 0) {
        setValidationErrors(invalidKeys);
        return;
      }

      if (walletsToImport.length === 0) {
        setError('No valid wallets to import');
        return;
      }

      // Use default passphrase for encryption
      const defaultPassphrase = 'justjewit2025';

      // Process and encrypt wallets
      const processedWallets = [];

      for (let i = 0; i < walletsToImport.length; i++) {
        const walletData = walletsToImport[i];

        // Derive address from private key
        const address = deriveAddressFromPrivateKey(walletData.privateKey);

        // Generate unique wallet ID
        const walletId = `imported_${Date.now()}_${i}`;

        // Encrypt and store private key with default passphrase
        const encryptedPrivateKey = await encryptPrivateKey(walletData.privateKey, defaultPassphrase);
        await secureStore(`wallet_${walletId}_pk`, encryptedPrivateKey);

        // Create wallet object (without private key in memory)
        processedWallets.push({
          id: walletId,
          publicKey: address, // In real implementation, derive public key
          address,
          balance: 0, // Will be updated by balance polling
          role: walletData.role,
          createdAt: new Date().toISOString(),
          isActive: true,
        });
      }

      // Add wallets to store
      addWallets(processedWallets);

      onSuccess?.(processedWallets.length);
      handleClose();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to import wallets';
      setError(errorMessage);
    } finally {
      setIsImporting(false);
    }
  }, [parseCsvContent, validatePrivateKey, deriveAddressFromPrivateKey, addWallets, onSuccess, handleClose]);

  const handleOverlayClick = useCallback((event: React.MouseEvent) => {
    if (event.target === event.currentTarget) {
      handleClose();
    }
  }, [handleClose]);

  const addWalletEntry = useCallback(() => {
    append({ privateKey: '', role: Role.NUMBERED, label: '' });
  }, [append]);

  const removeWalletEntry = useCallback((index: number) => {
    remove(index);
  }, [remove]);

  const handleCsvUpload = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const content = e.target?.result as string;
      setValue('csvContent', content);
    };
    reader.readAsText(file);
  }, [setValue]);

  if (!isOpen) return null;

  const dialogContent = (
    <div className="import-wallets-overlay" onClick={handleOverlayClick}>
      <div className="import-wallets-dialog">
        <div className="dialog-header">
          <h3 className="dialog-title">
            <span className="title-icon">üì•</span>
            Import Existing Wallets
          </h3>
          {!isImporting && (
            <button 
              className="dialog-close"
              onClick={handleClose}
              aria-label="Close dialog"
            >
              ‚úï
            </button>
          )}
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="import-form">
          <div className="form-content">
            {/* Security Notice */}
            <div className="security-warning">
              <div className="warning-icon">‚úÖ</div>
              <div className="warning-content">
                <strong>Automatic Security:</strong> Imported wallets are automatically encrypted and secured with your access key authentication.
              </div>
            </div>

            {/* Import Method Selection */}
            <div className="form-group">
              <label className="form-label">Import Method</label>
              <div className="method-selector">
                <label className="method-option">
                  <input
                    {...register('importMethod')}
                    type="radio"
                    value="single"
                    disabled={isImporting}
                  />
                  <span className="method-label">
                    <strong>Single Entry</strong>
                    <small>Import one wallet at a time</small>
                  </span>
                </label>

                <label className="method-option">
                  <input
                    {...register('importMethod')}
                    type="radio"
                    value="bulk"
                    disabled={isImporting}
                  />
                  <span className="method-label">
                    <strong>Multiple Entries</strong>
                    <small>Import several wallets manually</small>
                  </span>
                </label>

                <label className="method-option">
                  <input
                    {...register('importMethod')}
                    type="radio"
                    value="csv"
                    disabled={isImporting}
                  />
                  <span className="method-label">
                    <strong>CSV Upload</strong>
                    <small>Import from CSV file</small>
                  </span>
                </label>
              </div>
            </div>

            {/* CSV Upload */}
            {watchImportMethod === 'csv' && (
              <div className="csv-import">
                <div className="form-group">
                  <label htmlFor="csv-upload" className="form-label">
                    Upload CSV File
                    <span className="form-hint">
                      Format: private_key, role, label (one wallet per line)
                    </span>
                  </label>
                  <input
                    type="file"
                    id="csv-upload"
                    accept=".csv,.txt"
                    onChange={handleCsvUpload}
                    className="form-file"
                    disabled={isImporting}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="csv-content" className="form-label">
                    CSV Content Preview
                  </label>
                  <textarea
                    {...register('csvContent')}
                    id="csv-content"
                    className="form-textarea"
                    rows={8}
                    placeholder="0x1234567890abcdef..., dev, Development Wallet
0x9876543210fedcba..., mev, MEV Bot Wallet
# Lines starting with # are comments"
                    disabled={isImporting}
                  />
                </div>
              </div>
            )}

            {/* Manual Entry */}
            {(watchImportMethod === 'single' || watchImportMethod === 'bulk') && (
              <div className="manual-import">
                <div className="wallet-entries">
                  {fields.map((field, index) => (
                    <div key={field.id} className="wallet-entry">
                      <div className="entry-header">
                        <h4>Wallet {index + 1}</h4>
                        {watchImportMethod === 'bulk' && fields.length > 1 && (
                          <button
                            type="button"
                            className="remove-entry"
                            onClick={() => removeWalletEntry(index)}
                            disabled={isImporting}
                          >
                            ‚úï
                          </button>
                        )}
                      </div>

                      <div className="entry-fields">
                        <div className="form-group">
                          <label className="form-label">Private Key</label>
                          <input
                            {...register(`wallets.${index}.privateKey`, {
                              required: 'Private key is required',
                              validate: (value) => validatePrivateKey(value) || 'Invalid private key format',
                            })}
                            type="password"
                            className="form-input"
                            placeholder="0x1234567890abcdef..."
                            disabled={isImporting}
                          />
                          {errors.wallets?.[index]?.privateKey && (
                            <span className="form-error">
                              {errors.wallets[index]?.privateKey?.message}
                            </span>
                          )}
                        </div>

                        <div className="form-row">
                          <div className="form-group">
                            <label className="form-label">Role</label>
                            <select
                              {...register(`wallets.${index}.role`)}
                              className="form-select"
                              disabled={isImporting}
                            >
                              <option value={Role.NUMBERED}>üî¢ Numbered</option>
                              <option value={Role.DEV}>üë®‚Äçüíª Developer</option>
                              <option value={Role.MEV}>ü§ñ MEV Bot</option>
                              <option value={Role.FUNDER}>üí∞ Funder</option>
                            </select>
                          </div>

                          <div className="form-group">
                            <label className="form-label">Label (Optional)</label>
                            <input
                              {...register(`wallets.${index}.label`)}
                              type="text"
                              className="form-input"
                              placeholder="My wallet"
                              disabled={isImporting}
                            />
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>

                {watchImportMethod === 'bulk' && (
                  <button
                    type="button"
                    className="add-wallet-btn"
                    onClick={addWalletEntry}
                    disabled={isImporting}
                  >
                    + Add Another Wallet
                  </button>
                )}
              </div>
            )}

            {/* Validation Errors */}
            {validationErrors.length > 0 && (
              <div className="validation-errors">
                <h4>Validation Errors:</h4>
                <ul>
                  {validationErrors.map((error, index) => (
                    <li key={index} className="validation-error">
                      {error}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {/* Error Message */}
            {error && (
              <div className="form-error-banner">
                <span className="error-icon">‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            {/* Security Warning */}
            <div className="security-warning">
              <span className="warning-icon">üîí</span>
              <div className="warning-content">
                <strong>Security Notice:</strong>
                <p>Private keys will be encrypted and stored locally. Never share your private keys.</p>
              </div>
            </div>

            {/* Session Status */}
            {!isUnlocked && (
              <div className="session-warning">
                <span className="warning-icon">üîí</span>
                Session must be unlocked to import wallets
              </div>
            )}
          </div>

          <div className="dialog-actions">
            <button
              type="button"
              className="btn-secondary"
              onClick={handleClose}
              disabled={isImporting}
            >
              Cancel
            </button>

            <button
              type="submit"
              className="btn-primary"
              disabled={isImporting || !isUnlocked}
            >
              {isImporting ? (
                <>
                  <span className="loading-spinner"></span>
                  Importing wallets...
                </>
              ) : (
                <>
                  üì• Import Wallets
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );

  return createPortal(dialogContent, document.body);
};

