/**
 * BalanceDashboard - Visual balance overview component
 * Displays wallet balance statistics and distribution visualization
 */

import React, { useMemo } from 'react';
import { useWalletStore } from '../../store/wallets';
import { Role } from '../../types';

interface BalanceStats {
  totalBalance: number;
  selectedBalance: number;
  averageBalance: number;
  medianBalance: number;
  roleDistribution: Record<Role, { count: number; totalBalance: number; avgBalance: number }>;
  balanceRanges: {
    zero: number;
    low: number; // 0 < balance <= 0.01
    medium: number; // 0.01 < balance <= 0.1
    high: number; // balance > 0.1
  };
}

export const BalanceDashboard: React.FC = () => {
  const { wallets, selectedWallets } = useWalletStore();

  // Calculate comprehensive balance statistics
  const stats: BalanceStats = useMemo(() => {
    const totalBalance = wallets.reduce((sum, wallet) => sum + wallet.balance, 0);
    const selectedBalance = wallets
      .filter(wallet => selectedWallets.includes(wallet.id))
      .reduce((sum, wallet) => sum + wallet.balance, 0);

    // Sort balances for median calculation
    const sortedBalances = wallets.map(w => w.balance).sort((a, b) => a - b);
    const medianBalance = sortedBalances.length > 0 
      ? sortedBalances.length % 2 === 0
        ? (sortedBalances[sortedBalances.length / 2 - 1] + sortedBalances[sortedBalances.length / 2]) / 2
        : sortedBalances[Math.floor(sortedBalances.length / 2)]
      : 0;

    const averageBalance = wallets.length > 0 ? totalBalance / wallets.length : 0;

    // Role-based distribution
    const roleDistribution = wallets.reduce((acc, wallet) => {
      if (!acc[wallet.role]) {
        acc[wallet.role] = { count: 0, totalBalance: 0, avgBalance: 0 };
      }
      acc[wallet.role].count++;
      acc[wallet.role].totalBalance += wallet.balance;
      return acc;
    }, {} as Record<Role, { count: number; totalBalance: number; avgBalance: number }>);

    // Calculate average balance per role
    Object.keys(roleDistribution).forEach(role => {
      const roleData = roleDistribution[role as Role];
      roleData.avgBalance = roleData.count > 0 ? roleData.totalBalance / roleData.count : 0;
    });

    // Balance range distribution
    const balanceRanges = wallets.reduce(
      (ranges, wallet) => {
        if (wallet.balance === 0) ranges.zero++;
        else if (wallet.balance <= 0.01) ranges.low++;
        else if (wallet.balance <= 0.1) ranges.medium++;
        else ranges.high++;
        return ranges;
      },
      { zero: 0, low: 0, medium: 0, high: 0 }
    );

    return {
      totalBalance,
      selectedBalance,
      averageBalance,
      medianBalance,
      roleDistribution,
      balanceRanges,
    };
  }, [wallets, selectedWallets]);

  // Role colors for visualization
  const getRoleColor = (role: Role): string => {
    switch (role) {
      case Role.DEV:
        return '#3b82f6'; // Blue
      case Role.MEV:
        return '#ef4444'; // Red
      case Role.FUNDER:
        return '#10b981'; // Green
      case Role.NUMBERED:
        return '#f59e0b'; // Orange
      default:
        return '#6b7280'; // Gray
    }
  };

  // Get role display name
  const getRoleDisplayName = (role: Role): string => {
    switch (role) {
      case Role.DEV:
        return 'Developer';
      case Role.MEV:
        return 'MEV';
      case Role.FUNDER:
        return 'Funder';
      case Role.NUMBERED:
        return 'Numbered';
      default:
        return 'Unknown';
    }
  };

  // Format balance for display
  const formatBalance = (balance: number): string => {
    if (balance === 0) return '0';
    if (balance < 0.001) return balance.toFixed(6);
    if (balance < 1) return balance.toFixed(4);
    return balance.toFixed(2);
  };

  return (
    <div className="balance-dashboard">
      {/* Summary Cards */}
      <div className="summary-cards">
        <div className="summary-card total-balance">
          <div className="card-header">
            <i className="fas fa-coins"></i>
            <h3>Total Balance</h3>
          </div>
          <div className="card-value">
            {formatBalance(stats.totalBalance)} <span className="currency">BNB</span>
          </div>
          <div className="card-subtitle">
            Across {wallets.length} wallets
          </div>
        </div>

        <div className="summary-card selected-balance">
          <div className="card-header">
            <i className="fas fa-check-circle"></i>
            <h3>Selected Balance</h3>
          </div>
          <div className="card-value">
            {formatBalance(stats.selectedBalance)} <span className="currency">BNB</span>
          </div>
          <div className="card-subtitle">
            {selectedWallets.length} wallets selected
          </div>
        </div>

        <div className="summary-card average-balance">
          <div className="card-header">
            <i className="fas fa-chart-line"></i>
            <h3>Average Balance</h3>
          </div>
          <div className="card-value">
            {formatBalance(stats.averageBalance)} <span className="currency">BNB</span>
          </div>
          <div className="card-subtitle">
            Median: {formatBalance(stats.medianBalance)} BNB
          </div>
        </div>

        <div className="summary-card zero-balance">
          <div className="card-header">
            <i className="fas fa-exclamation-triangle"></i>
            <h3>Zero Balance</h3>
          </div>
          <div className="card-value">
            {stats.balanceRanges.zero}
          </div>
          <div className="card-subtitle">
            {wallets.length > 0 ? ((stats.balanceRanges.zero / wallets.length) * 100).toFixed(1) : 0}% of wallets
          </div>
        </div>
      </div>

      {/* Role Distribution */}
      <div className="role-distribution">
        <h3>
          <i className="fas fa-users"></i>
          Balance by Role
        </h3>
        <div className="role-cards">
          {Object.entries(stats.roleDistribution).map(([role, data]) => (
            <div
              key={role}
              className="role-card"
              style={{ borderLeftColor: getRoleColor(role as Role) }}
            >
              <div className="role-header">
                <span className="role-name">{getRoleDisplayName(role as Role)}</span>
                <span className="role-count">{data.count} wallets</span>
              </div>
              <div className="role-balance">
                <div className="total-balance">
                  <span className="label">Total:</span>
                  <span className="value">{formatBalance(data.totalBalance)} BNB</span>
                </div>
                <div className="avg-balance">
                  <span className="label">Avg:</span>
                  <span className="value">{formatBalance(data.avgBalance)} BNB</span>
                </div>
              </div>
              <div className="role-percentage">
                {stats.totalBalance > 0 
                  ? ((data.totalBalance / stats.totalBalance) * 100).toFixed(1)
                  : 0}% of total
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Balance Distribution Chart */}
      <div className="balance-distribution">
        <h3>
          <i className="fas fa-chart-pie"></i>
          Balance Distribution
        </h3>
        <div className="distribution-chart">
          <div className="chart-bars">
            <div className="chart-bar">
              <div className="bar-label">Zero (0 BNB)</div>
              <div className="bar-container">
                <div
                  className="bar-fill zero"
                  style={{
                    width: wallets.length > 0 
                      ? `${(stats.balanceRanges.zero / wallets.length) * 100}%`
                      : '0%'
                  }}
                ></div>
              </div>
              <div className="bar-value">{stats.balanceRanges.zero}</div>
            </div>

            <div className="chart-bar">
              <div className="bar-label">Low (0-0.01 BNB)</div>
              <div className="bar-container">
                <div
                  className="bar-fill low"
                  style={{
                    width: wallets.length > 0 
                      ? `${(stats.balanceRanges.low / wallets.length) * 100}%`
                      : '0%'
                  }}
                ></div>
              </div>
              <div className="bar-value">{stats.balanceRanges.low}</div>
            </div>

            <div className="chart-bar">
              <div className="bar-label">Medium (0.01-0.1 BNB)</div>
              <div className="bar-container">
                <div
                  className="bar-fill medium"
                  style={{
                    width: wallets.length > 0 
                      ? `${(stats.balanceRanges.medium / wallets.length) * 100}%`
                      : '0%'
                  }}
                ></div>
              </div>
              <div className="bar-value">{stats.balanceRanges.medium}</div>
            </div>

            <div className="chart-bar">
              <div className="bar-label">High (&gt;0.1 BNB)</div>
              <div className="bar-container">
                <div
                  className="bar-fill high"
                  style={{
                    width: wallets.length > 0 
                      ? `${(stats.balanceRanges.high / wallets.length) * 100}%`
                      : '0%'
                  }}
                ></div>
              </div>
              <div className="bar-value">{stats.balanceRanges.high}</div>
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="dashboard-actions">
        <h3>
          <i className="fas fa-bolt"></i>
          Quick Actions
        </h3>
        <div className="action-buttons">
          <button className="action-btn fund-zero">
            <i className="fas fa-plus-circle"></i>
            <span>Fund Zero Balance</span>
            <small>{stats.balanceRanges.zero} wallets</small>
          </button>
          <button className="action-btn fund-low">
            <i className="fas fa-level-up-alt"></i>
            <span>Top Up Low Balance</span>
            <small>{stats.balanceRanges.low} wallets</small>
          </button>
          <button className="action-btn equal-distribution">
            <i className="fas fa-balance-scale"></i>
            <span>Equal Distribution</span>
            <small>All selected wallets</small>
          </button>
          <button className="action-btn treasury-withdraw">
            <i className="fas fa-university"></i>
            <span>Collect to Treasury</span>
            <small>{formatBalance(stats.totalBalance)} BNB total</small>
          </button>
        </div>
      </div>
    </div>
  );
};

src/components/FundingPanel/Distribution.content:
/**
 * DistributionForm - BNB distribution controls component
 * Handles all distribution methods: equal, weighted, custom, and smart
 */

import React, { useState, useCallback, useMemo } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { useWalletStore } from '../../store/wallets';
import { useFundingStore } from '../../store/funding';
import { useSessionStore } from '../../store/session';
import type { DistributionMethod, FundingOperation, DistributionPlan, Role } from '../../types';

interface DistributionFormData {
  method: DistributionMethod;
  totalAmount: number;
  customAmounts: Record<string, number>;
  smartThreshold: number;
  targetRole?: Role;
}

interface Props {
  onShowPreview: (operation: FundingOperation) => void;
}

export const DistributionForm: React.FC<Props> = ({ onShowPreview }) => {
  // Store state
  const { wallets, selectedWallets } = useWalletStore();
  const { 
    calculateDistribution,
    createFundingOperation,
    currentOperation,
    isCalculating,
    error,
    preferences,
    updatePreferences
  } = useFundingStore();
  const { isUnlocked } = useSessionStore();

  // Local state
  const [previewPlan, setPreviewPlan] = useState<DistributionPlan[] | null>(null);
  const [showAdvancedOptions, setShowAdvancedOptions] = useState(false);

  // Form setup
  const { control, watch, handleSubmit, setValue, reset, formState: { errors } } = useForm<DistributionFormData>({
    defaultValues: {
      method: preferences.defaultMethod,
      totalAmount: 1.0,
      customAmounts: {},
      smartThreshold: preferences.smartDistributionThreshold,
      targetRole: undefined,
    },
  });

  const method = watch('method');
  const totalAmount = watch('totalAmount');
  const customAmounts = watch('customAmounts');

  // Get selected wallets
  const selectedWalletData = useMemo(() => {
    return wallets.filter(wallet => selectedWallets.includes(wallet.id));
  }, [wallets, selectedWallets]);

  // Calculate preview on form changes
  const handleCalculatePreview = useCallback(async () => {
    if (!totalAmount || totalAmount <= 0 || selectedWallets.length === 0) {
      setPreviewPlan(null);
      return;
    }

    try {
      const plan = await calculateDistribution(method, totalAmount, selectedWallets, method === 'custom' ? customAmounts : undefined);
      setPreviewPlan(plan);
    } catch (error) {
      console.error('Failed to calculate preview:', error);
      setPreviewPlan(null);
    }
  }, [method, totalAmount, selectedWallets, customAmounts, calculateDistribution]);

  // Auto-calculate preview when form changes
  React.useEffect(() => {
    const timer = setTimeout(handleCalculatePreview, 300);
    return () => clearTimeout(timer);
  }, [handleCalculatePreview]);

  // Form submission
  const onSubmit = async (data: DistributionFormData) => {
    if (!isUnlocked) {
      alert('Session must be unlocked to create funding operations');
      return;
    }

    if (selectedWallets.length === 0) {
      alert('Please select wallets to fund');
      return;
    }

    try {
      await createFundingOperation(
        data.method,
        data.totalAmount,
        selectedWallets,
        data.method === 'custom' ? data.customAmounts : undefined
      );

      if (currentOperation) {
        onShowPreview(currentOperation);
      }
    } catch (error) {
      console.error('Failed to create funding operation:', error);
    }
  };

  // Custom amount handlers
  const handleCustomAmountChange = (walletId: string, amount: string) => {
    const numAmount = parseFloat(amount) || 0;
    setValue(`customAmounts.${walletId}`, numAmount);
  };

  const setEqualCustomAmounts = () => {
    if (selectedWalletData.length === 0) return;

    const equalAmount = totalAmount / selectedWalletData.length;
    const amounts: Record<string, number> = {};

    selectedWalletData.forEach(wallet => {
      amounts[wallet.id] = equalAmount;
    });

    setValue('customAmounts', amounts);
  };

  // Format balance display
  const formatBalance = (balance: number): string => {
    if (balance < 0.001) return balance.toFixed(6);
    if (balance < 1) return balance.toFixed(4);
    return balance.toFixed(2);
  };

  // Get method description
  const getMethodDescription = (method: DistributionMethod): string => {
    switch (method) {
      case 'equal':
        return 'Distribute the total amount equally across all selected wallets';
      case 'weighted':
        return 'Distribute based on wallet roles (DEV: 2x, MEV: 3x, FUNDER: 1x, NUMBERED: 1x)';
      case 'custom':
        return 'Manually specify the amount for each wallet';
      case 'smart':
        return 'Only fund wallets that have balance below the threshold';
      default:
        return '';
    }
  };

  return (
    <div className="distribution-form">
      <div className="form-header">
        <h3>
          <i className="fas fa-share-alt"></i>
          BNB Distribution
        </h3>
        <p className="form-description">
          Distribute BNB across your selected wallets using various methods
        </p>
      </div>

      {/* Selection Status */}
      <div className="selection-status">
        <div className="status-item">
          <span className="label">Selected Wallets:</span>
          <span className="value">{selectedWallets.length}</span>
        </div>
        <div className="status-item">
          <span className="label">Total Balance:</span>
          <span className="value">
            {formatBalance(selectedWalletData.reduce((sum, w) => sum + w.balance, 0))} BNB
          </span>
        </div>
        {method === 'smart' && previewPlan && (
          <div className="status-item">
            <span className="label">Needs Funding:</span>
            <span className="value">{previewPlan.filter(p => p.requiresFunding).length}</span>
          </div>
        )}
      </div>

      <form onSubmit={handleSubmit(onSubmit)} className="distribution-form-content">
        {/* Distribution Method Selection */}
        <div className="form-group">
          <label className="form-label">Distribution Method</label>
          <Controller
            name="method"
            control={control}
            render={({ field }) => (
              <div className="method-selector">
                {(['equal', 'weighted', 'custom', 'smart'] as DistributionMethod[]).map((methodOption) => (
                  <label
                    key={methodOption}
                    className={`method-option ${field.value === methodOption ? 'selected' : ''}`}
                  >
                    <input
                      type="radio"
                      value={methodOption}
                      checked={field.value === methodOption}
                      onChange={() => field.onChange(methodOption)}
                    />
                    <div className="method-content">
                      <div className="method-name">
                        {methodOption.charAt(0).toUpperCase() + methodOption.slice(1)}
                      </div>
                      <div className="method-description">
                        {getMethodDescription(methodOption)}
                      </div>
                    </div>
                  </label>
                ))}
              </div>
            )}
          />
        </div>

        {/* Total Amount */}
        <div className="form-group">
          <label className="form-label" htmlFor="totalAmount">
            Total Amount (BNB)
            {method === 'custom' && (
              <small> - Will be calculated from custom amounts</small>
            )}
          </label>
          <Controller
            name="totalAmount"
            control={control}
            rules={{
              required: method !== 'custom' ? 'Total amount is required' : false,
              min: { value: 0.000001, message: 'Amount must be greater than 0' },
              max: { value: 1000, message: 'Amount cannot exceed 1000 BNB' },
            }}
            render={({ field }) => (
              <input
                {...field}
                type="number"
                step="0.000001"
                min="0.000001"
                max="1000"
                className={`form-input ${errors.totalAmount ? 'error' : ''}`}
                placeholder="Enter BNB amount"
                disabled={method === 'custom'}
                onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
              />
            )}
          />
          {errors.totalAmount && (
            <span className="error-message">{errors.totalAmount.message}</span>
          )}
        </div>

        {/* Smart Distribution Threshold */}
        {method === 'smart' && (
          <div className="form-group">
            <label className="form-label" htmlFor="smartThreshold">
              Minimum Balance Threshold (BNB)
            </label>
            <Controller
              name="smartThreshold"
              control={control}
              render={({ field }) => (
                <input
                  {...field}
                  type="number"
                  step="0.000001"
                  min="0"
                  className="form-input"
                  placeholder="0.01"
                  onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                />
              )}
            />
            <small className="form-hint">
              Only wallets with balance below this threshold will be funded
            </small>
          </div>
        )}

        {/* Custom Amounts */}
        {method === 'custom' && (
          <div className="form-group">
            <div className="custom-amounts-header">
              <label className="form-label">Custom Amounts per Wallet</label>
              <button
                type="button"
                className="equal-amounts-btn"
                onClick={setEqualCustomAmounts}
              >
                <i className="fas fa-balance-scale"></i>
                Set Equal Amounts
              </button>
            </div>
            <div className="custom-amounts-list">
              {selectedWalletData.map((wallet) => (
                <div key={wallet.id} className="custom-amount-item">
                  <div className="wallet-info">
                    <div className="wallet-address">
                      {wallet.address.slice(0, 6)}...{wallet.address.slice(-4)}
                    </div>
                    <div className="wallet-details">
                      <span className="wallet-role">{wallet.role.toUpperCase()}</span>
                      <span className="wallet-balance">
                        {formatBalance(wallet.balance)} BNB
                      </span>
                    </div>
                  </div>
                  <input
                    type="number"
                    step="0.000001"
                    min="0"
                    className="amount-input"
                    placeholder="0.00"
                    value={customAmounts[wallet.id] || ''}
                    onChange={(e) => handleCustomAmountChange(wallet.id, e.target.value)}
                  />
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Advanced Options */}
        <div className="form-group">
          <button
            type="button"
            className="toggle-advanced"
            onClick={() => setShowAdvancedOptions(!showAdvancedOptions)}
          >
            <i className={`fas fa-chevron-${showAdvancedOptions ? 'up' : 'down'}`}></i>
            Advanced Options
          </button>

          {showAdvancedOptions && (
            <div className="advanced-options">
              <div className="option-item">
                <label>
                  <input
                    type="checkbox"
                    checked={preferences.confirmLargeOperations}
                    onChange={(e) => updatePreferences({ confirmLargeOperations: e.target.checked })}
                  />
                  Require confirmation for large operations
                </label>
              </div>
              <div className="option-item">
                <label>Auto-approval limit (BNB):</label>
                <input
                  type="number"
                  step="0.1"
                  min="0"
                  value={preferences.autoApprovalLimit}
                  onChange={(e) => updatePreferences({ autoApprovalLimit: parseFloat(e.target.value) || 0 })}
                  className="form-input small"
                />
              </div>
            </div>
          )}
        </div>

        {/* Error Display */}
        {error && (
          <div className="error-banner">
            <i className="fas fa-exclamation-triangle"></i>
            <span>{error}</span>
          </div>
        )}

        {/* Preview Section */}
        {previewPlan && (
          <div className="distribution-preview">
            <h4>
              <i className="fas fa-eye"></i>
              Distribution Preview
            </h4>
            <div className="preview-summary">
              <div className="summary-item">
                <span className="label">Total Amount:</span>
                <span className="value">{formatBalance(previewPlan.reduce((sum, p) => sum + p.plannedAmount, 0))} BNB</span>
              </div>
              <div className="summary-item">
                <span className="label">Wallets to Fund:</span>
                <span className="value">{previewPlan.filter(p => p.requiresFunding).length}</span>
              </div>
              <div className="summary-item">
                <span className="label">Average Amount:</span>
                <span className="value">
                  {previewPlan.length > 0 
                    ? formatBalance(previewPlan.reduce((sum, p) => sum + p.plannedAmount, 0) / previewPlan.length)
                    : '0'} BNB
                </span>
              </div>
            </div>

            <div className="preview-list">
              {previewPlan.filter(p => p.requiresFunding).slice(0, 5).map((plan) => (
                <div key={plan.walletId} className="preview-item">
                  <div className="wallet-info">
                    <span className="address">{plan.address.slice(0, 6)}...{plan.address.slice(-4)}</span>
                    <span className="role">{plan.role.toUpperCase()}</span>
                  </div>
                  <div className="amount-info">
                    <span className="current">{formatBalance(plan.currentBalance)} BNB</span>
                    <i className="fas fa-arrow-right"></i>
                    <span className="final">{formatBalance(plan.finalBalance)} BNB</span>
                    <span className="planned">+{formatBalance(plan.plannedAmount)}</span>
                  </div>
                </div>
              ))}
              {previewPlan.filter(p => p.requiresFunding).length > 5 && (
                <div className="preview-more">
                  +{previewPlan.filter(p => p.requiresFunding).length - 5} more wallets
                </div>
              )}
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className="form-actions">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => reset()}
          >
            <i className="fas fa-undo"></i>
            Reset
          </button>

          <button
            type="button"
            className="btn btn-outline"
            onClick={handleCalculatePreview}
            disabled={isCalculating || selectedWallets.length === 0}
          >
            <i className={`fas ${isCalculating ? 'fa-spinner fa-spin' : 'fa-calculator'}`}></i>
            {isCalculating ? 'Calculating...' : 'Recalculate'}
          </button>

          <button
            type="submit"
            className="btn btn-primary"
            disabled={
              isCalculating ||
              !isUnlocked ||
              selectedWallets.length === 0 ||
              !previewPlan ||
              previewPlan.filter(p => p.requiresFunding).length === 0
            }
          >
            <i className="fas fa-coins"></i>
            Create Funding Operation
          </button>
        </div>
      </form>

      {/* Help Section */}
      <div className="distribution-help">
        <h4>
          <i className="fas fa-info-circle"></i>
          Distribution Methods Guide
        </h4>
        <div className="help-content">
          <div className="help-item">
            <strong>Equal:</strong> Divides the total amount equally among all selected wallets
          </div>
          <div className="help-item">
            <strong>Weighted:</strong> Distributes based on wallet roles with predefined multipliers
          </div>
          <div className="help-item">
            <strong>Custom:</strong> Allows you to specify exact amounts for each wallet
          </div>
          <div className="help-item">
            <strong>Smart:</strong> Only funds wallets below a certain balance threshold
          </div>
        </div>
      </div>
    </div>
  );
};

src/components/FundingPanel/FundingPanel.content:
/**
 * FundingPanel CSS - SolNox BNB Dark Theme
 * Comprehensive styling for the funding management interface
 */

/* Main Funding Panel Container */
.funding-panel {
  background: var(--bg-primary);
  color: var(--text-primary);
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
  margin: 20px 0;
}

/* Session Locked State */
.funding-panel-locked {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  background: var(--bg-secondary);
  border-radius: 12px;
  margin: 20px 0;
}

.lock-container {
  text-align: center;
  padding: 40px;
}

.lock-container .lock-icon {
  font-size: 48px;
  color: var(--accent-orange);
  margin-bottom: 20px;
}

.lock-container h3 {
  margin: 0 0 12px 0;
  font-size: 24px;
  font-weight: 600;
}

.lock-container p {
  margin: 0 0 20px 0;
  color: var(--text-secondary);
  font-size: 16px;
}

.security-note {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  color: var(--text-secondary);
  font-size: 14px;
}

.security-note i {
  color: var(--accent-green);
}

/* Panel Header */
.funding-panel-header {
  background: var(--bg-secondary);
  padding: 20px 30px;
  border-bottom: 1px solid var(--border-color);
}

.header-content {
  display: flex;
  align-items: center;
  justify-content: between;
  margin-bottom: 16px;
}

.panel-title {
  display: flex;
  align-items: center;
  gap: 12px;
  margin: 0;
  font-size: 24px;
  font-weight: 600;
  flex: 1;
}

.panel-title i {
  color: var(--accent-green);
}

.header-actions {
  display: flex;
  gap: 12px;
}

.refresh-btn, .clear-errors-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 16px;
  background: var(--bg-tertiary);
  border: 1px solid var(--border-color);
  border-radius: 8px;
  color: var(--text-primary);
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s;
}

.refresh-btn:hover, .clear-errors-btn:hover {
  background: var(--accent-green);
  border-color: var(--accent-green);
  color: var(--bg-primary);
}

.refresh-btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.clear-errors-btn {
  background: var(--accent-red);
  border-color: var(--accent-red);
}

/* Status Indicators */
.status-indicators {
  display: flex;
  gap: 16px;
  flex-wrap: wrap;
}

.status-indicator {
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.status-indicator.unlocked {
  background: rgba(16, 185, 129, 0.1);
  color: var(--accent-green);
  border: 1px solid rgba(16, 185, 129, 0.3);
}

.status-indicator.locked {
  background: rgba(239, 68, 68, 0.1);
  color: var(--accent-red);
  border: 1px solid rgba(239, 68, 68, 0.3);
}

.status-indicator.executing {
  background: rgba(255, 107, 53, 0.1);
  color: var(--accent-orange);
  border: 1px solid rgba(255, 107, 53, 0.3);
}

.status-indicator.selected {
  background: rgba(59, 130, 246, 0.1);
  color: var(--accent-blue);
  border: 1px solid rgba(59, 130, 246, 0.3);
}

/* Error Banner */
.error-banner {
  background: rgba(239, 68, 68, 0.1);
  border-left: 4px solid var(--accent-red);
  margin: 0;
  padding: 0;
}

.error-content {
  display: flex;
  align-items: flex-start;
  padding: 16px 30px;
  gap: 12px;
}

.error-content i {
  color: var(--accent-red);
  margin-top: 2px;
}

.error-messages {
  flex: 1;
}

.error-messages p {
  margin: 0 0 8px 0;
  font-size: 14px;
}

.error-messages p:last-child {
  margin-bottom: 0;
}

.error-close {
  background: none;
  border: none;
  color: var(--accent-red);
  font-size: 16px;
  cursor: pointer;
  opacity: 0.7;
  transition: opacity 0.2s;
}

.error-close:hover {
  opacity: 1;
}

/* Tab Navigation */
.funding-tabs {
  display: flex;
  background: var(--bg-secondary);
  border-bottom: 1px solid var(--border-color);
  overflow-x: auto;
  scrollbar-width: none;
  -ms-overflow-style: none;
}

.funding-tabs::-webkit-scrollbar {
  display: none;
}

.tab-button {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 16px 24px;
  background: none;
  border: none;
  color: var(--text-secondary);
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  border-bottom: 3px solid transparent;
  white-space: nowrap;
  position: relative;
}

.tab-button:hover {
  color: var(--text-primary);
  background: rgba(255, 255, 255, 0.05);
}

.tab-button.active {
  color: var(--accent-green);
  border-bottom-color: var(--accent-green);
  background: rgba(0, 255, 136, 0.05);
}

.tab-badge {
  background: var(--accent-green);
  color: var(--bg-primary);
  font-size: 11px;
  font-weight: 600;
  padding: 2px 6px;
  border-radius: 10px;
  min-width: 18px;
  text-align: center;
}

.tab-button.active .tab-badge {
  background: var(--text-primary);
}

/* Tab Content */
.tab-content {
  padding: 30px;
  min-height: 500px;
}

/* Individual Tab Styling */
.overview-tab, .distribution-tab, .treasury-tab, .history-tab {
  animation: fadeIn 0.3s ease-in-out;
}

@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

.active-operations {
  margin-top: 30px;
  padding: 20px;
  background: var(--bg-secondary);
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.active-operations h3 {
  display: flex;
  align-items: center;
  gap: 10px;
  margin: 0 0 20px 0;
  font-size: 18px;
  color: var(--accent-orange);
}

.history-content {
  text-align: center;
  padding: 60px 20px;
}

.history-content h3 {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  margin: 0 0 16px 0;
  color: var(--text-secondary);
}

.coming-soon {
  color: var(--text-secondary);
  font-style: italic;
  margin: 0;
}

/* Operation Overlay */
.operation-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
}

.operation-progress {
  background: var(--bg-secondary);
  padding: 40px;
  border-radius: 16px;
  text-align: center;
  max-width: 400px;
  border: 1px solid var(--border-color);
}

.progress-spinner {
  font-size: 48px;
  color: var(--accent-green);
  margin-bottom: 20px;
}

.operation-progress h4 {
  margin: 0 0 8px 0;
  font-size: 20px;
  font-weight: 600;
}

.operation-progress p {
  margin: 0 0 16px 0;
  color: var(--text-secondary);
}

.progress-details small {
  color: var(--text-secondary);
  font-size: 14px;
}

/* Balance Dashboard Styles */
.balance-dashboard {
  display: flex;
  flex-direction: column;
  gap: 30px;
}

.summary-cards {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
}

.summary-card {
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 12px;
  padding: 20px;
  transition: transform 0.2s, box-shadow 0.2s;
}

.summary-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
}

.card-header {
  display: flex;
  align-items: center;
  gap: 10px;
  margin-bottom: 16px;
}

.card-header i {
  font-size: 20px;
  color: var(--accent-green);
}

.card-header h3 {
  margin: 0;
  font-size: 16px;
  font-weight: 500;
  color: var(--text-secondary);
}

.card-value {
  font-size: 28px;
  font-weight: 700;
  color: var(--text-primary);
  margin-bottom: 8px;
}

.card-value .currency {
  font-size: 16px;
  font-weight: 500;
  color: var(--text-secondary);
  margin-left: 6px;
}

.card-subtitle {
  font-size: 14px;
  color: var(--text-secondary);
  margin: 0;
}

/* Specific card colors */
.summary-card.total-balance .card-header i {
  color: var(--accent-green);
}

.summary-card.selected-balance .card-header i {
  color: var(--accent-blue);
}

.summary-card.average-balance .card-header i {
  color: var(--accent-orange);
}

.summary-card.zero-balance .card-header i {
  color: var(--accent-red);
}

/* Role Distribution */
.role-distribution h3 {
  display: flex;
  align-items: center;
  gap: 10px;
  margin: 0 0 20px 0;
  font-size: 18px;
}

.role-cards {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
}

.role-card {
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-left: 4px solid;
  border-radius: 8px;
  padding: 16px;
}

.role-header {
  display: flex;
  justify-content: between;
  align-items: center;
  margin-bottom: 12px;
}

.role-name {
  font-weight: 600;
  font-size: 16px;
}

.role-count {
  background: var(--bg-tertiary);
  padding: 2px 8px;
  border-radius: 12px;
  font-size: 12px;
  color: var(--text-secondary);
}

.role-balance {
  margin-bottom: 8px;
}

.role-balance > div {
  display: flex;
  justify-content: between;
  margin-bottom: 4px;
  font-size: 14px;
}

.role-balance .label {
  color: var(--text-secondary);
}

.role-balance .value {
  font-weight: 500;
}

.role-percentage {
  text-align: center;
  font-size: 12px;
  color: var(--text-secondary);
  font-weight: 500;
}

/* Balance Distribution Chart */
.balance-distribution h3 {
  display: flex;
  align-items: center;
  gap: 10px;
  margin: 0 0 20px 0;
  font-size: 18px;
}

.distribution-chart {
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 12px;
  padding: 20px;
}

.chart-bars {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.chart-bar {
  display: grid;
  grid-template-columns: 120px 1fr 60px;
  align-items: center;
  gap: 16px;
}

.bar-label {
  font-size: 14px;
  color: var(--text-secondary);
  text-align: right;
}

.bar-container {
  background: var(--bg-primary);
  height: 24px;
  border-radius: 12px;
  overflow: hidden;
  border: 1px solid var(--border-color);
}

.bar-fill {
  height: 100%;
  border-radius: 12px;
  transition: width 0.5s ease-in-out;
}

.bar-fill.zero {
  background: linear-gradient(90deg, var(--accent-red), #ef4444);
}

.bar-fill.low {
  background: linear-gradient(90deg, var(--accent-orange), #f59e0b);
}

.bar-fill.medium {
  background: linear-gradient(90deg, var(--accent-blue), #3b82f6);
}

.bar-fill.high {
  background: linear-gradient(90deg, var(--accent-green), #10b981);
}

.bar-value {
  font-weight: 600;
  text-align: center;
  color: var(--text-primary);
}

/* Dashboard Actions */
.dashboard-actions h3 {
  display: flex;
  align-items: center;
  gap: 10px;
  margin: 0 0 20px 0;
  font-size: 18px;
}

.action-buttons {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
}

.action-btn {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
  padding: 20px 16px;
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 12px;
  color: var(--text-primary);
  cursor: pointer;
  transition: all 0.2s;
  text-align: center;
}

.action-btn:hover {
  background: var(--accent-green);
  border-color: var(--accent-green);
  color: var(--bg-primary);
  transform: translateY(-2px);
}

.action-btn i {
  font-size: 24px;
  color: var(--accent-green);
}

.action-btn:hover i {
  color: var(--bg-primary);
}

.action-btn span {
  font-weight: 500;
  font-size: 14px;
}

.action-btn small {
  font-size: 12px;
  opacity: 0.8;
}

/* Responsive Design */
@media (max-width: 768px) {
  .funding-panel-header {
    padding: 16px 20px;
  }

  .header-content {
    flex-direction: column;
    align-items: flex-start;
    gap: 12px;
  }

  .status-indicators {
    width: 100%;
    justify-content: space-between;
  }

  .tab-content {
    padding: 20px;
  }

  .summary-cards {
    grid-template-columns: 1fr;
  }

  .role-cards {
    grid-template-columns: 1fr;
  }

  .action-buttons {
    grid-template-columns: repeat(2, 1fr);
  }

  .chart-bar {
    grid-template-columns: 100px 1fr 50px;
    gap: 12px;
  }

  .bar-label {
    font-size: 12px;
  }
}

/* Distribution Form Styles */
.distribution-form {
  max-width: 100%;
}

.form-header {
  margin-bottom: 30px;
  padding-bottom: 20px;
  border-bottom: 1px solid var(--border-color);
}

.form-header h3 {
  display: flex;
  align-items: center;
  gap: 12px;
  margin: 0 0 8px 0;
  font-size: 20px;
  font-weight: 600;
  color: var(--text-primary);
}

.form-header h3 i {
  color: var(--accent-green);
}

.form-description {
  margin: 0;
  color: var(--text-secondary);
  font-size: 14px;
}

.selection-status {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
  margin-bottom: 30px;
  padding: 20px;
  background: var(--bg-secondary);
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.status-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.status-item .label {
  color: var(--text-secondary);
  font-size: 14px;
}

.status-item .value {
  color: var(--text-primary);
  font-weight: 600;
}

.distribution-form-content {
  display: flex;
  flex-direction: column;
  gap: 24px;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.form-label {
  font-weight: 500;
  color: var(--text-primary);
  font-size: 14px;
}

.form-input {
  padding: 12px 16px;
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 8px;
  color: var(--text-primary);
  font-size: 14px;
  transition: border-color 0.2s;
}

.form-input:focus {
  outline: none;
  border-color: var(--accent-green);
  box-shadow: 0 0 0 3px rgba(0, 255, 136, 0.1);
}

.form-input.error {
  border-color: var(--accent-red);
}

.form-input.small {
  max-width: 150px;
}

.form-hint {
  color: var(--text-secondary);
  font-size: 12px;
  font-style: italic;
}

.error-message {
  color: var(--accent-red);
  font-size: 12px;
  margin-top: 4px;
}

/* Method Selector */
.method-selector {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 12px;
}

.method-option {
  position: relative;
  display: block;
  padding: 16px;
  background: var(--bg-secondary);
  border: 2px solid var(--border-color);
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.2s;
}

.method-option:hover {
  border-color: var(--accent-green);
  background: rgba(0, 255, 136, 0.05);
}

.method-option.selected {
  border-color: var(--accent-green);
  background: rgba(0, 255, 136, 0.1);
}

.method-option input[type="radio"] {
  display: none;
}

.method-content {
  pointer-events: none;
}

.method-name {
  font-weight: 600;
  font-size: 16px;
  color: var(--text-primary);
  margin-bottom: 6px;
}

.method-description {
  font-size: 13px;
  color: var(--text-secondary);
  line-height: 1.4;
}

/* Custom Amounts */
.custom-amounts-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
}

.equal-amounts-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 16px;
  background: var(--accent-blue);
  border: none;
  border-radius: 8px;
  color: white;
  font-size: 13px;
  cursor: pointer;
  transition: background 0.2s;
}

.equal-amounts-btn:hover {
  background: #2563eb;
}

.custom-amounts-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
  max-height: 300px;
  overflow-y: auto;
  padding: 16px;
  background: var(--bg-secondary);
  border-radius: 8px;
  border: 1px solid var(--border-color);
}

.custom-amount-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px;
  background: var(--bg-primary);
  border-radius: 8px;
  border: 1px solid var(--border-color);
}

.wallet-info {
  display: flex;
  flex-direction: column;
  gap: 4px;
  flex: 1;
}

.wallet-address {
  font-family: monospace;
  font-size: 14px;
  color: var(--text-primary);
  font-weight: 500;
}

.wallet-details {
  display: flex;
  gap: 12px;
  font-size: 12px;
}

.wallet-role {
  color: var(--accent-orange);
  font-weight: 600;
}

.wallet-balance {
  color: var(--text-secondary);
}

.amount-input {
  width: 120px;
  padding: 8px 12px;
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 6px;
  color: var(--text-primary);
  font-size: 14px;
  text-align: right;
}

/* Advanced Options */
.toggle-advanced {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 16px;
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 8px;
  color: var(--text-primary);
  cursor: pointer;
  transition: all 0.2s;
  width: fit-content;
}

.toggle-advanced:hover {
  background: var(--bg-tertiary);
}

.advanced-options {
  margin-top: 16px;
  padding: 20px;
  background: var(--bg-secondary);
  border-radius: 8px;
  border: 1px solid var(--border-color);
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.option-item {
  display: flex;
  align-items: center;
  gap: 12px;
}

.option-item label {
  color: var(--text-primary);
  font-size: 14px;
  cursor: pointer;
}

.option-item input[type="checkbox"] {
  accent-color: var(--accent-green);
}

/* Distribution Preview */
.distribution-preview {
  margin-top: 24px;
  padding: 20px;
  background: var(--bg-secondary);
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.distribution-preview h4 {
