}

.preview-summary {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
  margin-bottom: 20px;
  padding: 16px;
  background: var(--bg-primary);
  border-radius: 8px;
}

.summary-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.summary-item .label {
  color: var(--text-secondary);
  font-size: 13px;
}

.summary-item .value {
  color: var(--text-primary);
  font-weight: 600;
  font-size: 14px;
}

.preview-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.preview-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px;
  background: var(--bg-primary);
  border-radius: 8px;
  border: 1px solid var(--border-color);
}

.preview-item .wallet-info {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.preview-item .address {
  font-family: monospace;
  font-size: 13px;
  color: var(--text-primary);
}

.preview-item .role {
  font-size: 11px;
  color: var(--accent-orange);
  font-weight: 600;
}

.amount-info {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 13px;
}

.amount-info .current {
  color: var(--text-secondary);
}

.amount-info .final {
  color: var(--accent-green);
  font-weight: 600;
}

.amount-info .planned {
  color: var(--accent-blue);
  font-weight: 600;
}

.amount-info i {
  color: var(--text-secondary);
  font-size: 10px;
}

.preview-more {
  text-align: center;
  padding: 8px;
  color: var(--text-secondary);
  font-size: 12px;
  font-style: italic;
}

/* Treasury Manager Styles */
.treasury-manager {
  max-width: 100%;
}

.treasury-stats {
  margin-bottom: 30px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 16px;
}

.stat-card {
  padding: 16px;
  background: var(--bg-secondary);
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.stat-header {
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 12px;
  color: var(--text-secondary);
  font-size: 13px;
  font-weight: 500;
}

.stat-header i {
  color: var(--accent-blue);
}

.stat-value {
  font-size: 24px;
  font-weight: 700;
  color: var(--text-primary);
  margin-bottom: 4px;
}

.stat-label {
  font-size: 12px;
  color: var(--text-secondary);
}

/* Form Actions */
.form-actions {
  display: flex;
  justify-content: flex-end;
  align-items: center;
  gap: 12px;
  margin-top: 30px;
  padding-top: 20px;
  border-top: 1px solid var(--border-color);
}

.btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  border: 1px solid transparent;
