/**
 * Main Bundle Configuration Component
 * Comprehensive interface for configuring BNB Smart Chain bundler operations
 */

import React, { useState, useEffect } from 'react';
import { useConfigStore } from '../../store/config';
import { usePresetStore } from '../../store/presets';
import { useWalletStore } from '../../store/wallets';
import { useSessionStore } from '../../store/session';
import { TokenConfig } from './TokenConfig';
import { TransactionSettings } from './TransactionSettings';
import { ExecutionParams } from './ExecutionParams';
import { BundlePreview } from './BundlePreview';
import { PresetManager } from './PresetManager';
import type { EnhancedBundleConfig } from '../../types/bundle-config';
import './BundleConfig.css';

interface TabConfig {
  id: string;
  label: string;
  icon: string;
  component: React.ComponentType<any>;
  badge?: string | number;
}

export const BundleConfig: React.FC = () => {
  // Store state
  const { 
    currentConfig, 
    updateConfig, 
    validateConfig, 
    isValidConfig,
    validationErrors,
    isDirty 
  } = useConfigStore();
  const { isUnlocked } = useSessionStore();
  const { wallets, selectedWallets } = useWalletStore();
  const { presets } = usePresetStore();

  // Local state
  const [activeTab, setActiveTab] = useState<string>('token');
  const [showPresetManager, setShowPresetManager] = useState(false);
  const [configProgress, setConfigProgress] = useState(0);
  const [lastValidation, setLastValidation] = useState<Date | null>(null);

  // Calculate configuration progress
  useEffect(() => {
    const requiredFields = [
      'token.address',
      'token.name', 
      'token.symbol',
      'purchaseAmount.totalBnb',
      'purchaseAmount.allocation',
      'transactionSettings.gasConfiguration',
      'executionParams.safetyFeatures'
    ];

    const completedFields = requiredFields.filter(field => {
      const keys = field.split('.');
      let value: any = currentConfig;

      for (const key of keys) {
        value = value?.[key];
        if (value === undefined || value === null || value === '') {
          return false;
        }
      }
      return true;
    });

    setConfigProgress(Math.round((completedFields.length / requiredFields.length) * 100));
  }, [currentConfig]);

  // Auto-validation
  useEffect(() => {
    if (isDirty) {
      const timer = setTimeout(() => {
        validateConfig();
        setLastValidation(new Date());
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [currentConfig, isDirty, validateConfig]);

  // Tab configuration
  const tabs: TabConfig[] = [
    {
      id: 'token',
      label: 'Token & Allocation',
      icon: 'fas fa-coins',
      component: TokenConfig,
      badge: currentConfig.token?.address ? '‚úì' : '',
    },
    {
      id: 'transaction',
      label: 'Transaction Settings',
      icon: 'fas fa-cogs',
      component: TransactionSettings,
      badge: currentConfig.transactionSettings?.gasConfiguration ? '‚úì' : '',
    },
    {
      id: 'execution',
      label: 'Execution Parameters',
      icon: 'fas fa-play-circle',
      component: ExecutionParams,
      badge: currentConfig.executionParams?.staggerSettings ? '‚úì' : '',
    },
    {
      id: 'preview',
      label: 'Preview & Validate',
      icon: 'fas fa-eye',
      component: BundlePreview,
      badge: isValidConfig ? '‚úì' : validationErrors.length > 0 ? validationErrors.length : '',
    },
  ];

  const ActiveComponent = tabs.find(tab => tab.id === activeTab)?.component || TokenConfig;

  // Handle configuration update
  const handleConfigUpdate = (updates: Partial<EnhancedBundleConfig>) => {
    updateConfig(updates as any);
  };

  // Session check
  if (!isUnlocked) {
    return (
      <div className="bundle-config">
        <div className="config-locked-state">
          <div className="lock-container">
            <div className="lock-icon">
              <i className="fas fa-lock"></i>
            </div>
            <h3>Session Locked</h3>
            <p>Please unlock your session to configure bundle parameters.</p>
            <div className="security-note">
              <i className="fas fa-shield-alt"></i>
              <span>Bundle configuration requires secure session authentication</span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bundle-config">
      {/* Header Section */}
      <div className="bundle-config-header">
        <div className="header-content">
          <h2 className="bundle-config-title">
            <i className="fas fa-sliders-h"></i>
            Bundle Configuration
          </h2>
          <div className="bundle-config-actions">
            <button
              className="config-button secondary"
              onClick={() => setShowPresetManager(true)}
            >
              <i className="fas fa-save"></i>
              Presets
            </button>
            <div className="status-indicator info">
              <i className="fas fa-chart-line"></i>
              <span>{configProgress}% Complete</span>
            </div>
          </div>
        </div>

        {/* Configuration Progress */}
        <div className="config-progress">
          {tabs.map((tab, index) => (
            <div 
              key={tab.id}
              className={`progress-step ${
                activeTab === tab.id ? 'active' : 
                tab.badge === '‚úì' ? 'completed' : ''
              }`}
            >
              <div className="progress-step-number">
                {tab.badge === '‚úì' ? '‚úì' : index + 1}
              </div>
              <span>{tab.label}</span>
            </div>
          ))}
        </div>

        {/* Validation Status */}
        {lastValidation && (
          <div className="validation-summary">
            {isValidConfig ? (
              <div className="status-indicator success">
                <i className="fas fa-check-circle"></i>
                <span>Configuration valid - ready for execution</span>
              </div>
            ) : validationErrors.length > 0 ? (
              <div className="status-indicator error">
                <i className="fas fa-exclamation-triangle"></i>
                <span>{validationErrors.length} validation error{validationErrors.length !== 1 ? 's' : ''}</span>
              </div>
            ) : (
              <div className="status-indicator warning">
                <i className="fas fa-clock"></i>
                <span>Configuration incomplete</span>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Tab Navigation */}
      <div className="bundle-config-tabs">
        {tabs.map(tab => (
          <button
            key={tab.id}
            className={`bundle-config-tab ${activeTab === tab.id ? 'active' : ''}`}
            onClick={() => setActiveTab(tab.id)}
          >
            <i className={tab.icon}></i>
            <span>{tab.label}</span>
            {tab.badge && (
              <span className={`tab-badge ${tab.badge === '‚úì' ? 'success' : 'info'}`}>
                {tab.badge}
              </span>
            )}
          </button>
        ))}
      </div>

      {/* Tab Content */}
      <div className="bundle-config-content">
        <ActiveComponent 
          config={currentConfig as Partial<EnhancedBundleConfig>}
          onUpdate={handleConfigUpdate}
          wallets={wallets}
          selectedWallets={selectedWallets}
          validationErrors={validationErrors}
          isValid={isValidConfig}
        />
      </div>

      {/* Preset Manager Dialog */}
      {showPresetManager && (
        <PresetManager 
          isOpen={showPresetManager}
          onClose={() => setShowPresetManager(false)}
          currentConfig={currentConfig as EnhancedBundleConfig}
          onLoadConfig={handleConfigUpdate}
        />
      )}

      {/* Quick Stats Footer */}
      <div className="bundle-config-footer">
        <div className="config-stats">
          <div className="stat-item">
            <span className="stat-label">Total Wallets</span>
            <span className="stat-value">{wallets.length}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Selected</span>
            <span className="stat-value">{selectedWallets.length}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Total BNB Required</span>
            <span className="stat-value">
              {currentConfig.purchaseAmount?.totalBnb || 0} BNB
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Saved Presets</span>
            <span className="stat-value">{presets.length}</span>
          </div>
        </div>

        {isDirty && (
          <div className="unsaved-changes-indicator">
            <i className="fas fa-circle"></i>
            <span>Unsaved changes</span>
          </div>
        )}
      </div>
    </div>
  );
};

src/components/BundleConfig/BundlePrevie.content:
/**
 * Bundle Preview Component
 * Displays configuration summary, validation results, cost estimation, and risk assessment
 */

import React, { useState, useEffect } from 'react';
import { useWalletStore } from '../../store/wallets';
import type { 
  EnhancedBundleConfig,
  ConfigurationSummary
} from '../../types/bundle-config';

interface BundlePreviewProps {
  config: Partial<EnhancedBundleConfig>;
  onUpdate: (updates: Partial<EnhancedBundleConfig>) => void;
  validationErrors: string[];
  isValid: boolean;
}

export const BundlePreview: React.FC<BundlePreviewProps> = ({
  config,
  onUpdate,
  validationErrors,
  isValid
}) => {
  const { wallets } = useWalletStore();

  const [configSummary, setConfigSummary] = useState<ConfigurationSummary | null>(null);
  const [estimatedCosts, setEstimatedCosts] = useState({
    totalGasCost: 0,
    totalInvestment: 0,
    estimatedTokens: 0,
    networkFees: 0,
  });
  const [riskLevel, setRiskLevel] = useState<'low' | 'medium' | 'high' | 'critical'>('medium');

  // Calculate configuration summary
  useEffect(() => {
    const calculateSummary = () => {
      const activeWallets = wallets.filter(w => w.isActive);
      const totalBnbRequired = config.purchaseAmount?.totalBnb || 0;

      // Gas cost estimation
      const baseGasPrice = parseInt(config.transactionSettings?.gasConfiguration?.baseGasPrice || '5000000000');
      const priorityFee = parseInt(config.transactionSettings?.gasConfiguration?.priorityFee || '2000000000');
      const gasLimit = parseInt(config.transactionSettings?.gasConfiguration?.gasLimit || '100000');
      const gasMultiplier = config.transactionSettings?.gasConfiguration?.gasMultiplier || 1.1;

      const totalGasPrice = (baseGasPrice + priorityFee) * gasMultiplier;
      const gasCostPerTx = (gasLimit * totalGasPrice) / 1e18;
      const totalGasCost = gasCostPerTx * activeWallets.length;

      // Execution time estimation
      const staggerEnabled = config.executionParams?.staggerSettings?.enabled;
      const avgDelay = staggerEnabled 
        ? ((config.executionParams?.staggerSettings?.delayMin || 2000) + 
           (config.executionParams?.staggerSettings?.delayMax || 8000)) / 2 
        : 0;
      const batchSize = config.executionParams?.batchConfiguration?.batchSize || 5;
      const pauseBetweenBatches = config.executionParams?.batchConfiguration?.pauseBetweenBatches || 2;

      const batchCount = Math.ceil(activeWallets.length / batchSize);
      const executionTime = (avgDelay * activeWallets.length / 1000) + (pauseBetweenBatches * batchCount);

      // Risk assessment
      const riskFactors = [];
      if (!staggerEnabled) riskFactors.push('Immediate execution');
      if (totalBnbRequired > 5) riskFactors.push('High investment amount');
      if (!config.transactionSettings?.mevProtection?.enabled) riskFactors.push('No MEV protection');
      if ((config.transactionSettings?.slippageSettings?.tolerance || 0) > 5) riskFactors.push('High slippage tolerance');
      if (!config.executionParams?.safetyFeatures?.emergencyStopEnabled) riskFactors.push('No emergency stop');

      let riskLevel: 'low' | 'medium' | 'high' | 'critical' = 'low';
      if (riskFactors.length >= 4) riskLevel = 'critical';
      else if (riskFactors.length >= 3) riskLevel = 'high';
      else if (riskFactors.length >= 2) riskLevel = 'medium';

      const summary: ConfigurationSummary = {
        totalWallets: activeWallets.length,
        totalBnbRequired,
        estimatedGasCost: totalGasCost,
        estimatedDuration: Math.round(executionTime),
        riskAssessment: {
          level: riskLevel,
          factors: riskFactors,
        },
        validationStatus: {
          isValid,
          errors: validationErrors.map(error => ({ 
            field: 'general', 
            message: error, 
            severity: 'error' as const 
          })),
          warnings: [],
          riskLevel,
        },
      };

      setConfigSummary(summary);
      setEstimatedCosts({
        totalGasCost,
        totalInvestment: totalBnbRequired,
        estimatedTokens: 0, // Would need token price data
        networkFees: totalGasCost,
      });
      setRiskLevel(riskLevel);
    };

    calculateSummary();
  }, [config, wallets, validationErrors, isValid]);

  // Validation categories
  const getValidationsByCategory = () => {
    const categories = {
      token: validationErrors.filter(error => 
        error.includes('token') || error.includes('address') || error.includes('contract')
      ),
      amounts: validationErrors.filter(error => 
        error.includes('amount') || error.includes('BNB') || error.includes('allocation')
      ),
      transaction: validationErrors.filter(error => 
        error.includes('gas') || error.includes('slippage') || error.includes('network')
      ),
      execution: validationErrors.filter(error => 
        error.includes('execution') || error.includes('stagger') || error.includes('batch')
      ),
      safety: validationErrors.filter(error => 
        error.includes('safety') || error.includes('limit') || error.includes('timeout')
      ),
    };

    return categories;
  };

  const validationCategories = getValidationsByCategory();

  // Risk level styling
  const getRiskLevelStyle = (level: string) => {
    switch (level) {
      case 'low': return 'success';
      case 'medium': return 'warning';
      case 'high': return 'error';
      case 'critical': return 'error';
      default: return 'info';
    }
  };

  if (!configSummary) {
    return (
      <div className="bundle-preview loading">
        <div className="loading-spinner"></div>
        <span>Calculating configuration summary...</span>
      </div>
    );
  }

  return (
    <div className="bundle-preview">
      {/* Configuration Overview */}
      <div className="config-section">
        <div className="config-section-header">
          <h3 className="config-section-title">
            <i className="fas fa-eye"></i>
            Configuration Overview
          </h3>
          <div className={`overall-status ${isValid ? 'success' : 'error'}`}>
            <i className={`fas ${isValid ? 'fa-check-circle' : 'fa-exclamation-circle'}`}></i>
            <span>{isValid ? 'Ready for Execution' : 'Configuration Issues'}</span>
          </div>
        </div>

        <div className="overview-grid">
          <div className="overview-card">
            <div className="card-header">
              <h4>Token Information</h4>
              <i className="fas fa-coins"></i>
            </div>
            <div className="card-content">
              {config.token ? (
                <>
                  <div className="info-row">
                    <span>Token:</span>
                    <span className="token-name">{config.token.name} ({config.token.symbol})</span>
                  </div>
                  <div className="info-row">
                    <span>Address:</span>
                    <span className="address">{config.token.address}</span>
                  </div>
                  <div className="info-row">
                    <span>Verified:</span>
                    <span className={`status ${config.token.contractValidated ? 'success' : 'warning'}`}>
                      {config.token.contractValidated ? 'Yes' : 'No'}
                    </span>
                  </div>
                </>
              ) : (
                <div className="no-config">
                  <i className="fas fa-info-circle"></i>
                  <span>No token selected</span>
                </div>
              )}
            </div>
          </div>

          <div className="overview-card">
            <div className="card-header">
              <h4>Investment Details</h4>
              <i className="fas fa-chart-line"></i>
            </div>
            <div className="card-content">
              <div className="info-row">
                <span>Total Investment:</span>
                <span className="investment-amount">{estimatedCosts.totalInvestment} BNB</span>
              </div>
              <div className="info-row">
                <span>Active Wallets:</span>
                <span className="wallet-count">{configSummary.totalWallets}</span>
              </div>
              <div className="info-row">
                <span>Avg per Wallet:</span>
                <span className="per-wallet">
                  {configSummary.totalWallets > 0 
                    ? (estimatedCosts.totalInvestment / configSummary.totalWallets).toFixed(3)
                    : '0'} BNB
                </span>
              </div>
            </div>
          </div>

          <div className="overview-card">
            <div className="card-header">
              <h4>Execution Plan</h4>
              <i className="fas fa-play-circle"></i>
            </div>
            <div className="card-content">
              <div className="info-row">
                <span>Strategy:</span>
                <span className="strategy">
                  {config.executionParams?.staggerSettings?.enabled ? 'Staggered' : 'Immediate'}
                  {config.executionParams?.stealthMode?.enabled && ' + Stealth'}
                </span>
              </div>
              <div className="info-row">
                <span>Duration:</span>
                <span className="duration">
                  ~{Math.floor(configSummary.estimatedDuration / 60)}m {configSummary.estimatedDuration % 60}s
                </span>
              </div>
              <div className="info-row">
                <span>Batch Size:</span>
                <span className="batch-size">
                  {config.executionParams?.batchConfiguration?.batchSize || 'N/A'}
                </span>
              </div>
            </div>
          </div>

          <div className="overview-card">
            <div className="card-header">
              <h4>Cost Estimation</h4>
              <i className="fas fa-calculator"></i>
            </div>
            <div className="card-content">
              <div className="info-row">
                <span>Gas Fees:</span>
                <span className="gas-cost">{estimatedCosts.totalGasCost.toFixed(6)} BNB</span>
              </div>
              <div className="info-row">
                <span>Network Fees:</span>
                <span className="network-fees">{estimatedCosts.networkFees.toFixed(6)} BNB</span>
              </div>
              <div className="info-row total">
                <span>Total Cost:</span>
                <span className="total-cost">
                  {(estimatedCosts.totalInvestment + estimatedCosts.totalGasCost).toFixed(6)} BNB
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Risk Assessment */}
      <div className="config-section">
        <div className="config-section-header">
          <h3 className="config-section-title">
            <i className="fas fa-shield-alt"></i>
            Risk Assessment
          </h3>
          <div className={`risk-level ${getRiskLevelStyle(riskLevel)}`}>
            <i className="fas fa-exclamation-triangle"></i>
            <span>{riskLevel.toUpperCase()} RISK</span>
          </div>
        </div>

        <div className="risk-analysis">
          <div className="risk-summary">
            <div className="risk-indicator">
              <div className={`risk-circle ${riskLevel}`}>
                <span className="risk-score">
                  {configSummary.riskAssessment.factors.length}
                </span>
              </div>
              <div className="risk-description">
                <h4>Risk Level: {riskLevel.charAt(0).toUpperCase() + riskLevel.slice(1)}</h4>
                <p>{configSummary.riskAssessment.factors.length} risk factor(s) identified</p>
              </div>
            </div>
          </div>

          {configSummary.riskAssessment.factors.length > 0 && (
            <div className="risk-factors">
              <h4>Risk Factors</h4>
              <div className="risk-list">
                {configSummary.riskAssessment.factors.map((factor, index) => (
                  <div key={index} className="risk-item">
                    <i className="fas fa-exclamation-triangle"></i>
                    <span>{factor}</span>
                    <div className="risk-impact">
                      {factor.includes('High') ? 'High Impact' : 
                       factor.includes('No') ? 'Medium Impact' : 'Low Impact'}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div className="risk-recommendations">
            <h4>Recommendations</h4>
            <div className="recommendation-list">
              {riskLevel === 'critical' && (
                <div className="recommendation critical">
                  <i className="fas fa-exclamation-circle"></i>
                  <span>Critical risk detected. Review configuration before proceeding.</span>
                </div>
              )}
              {riskLevel === 'high' && (
                <div className="recommendation high">
                  <i className="fas fa-exclamation-triangle"></i>
                  <span>High risk configuration. Consider reducing investment amount or enabling safety features.</span>
                </div>
              )}
              {!config.transactionSettings?.mevProtection?.enabled && (
                <div className="recommendation">
                  <i className="fas fa-shield-alt"></i>
                  <span>Enable MEV protection for safer execution</span>
                </div>
              )}
              {!config.executionParams?.staggerSettings?.enabled && (
                <div className="recommendation">
                  <i className="fas fa-clock"></i>
                  <span>Consider enabling staggered execution to reduce MEV risk</span>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Validation Results */}
      {!isValid && (
        <div className="config-section">
          <div className="config-section-header">
            <h3 className="config-section-title">
              <i className="fas fa-exclamation-triangle"></i>
              Validation Issues
            </h3>
            <div className="error-count">
              <span>{validationErrors.length} issue(s) found</span>
            </div>
          </div>

          <div className="validation-results">
            {Object.entries(validationCategories).map(([category, errors]) => 
              errors.length > 0 && (
                <div key={category} className="validation-category">
                  <h4 className="category-title">
                    {category.charAt(0).toUpperCase() + category.slice(1)} Issues
                  </h4>
                  <div className="error-list">
                    {errors.map((error, index) => (
                      <div key={index} className="error-item">
                        <i className="fas fa-times-circle"></i>
                        <span>{error}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )
            )}
          </div>
        </div>
      )}

      {/* Configuration Details */}
      <div className="config-section">
        <div className="config-section-header">
          <h3 className="config-section-title">
            <i className="fas fa-list-alt"></i>
            Detailed Configuration
          </h3>
        </div>

        <div className="collapsible-sections">
          {/* Token Configuration Details */}
          <div className="collapsible-section">
            <div className="collapsible-header">
              <span>Token & Allocation Settings</span>
              <i className="fas fa-chevron-down"></i>
            </div>
            <div className="collapsible-content">
              <div className="config-details-grid">
                <div className="detail-item">
                  <span>Token Address:</span>
                  <span className="code">{config.token?.address || 'Not set'}</span>
                </div>
                <div className="detail-item">
                  <span>Total BNB:</span>
                  <span>{config.purchaseAmount?.totalBnb || 0} BNB</span>
                </div>
                <div className="detail-item">
                  <span>Buy Strategy:</span>
                  <span>{config.strategy?.buyStrategy || 'Not set'}</span>
                </div>
                <div className="detail-item">
                  <span>Sell Strategy:</span>
                  <span>{config.strategy?.sellStrategy || 'Not set'}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Transaction Settings Details */}
          <div className="collapsible-section">
            <div className="collapsible-header">
              <span>Transaction Settings</span>
              <i className="fas fa-chevron-down"></i>
            </div>
            <div className="collapsible-content">
              <div className="config-details-grid">
                <div className="detail-item">
                  <span>Gas Price:</span>
                  <span>
                    {config.transactionSettings?.gasConfiguration?.baseGasPrice 
                      ? `${parseInt(config.transactionSettings.gasConfiguration.baseGasPrice) / 1e9} Gwei`
                      : 'Not set'}
                  </span>
                </div>
                <div className="detail-item">
                  <span>Slippage:</span>
                  <span>{config.transactionSettings?.slippageSettings?.tolerance || 0}%</span>
                </div>
                <div className="detail-item">
                  <span>MEV Protection:</span>
                  <span className={config.transactionSettings?.mevProtection?.enabled ? 'success' : 'warning'}>
                    {config.transactionSettings?.mevProtection?.enabled ? 'Enabled' : 'Disabled'}
                  </span>
                </div>
                <div className="detail-item">
                  <span>Network:</span>
                  <span>
                    BSC {config.transactionSettings?.networkSettings?.chainId === 56 ? 'Mainnet' : 'Testnet'}
                  </span>
                </div>
              </div>
            </div>
          </div>

          {/* Execution Parameters Details */}
          <div className="collapsible-section">
            <div className="collapsible-header">
              <span>Execution Parameters</span>
              <i className="fas fa-chevron-down"></i>
            </div>
            <div className="collapsible-content">
              <div className="config-details-grid">
                <div className="detail-item">
                  <span>Stagger Enabled:</span>
                  <span className={config.executionParams?.staggerSettings?.enabled ? 'success' : 'warning'}>
                    {config.executionParams?.staggerSettings?.enabled ? 'Yes' : 'No'}
                  </span>
                </div>
                <div className="detail-item">
                  <span>Stealth Mode:</span>
                  <span className={config.executionParams?.stealthMode?.enabled ? 'success' : 'info'}>
                    {config.executionParams?.stealthMode?.enabled ? 'Enabled' : 'Disabled'}
                  </span>
                </div>
                <div className="detail-item">
                  <span>Batch Size:</span>
                  <span>{config.executionParams?.batchConfiguration?.batchSize || 'Not set'}</span>
                </div>
                <div className="detail-item">
                  <span>Emergency Stop:</span>
                  <span className={config.executionParams?.safetyFeatures?.emergencyStopEnabled ? 'success' : 'warning'}>
                    {config.executionParams?.safetyFeatures?.emergencyStopEnabled ? 'Enabled' : 'Disabled'}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="config-section">
        <div className="preview-actions">
          <button 
            className={`config-button ${isValid ? 'success' : 'secondary'}`}
            disabled={!isValid}
          >
            <i className="fas fa-play"></i>
            {isValid ? 'Execute Bundle' : 'Fix Issues First'}
          </button>

          <button className="config-button secondary">
            <i className="fas fa-save"></i>
            Save Configuration
          </button>

          <button className="config-button secondary">
            <i className="fas fa-download"></i>
            Export Configuration
          </button>

          <button className="config-button secondary">
            <i className="fas fa-copy"></i>
            Copy Configuration
          </button>
        </div>
      </div>
    </div>
  );
};

src/components/BundleConfig/PresetManage.content:
/**
 * Preset Manager Component
 * Handles saving, loading, and managing bundle configuration presets
 */

import React, { useState, useEffect } from 'react';
import { usePresetStore } from '../../store/presets';
import type { EnhancedBundleConfig, ConfigurationPreset, ConfigurationTemplate } from '../../types/bundle-config';

interface PresetManagerProps {
  isOpen: boolean;
  onClose: () => void;
  currentConfig: EnhancedBundleConfig;
  onLoadConfig: (config: Partial<EnhancedBundleConfig>) => void;
}

export const PresetManager: React.FC<PresetManagerProps> = ({
  isOpen,
  onClose,
  currentConfig,
  onLoadConfig
}) => {
  const { 
    presets, 
    templates, 
    favoritePresets, 
    recentlyUsed,
    savePreset,
    loadPreset,
    deletePreset,
    duplicatePreset,
    addToFavorites,
    removeFromFavorites,
    getTemplate,
    createFromTemplate,
    exportPreset,
    importPreset,
    searchPresets
  } = usePresetStore();

  // Local state
  const [activeTab, setActiveTab] = useState<'presets' | 'templates' | 'import-export'>('presets');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPreset, setSelectedPreset] = useState<string | null>(null);
  const [showSaveDialog, setShowSaveDialog] = useState(false);
  const [saveForm, setSaveForm] = useState({ name: '', description: '' });
  const [importText, setImportText] = useState('');
  const [exportText, setExportText] = useState('');

  // Filter presets based on search query
  const filteredPresets = searchQuery 
    ? searchPresets(searchQuery)
    : presets;

  // Sort presets (favorites first, then recently used, then by creation date)
  const sortedPresets = filteredPresets.sort((a, b) => {
    const aIsFavorite = favoritePresets.includes(a.id);
    const bIsFavorite = favoritePresets.includes(b.id);
    const aRecentIndex = recentlyUsed.indexOf(a.id);
    const bRecentIndex = recentlyUsed.indexOf(b.id);

    if (aIsFavorite && !bIsFavorite) return -1;
    if (!aIsFavorite && bIsFavorite) return 1;
    if (aRecentIndex !== -1 && bRecentIndex !== -1) return aRecentIndex - bRecentIndex;
    if (aRecentIndex !== -1) return -1;
    if (bRecentIndex !== -1) return 1;

    return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
  });

  // Handle saving current configuration
  const handleSavePreset = async () => {
    if (!saveForm.name.trim()) return;

    try {
      await savePreset(saveForm.name, saveForm.description, currentConfig);
      setSaveForm({ name: '', description: '' });
      setShowSaveDialog(false);
    } catch (error) {
      console.error('Failed to save preset:', error);
    }
  };

  // Handle loading a preset
  const handleLoadPreset = (presetId: string) => {
    const preset = loadPreset(presetId);
    if (preset) {
      onLoadConfig(preset.config);
      onClose();
    }
  };

  // Handle creating from template
  const handleCreateFromTemplate = (templateId: ConfigurationTemplate) => {
    const config = createFromTemplate(templateId);
    onLoadConfig(config);
    onClose();
  };

  // Handle preset deletion
  const handleDeletePreset = (presetId: string) => {
    if (window.confirm('Are you sure you want to delete this preset?')) {
      deletePreset(presetId);
    }
  };

  // Handle preset duplication
  const handleDuplicatePreset = (presetId: string) => {
    const originalPreset = presets.find(p => p.id === presetId);
    if (originalPreset) {
      const newName = `${originalPreset.name} (Copy)`;
      duplicatePreset(presetId, newName);
    }
  };

  // Handle import
  const handleImport = () => {
    if (importText.trim()) {
      const success = importPreset(importText);
      if (success) {
        setImportText('');
        alert('Preset imported successfully!');
      } else {
        alert('Failed to import preset. Please check the format.');
      }
    }
  };

  // Handle export
  const handleExport = (presetId: string) => {
    try {
      const exported = exportPreset(presetId);
      setExportText(exported);
    } catch (error) {
      alert('Failed to export preset.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="preset-manager-overlay">
      <div className="preset-manager">
        {/* Header */}
        <div className="preset-manager-header">
          <h2 className="preset-manager-title">
            <i className="fas fa-save"></i>
            Preset Manager
          </h2>
          <button className="close-button" onClick={onClose}>
            <i className="fas fa-times"></i>
          </button>
        </div>

        {/* Tab Navigation */}
        <div className="preset-tabs">
          <button 
            className={`preset-tab ${activeTab === 'presets' ? 'active' : ''}`}
            onClick={() => setActiveTab('presets')}
          >
            <i className="fas fa-bookmark"></i>
            My Presets ({presets.length})
          </button>
          <button 
            className={`preset-tab ${activeTab === 'templates' ? 'active' : ''}`}
            onClick={() => setActiveTab('templates')}
          >
            <i className="fas fa-star"></i>
            Templates ({templates.length})
          </button>
          <button 
            className={`preset-tab ${activeTab === 'import-export' ? 'active' : ''}`}
            onClick={() => setActiveTab('import-export')}
          >
            <i className="fas fa-exchange-alt"></i>
            Import/Export
          </button>
        </div>

        {/* Tab Content */}
        <div className="preset-content">
          {/* My Presets Tab */}
          {activeTab === 'presets' && (
            <div className="presets-tab">
              <div className="presets-controls">
                <div className="search-bar">
                  <i className="fas fa-search"></i>
                  <input
                    type="text"
                    placeholder="Search presets..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="preset-search-input"
                  />
                </div>
                <button 
                  className="config-button"
                  onClick={() => setShowSaveDialog(true)}
                >
                  <i className="fas fa-plus"></i>
                  Save Current
                </button>
              </div>

              <div className="presets-list">
                {sortedPresets.length === 0 ? (
                  <div className="no-presets">
                    <i className="fas fa-info-circle"></i>
                    <p>No presets found. Save your current configuration to get started.</p>
                  </div>
                ) : (
                  sortedPresets.map(preset => (
                    <div 
                      key={preset.id} 
                      className={`preset-item ${selectedPreset === preset.id ? 'selected' : ''}`}
                    >
                      <div className="preset-info">
                        <div className="preset-header">
                          <h4 className="preset-name">
                            {favoritePresets.includes(preset.id) && (
                              <i className="fas fa-star favorite-star"></i>
                            )}
                            {preset.name}
                            {recentlyUsed.includes(preset.id) && (
                              <span className="recent-badge">Recent</span>
                            )}
                          </h4>
                          <div className="preset-actions">
                            <button
                              className="action-button"
                              onClick={() => favoritePresets.includes(preset.id) 
                                ? removeFromFavorites(preset.id)
                                : addToFavorites(preset.id)
                              }
                              title={favoritePresets.includes(preset.id) ? 'Remove from favorites' : 'Add to favorites'}
                            >
                              <i className={`fas ${favoritePresets.includes(preset.id) ? 'fa-star' : 'fa-star-o'}`}></i>
                            </button>
                            <button
                              className="action-button"
                              onClick={() => handleDuplicatePreset(preset.id)}
                              title="Duplicate preset"
                            >
                              <i className="fas fa-copy"></i>
                            </button>
                            <button
                              className="action-button"
                              onClick={() => handleExport(preset.id)}
                              title="Export preset"
                            >
                              <i className="fas fa-download"></i>
                            </button>
                            <button
                              className="action-button danger"
                              onClick={() => handleDeletePreset(preset.id)}
                              title="Delete preset"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </div>
                        </div>
                        <p className="preset-description">{preset.description}</p>
                        <div className="preset-metadata">
                          <div className="metadata-item">
                            <span>Created:</span>
                            <span>{new Date(preset.createdAt).toLocaleDateString()}</span>
                          </div>
                          <div className="metadata-item">
                            <span>Used:</span>
                            <span>{preset.usage} times</span>
                          </div>
                          <div className="preset-tags">
                            {preset.config.tags.map(tag => (
                              <span key={tag} className="preset-tag">{tag}</span>
                            ))}
                          </div>
                        </div>
                      </div>
                      <div className="preset-preview">
                        <div className="preview-item">
                          <span>Investment:</span>
                          <span>{preset.config.purchaseAmount?.totalBnb || 0} BNB</span>
                        </div>
                        <div className="preview-item">
                          <span>Strategy:</span>
                          <span>{preset.config.strategy?.buyStrategy || 'N/A'}</span>
                        </div>
                        <div className="preview-item">
                          <span>Stagger:</span>
                          <span>{preset.config.executionParams?.staggerSettings?.enabled ? 'Yes' : 'No'}</span>
                        </div>
                      </div>
                      <button 
                        className="load-preset-button"
                        onClick={() => handleLoadPreset(preset.id)}
                      >
                        Load Preset
                      </button>
                    </div>
                  ))
                )}
              </div>
            </div>
          )}

          {/* Templates Tab */}
          {activeTab === 'templates' && (
            <div className="templates-tab">
              <div className="templates-grid">
                {templates.map(template => (
                  <div key={template.id} className="template-card">
                    <div className="template-header">
                      <h4 className="template-name">{template.name}</h4>
                      <div className={`risk-badge ${template.riskLevel}`}>
                        {template.riskLevel.toUpperCase()} RISK
                      </div>
                    </div>
                    <p className="template-description">{template.description}</p>

                    <div className="template-features">
                      <div className="feature-item">
                        <span>Investment:</span>
                        <span>{template.config.purchaseAmount?.totalBnb || 0} BNB</span>
                      </div>
                      <div className="feature-item">
                        <span>Stagger:</span>
                        <span>{template.config.executionParams?.staggerSettings?.enabled ? 'Yes' : 'No'}</span>
                      </div>
                      <div className="feature-item">
                        <span>Stealth:</span>
                        <span>{template.config.executionParams?.stealthMode?.enabled ? 'Yes' : 'No'}</span>
                      </div>
                      <div className="feature-item">
                        <span>MEV Protection:</span>
                        <span>{template.config.transactionSettings?.mevProtection?.enabled ? 'Yes' : 'No'}</span>
                      </div>
                    </div>

                    <button 
                      className="config-button"
                      onClick={() => handleCreateFromTemplate(template.id)}
                    >
                      Use Template
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Import/Export Tab */}
          {activeTab === 'import-export' && (
            <div className="import-export-tab">
              <div className="import-section">
                <h4>Import Preset</h4>
                <p>Paste a preset configuration JSON below to import it:</p>
                <textarea
                  className="import-textarea"
                  placeholder="Paste preset JSON here..."
                  value={importText}
                  onChange={(e) => setImportText(e.target.value)}
                  rows={10}
                />
                <div className="import-actions">
                  <button 
                    className="config-button"
                    onClick={handleImport}
                    disabled={!importText.trim()}
                  >
                    <i className="fas fa-upload"></i>
                    Import Preset
                  </button>
                  <button 
                    className="config-button secondary"
                    onClick={() => setImportText('')}
                  >
                    Clear
                  </button>
                </div>
              </div>

              {exportText && (
                <div className="export-section">
                  <h4>Export Result</h4>
                  <p>Copy the JSON below to share your preset:</p>
                  <textarea
                    className="export-textarea"
                    value={exportText}
                    readOnly
                    rows={10}
                  />
                  <div className="export-actions">
                    <button 
                      className="config-button"
                      onClick={() => {
                        navigator.clipboard.writeText(exportText);
                        alert('Copied to clipboard!');
                      }}
                    >
                      <i className="fas fa-copy"></i>
                      Copy to Clipboard
                    </button>
                    <button 
                      className="config-button secondary"
                      onClick={() => setExportText('')}
                    >
                      Clear
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Save Dialog */}
      {showSaveDialog && (
        <div className="save-dialog-overlay">
          <div className="save-dialog">
            <h3>Save Configuration Preset</h3>
            <div className="save-form">
              <div className="config-input-group">
                <label className="config-label">Preset Name</label>
                <input
                  type="text"
                  className="config-input"
                  placeholder="Enter preset name..."
                  value={saveForm.name}
                  onChange={(e) => setSaveForm(prev => ({ ...prev, name: e.target.value }))}
                />
              </div>
              <div className="config-input-group">
                <label className="config-label">Description (Optional)</label>
                <textarea
                  className="config-textarea"
                  placeholder="Describe this preset..."
                  value={saveForm.description}
                  onChange={(e) => setSaveForm(prev => ({ ...prev, description: e.target.value }))}
                  rows={3}
                />
              </div>
            </div>
            <div className="save-dialog-actions">
              <button 
                className="config-button"
                onClick={handleSavePreset}
                disabled={!saveForm.name.trim()}
              >
                <i className="fas fa-save"></i>
                Save Preset
              </button>
              <button 
                className="config-button secondary"
                onClick={() => {
                  setShowSaveDialog(false);
                  setSaveForm({ name: '', description: '' });
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

