/**
 * Transaction Tracking and Management Store
 * Handles individual transaction states, queuing, and historical data
 */

import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import type { Transaction } from '../types';

export type TransactionStatus = 'queued' | 'pending' | 'submitted' | 'confirming' | 'confirmed' | 'failed' | 'cancelled';
export type TransactionType = 'buy' | 'sell' | 'approve' | 'transfer' | 'funding';
export type TransactionPriority = 'low' | 'normal' | 'high' | 'critical';

export interface EnhancedTransaction extends Omit<Transaction, 'status'> {
  // Override status with enhanced values
  status: TransactionStatus;

  // Enhanced fields
  type: TransactionType;
  priority: TransactionPriority;
  walletId: string;
  executionId: string;
  batchId?: string;

  // Timing
  queuedAt: string;
  submittedAt?: string;
  confirmedAt?: string;
  failedAt?: string;

  // Gas and fees
  gasLimit: string;
  maxFeePerGas?: string;
  maxPriorityFeePerGas?: string;
  effectiveGasPrice?: string;
  gasUsedActual?: string;

  // Transaction data
  inputData?: string;
  amount?: string; // Transaction amount (alias for value)
  data?: string; // Transaction data
  nonce?: number;
  confirmations: number;
  requiredConfirmations: number;

  // Error handling
  error?: string;
  retryCount: number;
  maxRetries: number;

  // MEV protection
  isPrivateMempool?: boolean;
  bundleHash?: string;

  // Execution context
  staggerDelay?: number;
  actualDelay?: number;
  batchPosition?: number;

  // Tax system properties
  taxCollectionStatus?: 'pending' | 'processing' | 'completed' | 'failed';
  taxTransactionHash?: string;

  // Additional required fields
  createdAt?: string;
  updatedAt?: string;
}

export interface TransactionBatch {
  id: string;
  executionId: string;
  transactions: string[]; // Transaction IDs
  status: 'pending' | 'executing' | 'completed' | 'failed';
  startedAt?: string;
  completedAt?: string;
  concurrentLimit: number;
  pauseBetweenTx: number;
}

export interface GasTracker {
  networkGasPrice: string;
  recommendedGasPrice: string;
  fastGasPrice: string;
  estimatedConfirmationTime: number;
  networkCongestion: 'low' | 'medium' | 'high';
  lastUpdated: string;
}

export interface RetryPolicy {
  enabled: boolean;
  maxRetries: number;
  baseDelayMs: number;
  maxDelayMs: number;
  backoffMultiplier: number;
  retryableErrors: string[];
}

export interface TransactionQueue {
  transactions: string[]; // Transaction IDs in order
  currentIndex: number;
  isPaused: boolean;
  concurrentLimit: number;
  activeTransactions: string[];
}

export interface TransactionFilters {
  status?: TransactionStatus[];
  type?: TransactionType[];
  walletId?: string;
  executionId?: string;
  dateRange?: {
    start: string;
    end: string;
  };
}

export interface TransactionMetrics {
  totalTransactions: number;
  successRate: number;
  averageConfirmationTime: number;
  averageGasUsed: string;
  totalGasCost: string;
  fastestTransaction: number;
  slowestTransaction: number;
  retryRate: number;
}

interface TransactionState {
  // Core data
  transactions: Record<string, EnhancedTransaction>;
  batches: Record<string, TransactionBatch>;
  queue: TransactionQueue;

  // Gas management
  gasTracker: GasTracker;
  retryPolicy: RetryPolicy;

  // Metrics and filtering
  metrics: TransactionMetrics;
  filters: TransactionFilters;

  // Real-time updates
  isMonitoring: boolean;
  lastUpdate: Date | null;

  // Actions - Transaction Management
  addTransaction: (tx: Omit<EnhancedTransaction, 'id' | 'queuedAt'>) => string;
  updateTransaction: (id: string, updates: Partial<EnhancedTransaction>) => void;
  updateTransactionStatus: (id: string, status: TransactionStatus) => void;
  removeTransaction: (id: string) => void;
  retryTransaction: (id: string) => Promise<boolean>;
  cancelTransaction: (id: string) => void;

  // Actions - Queue Management
  queueTransaction: (txId: string, priority?: TransactionPriority) => void;
  dequeueTransaction: () => string | null;
  pauseQueue: () => void;
  resumeQueue: () => void;
  clearQueue: () => void;
  reorderQueue: (txIds: string[]) => void;

  // Actions - Batch Management
  createBatch: (executionId: string, txIds: string[], concurrentLimit: number) => string;
  updateBatch: (batchId: string, updates: Partial<TransactionBatch>) => void;
  completeBatch: (batchId: string) => void;

  // Actions - Gas Management
  updateGasTracker: (gasData: Partial<GasTracker>) => void;
  optimizeGasPrice: (txId: string) => string;

  // Actions - Monitoring
  startMonitoring: () => void;
  stopMonitoring: () => void;
  pollTransactionStatus: (txId: string) => Promise<void>;

  // Actions - Filtering and Search
  setFilters: (filters: Partial<TransactionFilters>) => void;
  clearFilters: () => void;
  getFilteredTransactions: () => EnhancedTransaction[];
  searchTransactions: (query: string) => EnhancedTransaction[];

  // Actions - Metrics
  calculateMetrics: () => void;
  getTransactionsByStatus: (status: TransactionStatus) => EnhancedTransaction[];
  getTransactionsByWallet: (walletId: string) => EnhancedTransaction[];
  getTransactionsByExecution: (executionId: string) => EnhancedTransaction[];

  // Actions - Utilities
  exportTransactionHistory: () => string;
  importTransactionHistory: (data: string) => boolean;
  clearHistory: () => void;
  clearCompleted: () => void;
}

const initialGasTracker: GasTracker = {
  networkGasPrice: '5000000000', // 5 gwei
  recommendedGasPrice: '5000000000',
  fastGasPrice: '7000000000', // 7 gwei
  estimatedConfirmationTime: 60, // seconds
  networkCongestion: 'medium',
  lastUpdated: new Date().toISOString(),
};

const initialRetryPolicy: RetryPolicy = {
  enabled: true,
  maxRetries: 3,
  baseDelayMs: 5000,
  maxDelayMs: 30000,
  backoffMultiplier: 2,
  retryableErrors: [
    'insufficient funds',
    'nonce too low',
    'replacement transaction underpriced',
    'network error',
    'timeout',
  ],
};

const initialQueue: TransactionQueue = {
  transactions: [],
  currentIndex: 0,
  isPaused: false,
  concurrentLimit: 5,
  activeTransactions: [],
};

const initialMetrics: TransactionMetrics = {
  totalTransactions: 0,
  successRate: 0,
  averageConfirmationTime: 0,
  averageGasUsed: '0',
  totalGasCost: '0',
  fastestTransaction: 0,
  slowestTransaction: 0,
  retryRate: 0,
};

export const useTransactionStore = create<TransactionState>()(
  persist(
    (set, get) => ({
      // Initial state
      transactions: {},
      batches: {},
      queue: { ...initialQueue },
      gasTracker: { ...initialGasTracker },
      retryPolicy: { ...initialRetryPolicy },
      metrics: { ...initialMetrics },
      filters: {},
      isMonitoring: false,
      lastUpdate: null,

      // Add transaction
      addTransaction: (txData: Omit<EnhancedTransaction, 'id' | 'queuedAt'>) => {
        const id = `tx_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
        const timestamp = new Date().toISOString();

        const transaction: EnhancedTransaction = {
          ...txData,
          id,
          queuedAt: timestamp,
          confirmations: 0,
          requiredConfirmations: 1,
          retryCount: 0,
          maxRetries: get().retryPolicy.maxRetries,
        };

        set((state: TransactionState) => ({
          ...state,
          transactions: {
            ...state.transactions,
            [id]: transaction,
          },
          lastUpdate: new Date(),
        }));

        // Auto-calculate metrics
        get().calculateMetrics();

        return id;
      },

      // Update transaction
      updateTransaction: (id: string, updates: Partial<EnhancedTransaction>) => {
        set((state: TransactionState) => {
          const transaction = state.transactions[id];
          if (!transaction) return state;

          const updatedTransaction = { ...transaction, ...updates };

          // Update timestamps based on status changes
          if (updates.status) {
            const timestamp = new Date().toISOString();
            switch (updates.status) {
              case 'submitted':
                updatedTransaction.submittedAt = timestamp;
                break;
              case 'confirmed':
                updatedTransaction.confirmedAt = timestamp;
                break;
              case 'failed':
                updatedTransaction.failedAt = timestamp;
                break;
            }
          }

          return {
            ...state,
            transactions: {
              ...state.transactions,
              [id]: updatedTransaction,
            },
            lastUpdate: new Date(),
          };
        });

        // Auto-calculate metrics after update
        get().calculateMetrics();
      },

      // Update transaction status (convenience method)
      updateTransactionStatus: (id: string, status: TransactionStatus) => {
        get().updateTransaction(id, { status });
      },

      // Remove transaction
      removeTransaction: (id: string) => {
        set((state: TransactionState) => {
          const { [id]: removed, ...rest } = state.transactions;

          return {
            ...state,
            transactions: rest,
            queue: {
              ...state.queue,
              transactions: state.queue.transactions.filter(txId => txId !== id),
              activeTransactions: state.queue.activeTransactions.filter(txId => txId !== id),
            },
            lastUpdate: new Date(),
          };
        });
      },

      // Retry transaction
      retryTransaction: async (id: string) => {
        try {
          const state = get();
          const transaction = state.transactions[id];

          if (!transaction) {
            throw new Error('Transaction not found');
          }

          if (transaction.retryCount >= transaction.maxRetries) {
            throw new Error('Maximum retry attempts reached');
          }

          // Check if error is retryable
          const isRetryable = state.retryPolicy.retryableErrors.some(error => 
            transaction.error?.toLowerCase().includes(error.toLowerCase())
          );

          if (!isRetryable) {
            throw new Error('Error is not retryable');
          }

          // Calculate retry delay with exponential backoff
          const delay = Math.min(
            state.retryPolicy.baseDelayMs * Math.pow(state.retryPolicy.backoffMultiplier, transaction.retryCount),
            state.retryPolicy.maxDelayMs
          );

          // Schedule retry
          setTimeout(() => {
            get().updateTransaction(id, {
              status: 'queued',
              retryCount: transaction.retryCount + 1,
              error: undefined,
            });

            // Re-queue transaction
            get().queueTransaction(id, transaction.priority);
          }, delay);

          return true;
        } catch (error) {
          console.error('Failed to retry transaction:', error);
          return false;
        }
      },

      // Cancel transaction
      cancelTransaction: (id: string) => {
        get().updateTransaction(id, {
          status: 'cancelled',
          error: 'Cancelled by user',
        });
      },

      // Queue transaction
      queueTransaction: (txId: string, priority: TransactionPriority = 'normal') => {
        set((state: TransactionState) => {
          const queue = { ...state.queue };

          // Remove if already in queue
          queue.transactions = queue.transactions.filter(id => id !== txId);

          // Insert based on priority
          if (priority === 'critical') {
            queue.transactions.unshift(txId);
          } else if (priority === 'high') {
            const firstNormalIndex = queue.transactions.findIndex(id => {
              const tx = state.transactions[id];
              return tx && tx.priority !== 'critical';
            });
            if (firstNormalIndex === -1) {
              queue.transactions.push(txId);
            } else {
              queue.transactions.splice(firstNormalIndex, 0, txId);
            }
          } else {
            queue.transactions.push(txId);
          }

          return {
            ...state,
            queue,
            lastUpdate: new Date(),
          };
        });
      },

      // Dequeue transaction
      dequeueTransaction: () => {
        const state = get();

        if (state.queue.isPaused || state.queue.transactions.length === 0) {
          return null;
        }

        if (state.queue.activeTransactions.length >= state.queue.concurrentLimit) {
          return null;
        }

        const txId = state.queue.transactions[state.queue.currentIndex];

        set((state: TransactionState) => ({
          ...state,
          queue: {
            ...state.queue,
            currentIndex: state.queue.currentIndex + 1,
            activeTransactions: [...state.queue.activeTransactions, txId],
          },
          lastUpdate: new Date(),
        }));

        return txId;
      },

      // Pause queue
      pauseQueue: () => {
        set((state: TransactionState) => ({
          ...state,
          queue: {
            ...state.queue,
            isPaused: true,
          },
          lastUpdate: new Date(),
        }));
      },

      // Resume queue
      resumeQueue: () => {
        set((state: TransactionState) => ({
          ...state,
          queue: {
            ...state.queue,
            isPaused: false,
          },
          lastUpdate: new Date(),
        }));
      },

      // Clear queue
      clearQueue: () => {
        set((state: TransactionState) => ({
          ...state,
          queue: {
            ...initialQueue,
            concurrentLimit: state.queue.concurrentLimit,
          },
          lastUpdate: new Date(),
        }));
      },

      // Reorder queue
      reorderQueue: (txIds: string[]) => {
        set((state: TransactionState) => ({
          ...state,
          queue: {
            ...state.queue,
            transactions: txIds,
            currentIndex: 0,
          },
          lastUpdate: new Date(),
        }));
      },

      // Create batch
      createBatch: (executionId: string, txIds: string[], concurrentLimit: number) => {
        const batchId = `batch_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

        const batch: TransactionBatch = {
          id: batchId,
          executionId,
          transactions: txIds,
          status: 'pending',
          concurrentLimit,
          pauseBetweenTx: 1000, // 1 second default
        };

        set((state: TransactionState) => ({
          ...state,
          batches: {
            ...state.batches,
            [batchId]: batch,
          },
          lastUpdate: new Date(),
        }));

        return batchId;
      },

      // Update batch
      updateBatch: (batchId: string, updates: Partial<TransactionBatch>) => {
        set((state: TransactionState) => {
          const batch = state.batches[batchId];
          if (!batch) return state;

          const updatedBatch = { ...batch, ...updates };

          // Update timestamps
          if (updates.status === 'executing' && !batch.startedAt) {
            updatedBatch.startedAt = new Date().toISOString();
          } else if (['completed', 'failed'].includes(updates.status || '') && !batch.completedAt) {
            updatedBatch.completedAt = new Date().toISOString();
          }

          return {
            ...state,
            batches: {
              ...state.batches,
              [batchId]: updatedBatch,
            },
            lastUpdate: new Date(),
          };
        });
      },

      // Complete batch
      completeBatch: (batchId: string) => {
        get().updateBatch(batchId, {
          status: 'completed',
          completedAt: new Date().toISOString(),
        });
      },

      // Update gas tracker
      updateGasTracker: (gasData: Partial<GasTracker>) => {
        set((state: TransactionState) => ({
          ...state,
          gasTracker: {
            ...state.gasTracker,
            ...gasData,
            lastUpdated: new Date().toISOString(),
          },
          lastUpdate: new Date(),
        }));
      },

      // Optimize gas price
      optimizeGasPrice: (txId: string) => {
        const state = get();
        const transaction = state.transactions[txId];
        const gasTracker = state.gasTracker;

        if (!transaction) return '0';

        // Simple optimization based on priority and network conditions
        let gasPrice = gasTracker.recommendedGasPrice;

        if (transaction.priority === 'high' || transaction.priority === 'critical') {
          gasPrice = gasTracker.fastGasPrice;
        }

        if (gasTracker.networkCongestion === 'high') {
          gasPrice = (BigInt(gasPrice) * BigInt(120) / BigInt(100)).toString(); // +20%
        }

        return gasPrice;
      },

      // Start monitoring
      startMonitoring: () => {
        set({ isMonitoring: true });
      },

      // Stop monitoring
      stopMonitoring: () => {
        set({ isMonitoring: false });
      },

      // Poll transaction status (placeholder - would integrate with blockchain API)
      pollTransactionStatus: async (txId: string) => {
        // In real implementation, this would check blockchain for transaction status
        const state = get();
        const transaction = state.transactions[txId];

        if (!transaction || !transaction.hash) {
          return;
        }

        // Simulate status polling
        // get().updateTransaction(txId, { confirmations: transaction.confirmations + 1 });
      },

      // Set filters
      setFilters: (filters: Partial<TransactionFilters>) => {
        set((state: TransactionState) => ({
          ...state,
          filters: { ...state.filters, ...filters },
          lastUpdate: new Date(),
        }));
      },

      // Clear filters
      clearFilters: () => {
        set({ filters: {} });
      },

      // Get filtered transactions
      getFilteredTransactions: () => {
        const state = get();
        const transactions = Object.values(state.transactions);
        const filters = state.filters;

        return transactions.filter(tx => {
          if (filters.status && !filters.status.includes(tx.status as TransactionStatus)) {
            return false;
          }

          if (filters.type && !filters.type.includes(tx.type)) {
            return false;
          }

          if (filters.walletId && tx.walletId !== filters.walletId) {
            return false;
          }

          if (filters.executionId && tx.executionId !== filters.executionId) {
            return false;
          }

          if (filters.dateRange) {
            const txDate = new Date(tx.queuedAt);
            const startDate = new Date(filters.dateRange.start);
            const endDate = new Date(filters.dateRange.end);

            if (txDate < startDate || txDate > endDate) {
              return false;
            }
          }

          return true;
        });
      },

      // Search transactions
      searchTransactions: (query: string) => {
        const state = get();
        const transactions = Object.values(state.transactions);
        const lowercaseQuery = query.toLowerCase();

        return transactions.filter(tx => 
          tx.id.toLowerCase().includes(lowercaseQuery) ||
          tx.hash.toLowerCase().includes(lowercaseQuery) ||
          tx.from.toLowerCase().includes(lowercaseQuery) ||
          tx.to.toLowerCase().includes(lowercaseQuery) ||
          tx.walletId.toLowerCase().includes(lowercaseQuery)
        );
      },

      // Calculate metrics
      calculateMetrics: () => {
        set((state: TransactionState) => {
          const transactions = Object.values(state.transactions);
          const completedTx = transactions.filter(tx => 
            ['confirmed', 'failed'].includes(tx.status)
          );

          const successfulTx = transactions.filter(tx => tx.status === 'confirmed');
          const failedTx = transactions.filter(tx => tx.status === 'failed');
          const retriedTx = transactions.filter(tx => tx.retryCount > 0);

          const confirmationTimes = successfulTx
            .filter(tx => tx.submittedAt && tx.confirmedAt)
            .map(tx => new Date(tx.confirmedAt!).getTime() - new Date(tx.submittedAt!).getTime());

          const gasUsed = successfulTx
            .filter(tx => tx.gasUsedActual)
            .map(tx => BigInt(tx.gasUsedActual!));

          const metrics: TransactionMetrics = {
            totalTransactions: transactions.length,
            successRate: completedTx.length > 0 ? (successfulTx.length / completedTx.length) * 100 : 0,
            averageConfirmationTime: confirmationTimes.length > 0 
              ? confirmationTimes.reduce((a, b) => a + b, 0) / confirmationTimes.length / 1000 // seconds
              : 0,
            averageGasUsed: gasUsed.length > 0
              ? (gasUsed.reduce((a, b) => a + b, BigInt(0)) / BigInt(gasUsed.length)).toString()
              : '0',
            totalGasCost: '0', // Would calculate from gas used * gas price
            fastestTransaction: confirmationTimes.length > 0 ? Math.min(...confirmationTimes) / 1000 : 0,
            slowestTransaction: confirmationTimes.length > 0 ? Math.max(...confirmationTimes) / 1000 : 0,
            retryRate: transactions.length > 0 ? (retriedTx.length / transactions.length) * 100 : 0,
          };

          return {
            ...state,
            metrics,
            lastUpdate: new Date(),
          };
        });
      },

      // Get transactions by status
      getTransactionsByStatus: (status: TransactionStatus) => {
        const state = get();
        return Object.values(state.transactions).filter(tx => tx.status === status);
      },

      // Get transactions by wallet
      getTransactionsByWallet: (walletId: string) => {
        const state = get();
        return Object.values(state.transactions).filter(tx => tx.walletId === walletId);
      },

      // Get transactions by execution
      getTransactionsByExecution: (executionId: string) => {
        const state = get();
        return Object.values(state.transactions).filter(tx => tx.executionId === executionId);
      },

      // Export transaction history
      exportTransactionHistory: () => {
        const state = get();
        const data = {
          transactions: state.transactions,
          batches: state.batches,
          exportedAt: new Date().toISOString(),
        };
        return JSON.stringify(data, null, 2);
      },

      // Import transaction history
      importTransactionHistory: (data: string) => {
        try {
          const parsed = JSON.parse(data);

          if (!parsed.transactions || !parsed.batches) {
            throw new Error('Invalid data format');
          }

          set((state: TransactionState) => ({
            ...state,
            transactions: { ...state.transactions, ...parsed.transactions },
            batches: { ...state.batches, ...parsed.batches },
            lastUpdate: new Date(),
          }));

          get().calculateMetrics();
          return true;
        } catch (error) {
          console.error('Failed to import transaction history:', error);
          return false;
        }
      },

      // Clear history
      clearHistory: () => {
        set({
          transactions: {},
          batches: {},
          queue: { ...initialQueue },
          metrics: { ...initialMetrics },
          lastUpdate: new Date(),
        });
      },

      // Clear completed transactions
      clearCompleted: () => {
        set((state: TransactionState) => {
          const transactions = Object.fromEntries(
            Object.entries(state.transactions).filter(([_, tx]) => 
              !['confirmed', 'failed', 'cancelled'].includes((tx as EnhancedTransaction).status)
            )
          );

          return {
            ...state,
            transactions,
            lastUpdate: new Date(),
          };
        });

        get().calculateMetrics();
      },
    }),
    {
      name: 'transaction-store',
      // Persist configuration but not sensitive transaction data
      partialize: (state: TransactionState) => ({
        retryPolicy: state.retryPolicy,
        filters: state.filters,
        gasTracker: {
          ...state.gasTracker,
          // Don't persist real-time gas data
          lastUpdated: new Date().toISOString(),
        },
      }),
    }
  )
);

src/components/BundleExecution/BundleExe.content:
/**
 * Bundle Execution Component Styles
 * Comprehensive styling for the execution interface
 * Maintains SolNox BNB dark theme consistency
 */

.bundle-execution {
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: #0a0a0a;
  color: #ffffff;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  overflow: hidden;
}

/* Header Styles */
.execution-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem;
  background: linear-gradient(135deg, #1a1a1a 0%, #2d1810 100%);
  border-bottom: 1px solid #333;
}

.execution-title {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.execution-title h1 {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 600;
  color: #f5b800;
}

.execution-icon {
  font-size: 1.5rem;
}

.execution-actions {
  display: flex;
  gap: 0.75rem;
}

.close-button {
  background: transparent;
  border: 1px solid #555;
  color: #ccc;
  padding: 0.5rem 1rem;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.2s ease;
}

.close-button:hover {
  background: #333;
  color: #fff;
}

/* Main Content */
.execution-content {
  display: flex;
  flex: 1;
  overflow: hidden;
}

.execution-left {
  width: 350px;
  background: #111;
  border-right: 1px solid #333;
  display: flex;
  flex-direction: column;
}

.execution-main {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

/* Execution Controls Styles */
.execution-controls {
  padding: 1.5rem;
  border-bottom: 1px solid #333;
}

.controls-header {
  margin-bottom: 1rem;
}

.controls-header h3 {
  margin: 0 0 0.5rem 0;
  color: #f5b800;
  font-size: 1.125rem;
}

.status-message {
  font-size: 0.875rem;
  opacity: 0.9;
}

.controls-grid {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.control-button {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 1rem;
  background: linear-gradient(135deg, #1a1a1a 0%, #2a2a2a 100%);
  border: 1px solid #444;
  border-radius: 8px;
  color: #fff;
  cursor: pointer;
  transition: all 0.2s ease;
  text-align: left;
}

.control-button:hover {
  background: linear-gradient(135deg, #2a2a2a 0%, #3a3a3a 100%);
  border-color: #f5b800;
}

.control-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  background: #1a1a1a;
}

.control-button:disabled:hover {
  background: #1a1a1a;
  border-color: #444;
}

.start-button {
  border-color: #4caf50;
}

.start-button:hover {
  border-color: #66bb6a;
  box-shadow: 0 0 10px rgba(76, 175, 80, 0.3);
}

.pause-button {
  border-color: #ff9800;
}

.pause-button:hover {
  border-color: #ffb74d;
  box-shadow: 0 0 10px rgba(255, 152, 0, 0.3);
}

.stop-button {
  border-color: #2196f3;
}

.stop-button:hover {
  border-color: #64b5f6;
  box-shadow: 0 0 10px rgba(33, 150, 243, 0.3);
}

.abort-button {
  border-color: #f44336;
}

.abort-button:hover {
  border-color: #ef5350;
  box-shadow: 0 0 10px rgba(244, 67, 54, 0.3);
}

.button-icon {
  font-size: 1.25rem;
}

.button-content {
  display: flex;
  flex-direction: column;
  flex: 1;
}

.button-title {
  font-weight: 600;
  margin-bottom: 0.25rem;
}

.button-subtitle {
  font-size: 0.75rem;
  opacity: 0.7;
}

.control-status {
  margin-top: 1rem;
  padding-top: 1rem;
  border-top: 1px solid #333;
}

.status-indicators {
  display: flex;
  flex-wrap: wrap;
  gap: 0.75rem;
}

.indicator {
  display: flex;
  align-items: center;
  gap: 0.25rem;
  font-size: 0.75rem;
  padding: 0.25rem 0.5rem;
  background: #1a1a1a;
  border-radius: 4px;
}

.indicator.enabled {
  color: #4caf50;
}

.indicator.disabled {
  color: #666;
}

.indicator-dot {
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: currentColor;
}

/* Pre-execution Setup */
.pre-execution {
  flex: 1;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background: #0a0a0a;
}

.setup-container {
  max-width: 500px;
  width: 100%;
  background: linear-gradient(135deg, #1a1a1a 0%, #2d1810 100%);
  border: 1px solid #333;
  border-radius: 12px;
  padding: 2rem;
}

.setup-header {
  text-align: center;
  margin-bottom: 2rem;
}

.setup-header h2 {
  margin: 0 0 0.5rem 0;
  color: #f5b800;
  font-size: 1.5rem;
}

.setup-description {
  color: #ccc;
  font-size: 0.875rem;
  line-height: 1.5;
}

.setup-form {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.form-group label {
  color: #f5b800;
  font-weight: 500;
  font-size: 0.875rem;
}

.form-input {
  background: #2a2a2a;
  border: 1px solid #444;
  border-radius: 6px;
  padding: 0.75rem;
  color: #fff;
  font-family: inherit;
  transition: border-color 0.2s ease;
}

.form-input:focus {
  outline: none;
  border-color: #f5b800;
  box-shadow: 0 0 0 2px rgba(245, 184, 0, 0.1);
}

.passphrase-container {
  position: relative;
}

.passphrase-toggle {
  position: absolute;
  right: 0.75rem;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  color: #ccc;
  cursor: pointer;
  padding: 0.25rem;
}

.passphrase-toggle:hover {
  color: #f5b800;
}

.validation-section {
  padding: 1rem;
  background: #2a2a2a;
  border-radius: 6px;
  border-left: 3px solid #f5b800;
}

.validation-section h4 {
  margin: 0 0 1rem 0;
  color: #f5b800;
  font-size: 0.875rem;
}

.validation-errors {
  list-style: none;
  padding: 0;
  margin: 0;
}

.validation-errors li {
  padding: 0.5rem 0;
  color: #f44336;
  font-size: 0.75rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.validation-errors li::before {
  content: '‚ö†Ô∏è';
}

.setup-actions {
  display: flex;
  gap: 1rem;
}

.primary-button {
  flex: 1;
  background: linear-gradient(135deg, #f5b800 0%, #ff8c00 100%);
  border: none;
  border-radius: 6px;
  padding: 0.875rem 1.5rem;
  color: #000;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
}

.primary-button:hover {
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(245, 184, 0, 0.3);
}

.primary-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
  box-shadow: none;
}

.secondary-button {
  background: transparent;
  border: 1px solid #666;
  border-radius: 6px;
  padding: 0.875rem 1.5rem;
  color: #ccc;
  cursor: pointer;
  transition: all 0.2s ease;
}

.secondary-button:hover {
  background: #2a2a2a;
  color: #fff;
}

/* Risk Assessment */
.risk-assessment {
  background: #2a2a2a;
  border-radius: 6px;
  padding: 1rem;
}

.risk-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1rem;
}

.risk-level {
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
}

.risk-level.low {
  background: #4caf50;
  color: #000;
}

.risk-level.medium {
  background: #ff9800;
  color: #000;
}

.risk-level.high {
  background: #f44336;
  color: #fff;
}

.risk-factors, .risk-recommendations {
  margin-bottom: 1rem;
}

.risk-factors h5, .risk-recommendations h5 {
  margin: 0 0 0.5rem 0;
  color: #f5b800;
  font-size: 0.75rem;
}

.risk-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.risk-list li {
  padding: 0.25rem 0;
  font-size: 0.75rem;
  color: #ccc;
  display: flex;
  align-items: flex-start;
  gap: 0.5rem;
}

.risk-list li::before {
  content: '‚Ä¢';
  color: #f5b800;
  font-weight: bold;
}

/* Confirmation Dialog */
.confirmation-dialog {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
}

.confirmation-content {
  background: #1a1a1a;
  border: 1px solid #333;
  border-radius: 12px;
  padding: 2rem;
  max-width: 500px;
  width: 90%;
}

.confirmation-header {
  text-align: center;
  margin-bottom: 1.5rem;
}

.confirmation-header h3 {
  margin: 0 0 0.5rem 0;
  color: #f5b800;
}

.confirmation-header p {
  margin: 0;
  color: #ccc;
  font-size: 0.875rem;
}

.confirmation-actions {
  display: flex;
  gap: 1rem;
  justify-content: center;
}

/* Tab Navigation */
.tab-navigation {
  display: flex;
  background: #1a1a1a;
  border-bottom: 1px solid #333;
}

.tab-button {
  background: transparent;
  border: none;
  color: #ccc;
  padding: 1rem 1.5rem;
  cursor: pointer;
  border-bottom: 2px solid transparent;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-family: inherit;
}

.tab-button:hover {
  background: #2a2a2a;
  color: #fff;
}

.tab-button.active {
  color: #f5b800;
  border-bottom-color: #f5b800;
  background: #2a2a2a;
}

.tab-badge {
  background: #f5b800;
  color: #000;
  font-size: 0.75rem;
  font-weight: 600;
  padding: 0.125rem 0.375rem;
  border-radius: 10px;
  min-width: 1.5rem;
  text-align: center;
}

.tab-content {
  flex: 1;
  overflow: hidden;
}

/* Status Dashboard Styles */
.status-dashboard {
  padding: 1.5rem;
  overflow-y: auto;
  background: #0a0a0a;
}

.overview-cards {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1rem;
  margin-bottom: 2rem;
}

.overview-card {
  background: linear-gradient(135deg, #1a1a1a 0%, #2a2a2a 100%);
  border: 1px solid #333;
  border-radius: 8px;
  padding: 1.5rem;
}

.card-header {
  display: flex;
  justify-content: between;
  align-items: center;
  margin-bottom: 1rem;
}

.card-header h4 {
  margin: 0;
  color: #f5b800;
  font-size: 0.875rem;
  font-weight: 600;
}

.card-value {
  font-size: 1.5rem;
  font-weight: 700;
  color: #fff;
}

.progress-bar {
  width: 100%;
  height: 6px;
  background: #2a2a2a;
  border-radius: 3px;
  overflow: hidden;
  margin: 0.75rem 0;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #f5b800 0%, #ff8c00 100%);
  transition: width 0.3s ease;
}

.card-details {
  font-size: 0.75rem;
  color: #ccc;
}

.stats-breakdown {
  display: flex;
  justify-content: space-between;
  margin-top: 1rem;
}

.stat-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.25rem;
}

.stat-label {
  font-size: 0.75rem;
  color: #ccc;
}

.stat-value {
  font-weight: 600;
  color: #fff;
}

.stat-item.success .stat-value {
  color: #4caf50;
}

.stat-item.failed .stat-value {
  color: #f44336;
}

.stat-item.pending .stat-value {
  color: #ff9800;
}

/* Transaction Queue Styles */
.transaction-queue {
  display: flex;
  flex-direction: column;
  height: 100%;
  background: #0a0a0a;
}

.queue-header {
  padding: 1.5rem;
  background: #1a1a1a;
  border-bottom: 1px solid #333;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 1rem;
}

.queue-stats {
  display: flex;
  gap: 1.5rem;
  flex-wrap: wrap;
}

.stat-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.25rem;
}

.stat-label {
  font-size: 0.75rem;
  color: #ccc;
}

.stat-value {
  font-weight: 600;
  font-size: 1rem;
}

.stat-value.queued {
  color: #9e9e9e;
}

.stat-value.pending {
  color: #ff9800;
}

.stat-value.confirmed {
  color: #4caf50;
}

.stat-value.failed {
  color: #f44336;
}

.queue-controls {
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
}

.control-group {
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
}

.control-group label {
  font-size: 0.75rem;
  color: #ccc;
}

.control-group select {
  background: #2a2a2a;
  border: 1px solid #444;
  border-radius: 4px;
  padding: 0.375rem 0.5rem;
  color: #fff;
  font-size: 0.75rem;
}

.queue-status {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 1rem 1.5rem;
  background: linear-gradient(135deg, #2a2a2a 0%, #3a3a3a 100%);
  border-bottom: 1px solid #333;
}

.queue-status.paused {
  background: linear-gradient(135deg, #ff9800 0%, #f57c00 100%);
  color: #000;
}

.queue-status.active {
  background: linear-gradient(135deg, #4caf50 0%, #388e3c 100%);
  color: #000;
}

.status-icon {
  font-size: 1.25rem;
}

.status-message {
  display: flex;
  flex-direction: column;
}

.status-title {
  font-weight: 600;
}

.status-description {
  font-size: 0.75rem;
  opacity: 0.8;
}

.transaction-list {
  flex: 1;
  overflow-y: auto;
  padding: 1rem;
}

.empty-queue {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  gap: 1rem;
  color: #666;
}

.empty-icon {
  font-size: 3rem;
}

.empty-message {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
}

.empty-title {
  font-weight: 600;
  color: #999;
}

.empty-description {
  font-size: 0.75rem;
  color: #666;
