/**
 * Execution Controls Component
 * Provides start, pause, stop, and emergency abort controls for bundle execution
 */

import React, { useState } from 'react';
import type { ExecutionStatus } from '../../store/execution';

interface ExecutionControlsProps {
  status: ExecutionStatus;
  canStart: boolean;
  canPause: boolean;
  canStop: boolean;
  canAbort: boolean;
  onStart: () => void;
  onPause: () => void;
  onStop: () => void;
  onAbort: () => void;
}

export const ExecutionControls: React.FC<ExecutionControlsProps> = ({
  status,
  canStart,
  canPause,
  canStop,
  canAbort,
  onStart,
  onPause,
  onStop,
  onAbort,
}) => {
  const [showAbortConfirm, setShowAbortConfirm] = useState(false);
  const [showStopConfirm, setShowStopConfirm] = useState(false);

  const handleAbortClick = () => {
    setShowAbortConfirm(true);
  };

  const confirmAbort = () => {
    setShowAbortConfirm(false);
    onAbort();
  };

  const handleStopClick = () => {
    setShowStopConfirm(true);
  };

  const confirmStop = () => {
    setShowStopConfirm(false);
    onStop();
  };

  const getStatusMessage = () => {
    switch (status) {
      case 'idle':
        return 'Ready to start execution';
      case 'preparing':
        return 'Preparing execution environment...';
      case 'executing':
        return 'Execution in progress';
      case 'paused':
        return 'Execution paused - ready to resume';
      case 'completed':
        return 'Execution completed successfully';
      case 'failed':
        return 'Execution failed';
      case 'aborted':
        return 'Execution aborted by user';
      case 'stopping':
        return 'Stopping execution...';
      default:
        return 'Unknown status';
    }
  };

  const getStatusColor = () => {
    switch (status) {
      case 'executing':
        return '#4caf50'; // Green
      case 'paused':
        return '#ff9800'; // Orange
      case 'completed':
        return '#2196f3'; // Blue
      case 'failed':
      case 'aborted':
        return '#f44336'; // Red
      case 'stopping':
        return '#9e9e9e'; // Gray
      default:
        return '#757575'; // Light gray
    }
  };

  return (
    <div className="execution-controls">
      <div className="controls-header">
        <h3>Execution Controls</h3>
        <div className="status-message" style={{ color: getStatusColor() }}>
          {getStatusMessage()}
        </div>
      </div>

      <div className="controls-grid">
        {/* Start/Resume Button */}
        {(status === 'idle' || status === 'paused') && (
          <button
            onClick={onStart}
            disabled={!canStart}
            className="control-button start-button"
            title={status === 'paused' ? 'Resume execution' : 'Start execution'}
          >
            <div className="button-icon">‚ñ∂Ô∏è</div>
            <div className="button-content">
              <span className="button-title">
                {status === 'paused' ? 'Resume' : 'Start'}
              </span>
              <span className="button-subtitle">
                {status === 'paused' ? 'Continue execution' : 'Begin bundle execution'}
              </span>
            </div>
          </button>
        )}

        {/* Pause Button */}
        {status === 'executing' && (
          <button
            onClick={onPause}
            disabled={!canPause}
            className="control-button pause-button"
            title="Pause execution"
          >
            <div className="button-icon">‚è∏Ô∏è</div>
            <div className="button-content">
              <span className="button-title">Pause</span>
              <span className="button-subtitle">Pause current execution</span>
            </div>
          </button>
        )}

        {/* Stop Button */}
        {(['executing', 'paused'].includes(status)) && (
          <button
            onClick={handleStopClick}
            disabled={!canStop}
            className="control-button stop-button"
            title="Stop execution gracefully"
          >
            <div className="button-icon">‚èπÔ∏è</div>
            <div className="button-content">
              <span className="button-title">Stop</span>
              <span className="button-subtitle">Complete current transactions and stop</span>
            </div>
          </button>
        )}

        {/* Emergency Abort Button */}
        {(['executing', 'paused', 'stopping'].includes(status)) && (
          <button
            onClick={handleAbortClick}
            disabled={!canAbort}
            className="control-button abort-button"
            title="Emergency abort - immediate stop"
          >
            <div className="button-icon">üõë</div>
            <div className="button-content">
              <span className="button-title">Emergency Abort</span>
              <span className="button-subtitle">Immediately halt all operations</span>
            </div>
          </button>
        )}
      </div>

      {/* Control Status Indicators */}
      <div className="control-status">
        <div className="status-indicators">
          <div className={`indicator ${canStart ? 'enabled' : 'disabled'}`}>
            <span className="indicator-dot"></span>
            <span>Can Start</span>
          </div>
          <div className={`indicator ${canPause ? 'enabled' : 'disabled'}`}>
            <span className="indicator-dot"></span>
            <span>Can Pause</span>
          </div>
          <div className={`indicator ${canStop ? 'enabled' : 'disabled'}`}>
            <span className="indicator-dot"></span>
            <span>Can Stop</span>
          </div>
          <div className={`indicator ${canAbort ? 'enabled' : 'disabled'}`}>
            <span className="indicator-dot"></span>
            <span>Emergency Available</span>
          </div>
        </div>
      </div>

      {/* Safety Features */}
      <div className="safety-features">
        <h4>Safety Controls</h4>
        <div className="safety-items">
          <div className="safety-item">
            <span className="safety-icon">üîí</span>
            <div className="safety-content">
              <span className="safety-title">Secure Session</span>
              <span className="safety-description">Private keys encrypted in memory</span>
            </div>
          </div>
          <div className="safety-item">
            <span className="safety-icon">‚è±Ô∏è</span>
            <div className="safety-content">
              <span className="safety-title">Timeout Protection</span>
              <span className="safety-description">Auto-abort on execution timeout</span>
            </div>
          </div>
          <div className="safety-item">
            <span className="safety-icon">üí∞</span>
            <div className="safety-content">
              <span className="safety-title">Spending Limits</span>
              <span className="safety-description">Enforced maximum spend protection</span>
            </div>
          </div>
        </div>
      </div>

      {/* Stop Confirmation Modal */}
      {showStopConfirm && (
        <div className="confirmation-overlay">
          <div className="confirmation-modal">
            <div className="modal-header">
              <h3>‚ö†Ô∏è Confirm Stop Execution</h3>
            </div>
            <div className="modal-content">
              <p>
                This will gracefully stop the execution after completing current transactions.
                No new transactions will be started.
              </p>
              <div className="warning-box">
                <strong>Note:</strong> Partially completed batches will finish before stopping.
              </div>
            </div>
            <div className="modal-actions">
              <button
                onClick={() => setShowStopConfirm(false)}
                className="secondary-button"
              >
                Cancel
              </button>
              <button
                onClick={confirmStop}
                className="primary-button stop-button"
              >
                Stop Execution
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Abort Confirmation Modal */}
      {showAbortConfirm && (
        <div className="confirmation-overlay">
          <div className="confirmation-modal">
            <div className="modal-header">
              <h3>üõë Confirm Emergency Abort</h3>
            </div>
            <div className="modal-content">
              <p>
                This will immediately halt all execution operations. Current transactions
                may be left in an incomplete state.
              </p>
              <div className="danger-box">
                <strong>Warning:</strong> This action cannot be undone. Use only in emergency situations.
              </div>
              <ul>
                <li>All pending transactions will be cancelled</li>
                <li>Current transactions may fail or remain pending</li>
                <li>Manual intervention may be required to resolve issues</li>
              </ul>
            </div>
            <div className="modal-actions">
              <button
                onClick={() => setShowAbortConfirm(false)}
                className="secondary-button"
              >
                Cancel
              </button>
              <button
                onClick={confirmAbort}
                className="primary-button abort-button"
              >
                Emergency Abort
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ExecutionControls;

/**
 * Execution Summary Component
 * Results and reporting interface for completed or failed bundle executions
 */

import React, { useMemo, useState } from 'react';
import type { ExecutionStatus, ExecutionStatistics } from '../../store/execution';
import type { EnhancedTransaction, GasTracker } from '../../store/transactions';
import type { ExecutionPlan } from '../../services/execution-engine';

interface ExecutionSummaryProps {
  status: ExecutionStatus;
  statistics: ExecutionStatistics;
  transactions: EnhancedTransaction[];
  executionPlan: ExecutionPlan | null;
  gasTracker: GasTracker;
  onExportReport: () => void;
}

export const ExecutionSummary: React.FC<ExecutionSummaryProps> = ({
  status,
  statistics,
  transactions,
  executionPlan,
  gasTracker,
  onExportReport,
}) => {
  const [selectedMetric, setSelectedMetric] = useState<'overview' | 'gas' | 'timing' | 'errors'>('overview');

  const analysisData = useMemo(() => {
    const successful = transactions.filter(tx => tx.status === 'confirmed');
    const failed = transactions.filter(tx => tx.status === 'failed');
    const totalValue = transactions.reduce((sum, tx) => sum + parseFloat(tx.value), 0);
    const totalGasCost = transactions.reduce((sum, tx) => {
      if (tx.gasUsedActual && tx.effectiveGasPrice) {
        return sum + (parseFloat(tx.gasUsedActual) * parseFloat(tx.effectiveGasPrice));
      }
      return sum;
    }, 0);

    // Calculate timing metrics
    const completedTxs = transactions.filter(tx => tx.submittedAt && tx.confirmedAt);
    const confirmationTimes = completedTxs.map(tx => {
      const submitted = new Date(tx.submittedAt!).getTime();
      const confirmed = new Date(tx.confirmedAt!).getTime();
      return confirmed - submitted;
    });

    const avgConfirmationTime = confirmationTimes.length > 0 
      ? confirmationTimes.reduce((a, b) => a + b, 0) / confirmationTimes.length 
      : 0;

    // Calculate success rate by wallet
    const walletStats = transactions.reduce((acc, tx) => {
      if (!acc[tx.walletId]) {
        acc[tx.walletId] = { successful: 0, failed: 0, total: 0 };
      }
      acc[tx.walletId].total++;
      if (tx.status === 'confirmed') acc[tx.walletId].successful++;
      if (tx.status === 'failed') acc[tx.walletId].failed++;
      return acc;
    }, {} as Record<string, { successful: number; failed: number; total: number }>);

    return {
      successful,
      failed,
      totalValue,
      totalGasCost,
      avgConfirmationTime,
      walletStats,
      confirmationTimes,
    };
  }, [transactions]);

  const getStatusColor = () => {
    switch (status) {
      case 'completed': return '#4caf50';
      case 'failed': return '#f44336';
      case 'aborted': return '#ff9800';
      case 'paused': return '#2196f3';
      default: return '#9e9e9e';
    }
  };

  const getStatusIcon = () => {
    switch (status) {
      case 'completed': return '‚úÖ';
      case 'failed': return '‚ùå';
      case 'aborted': return '‚ö†Ô∏è';
      case 'paused': return '‚è∏Ô∏è';
      default: return '‚ö°';
    }
  };

  const formatTime = (ms: number): string => {
    if (ms < 1000) return `${ms}ms`;
    if (ms < 60000) return `${(ms / 1000).toFixed(1)}s`;
    return `${(ms / 60000).toFixed(1)}m`;
  };

  const formatBNB = (value: number): string => {
    return value.toFixed(6);
  };

  const formatGas = (value: number): string => {
    return value.toLocaleString();
  };

  return (
    <div className="execution-summary">
      {/* Summary Header */}
      <div className="summary-header">
        <div className="status-indicator">
          <div className="status-icon" style={{ color: getStatusColor() }}>
            {getStatusIcon()}
          </div>
          <div className="status-info">
            <h2 className="status-title">
              Execution {status.charAt(0).toUpperCase() + status.slice(1)}
            </h2>
            <p className="status-description">
              Bundle execution completed at {new Date().toLocaleString()}
            </p>
          </div>
        </div>

        <div className="summary-actions">
          <button onClick={onExportReport} className="export-button">
            üìÑ Export Report
          </button>
        </div>
      </div>

      {/* Key Metrics */}
      <div className="key-metrics">
        <div className="metric-card success">
          <div className="metric-icon">‚úÖ</div>
          <div className="metric-content">
            <span className="metric-value">{statistics.successfulTransactions}</span>
            <span className="metric-label">Successful</span>
            <span className="metric-percentage">
              {statistics.totalTransactions > 0 
                ? Math.round((statistics.successfulTransactions / statistics.totalTransactions) * 100)
                : 0}%
            </span>
          </div>
        </div>

        <div className="metric-card failed">
          <div className="metric-icon">‚ùå</div>
          <div className="metric-content">
            <span className="metric-value">{statistics.failedTransactions}</span>
            <span className="metric-label">Failed</span>
            <span className="metric-percentage">
              {statistics.totalTransactions > 0 
                ? Math.round((statistics.failedTransactions / statistics.totalTransactions) * 100)
                : 0}%
            </span>
          </div>
        </div>

        <div className="metric-card value">
          <div className="metric-icon">üí∞</div>
          <div className="metric-content">
            <span className="metric-value">{formatBNB(analysisData.totalValue)}</span>
            <span className="metric-label">Total Value (BNB)</span>
          </div>
        </div>

        <div className="metric-card gas">
          <div className="metric-icon">‚õΩ</div>
          <div className="metric-content">
            <span className="metric-value">{formatGas(analysisData.totalGasCost)}</span>
            <span className="metric-label">Gas Cost (wei)</span>
          </div>
        </div>

        <div className="metric-card time">
          <div className="metric-icon">‚è±Ô∏è</div>
          <div className="metric-content">
            <span className="metric-value">{formatTime(analysisData.avgConfirmationTime)}</span>
            <span className="metric-label">Avg Confirmation</span>
          </div>
        </div>
      </div>

      {/* Detailed Analysis Tabs */}
      <div className="analysis-tabs">
        <div className="tab-headers">
          <button
            onClick={() => setSelectedMetric('overview')}
            className={selectedMetric === 'overview' ? 'active' : ''}
          >
            Overview
          </button>
          <button
            onClick={() => setSelectedMetric('gas')}
            className={selectedMetric === 'gas' ? 'active' : ''}
          >
            Gas Analysis
          </button>
          <button
            onClick={() => setSelectedMetric('timing')}
            className={selectedMetric === 'timing' ? 'active' : ''}
          >
            Timing Analysis
          </button>
          <button
            onClick={() => setSelectedMetric('errors')}
            className={selectedMetric === 'errors' ? 'active' : ''}
          >
            Error Analysis
          </button>
        </div>

        <div className="tab-content">
          {selectedMetric === 'overview' && (
            <div className="overview-analysis">
              <div className="analysis-section">
                <h3>Execution Overview</h3>
                <div className="overview-stats">
                  <div className="stat-row">
                    <span className="stat-label">Total Transactions:</span>
                    <span className="stat-value">{statistics.totalTransactions}</span>
                  </div>
                  <div className="stat-row">
                    <span className="stat-label">Success Rate:</span>
                    <span className="stat-value">{statistics.successRate.toFixed(1)}%</span>
                  </div>
                  <div className="stat-row">
                    <span className="stat-label">Total Execution Time:</span>
                    <span className="stat-value">{formatTime(statistics.executionTimeMs)}</span>
                  </div>
                  <div className="stat-row">
                    <span className="stat-label">Average Gas Price:</span>
                    <span className="stat-value">{statistics.averageGasPrice} gwei</span>
                  </div>
                </div>
              </div>

              {/* Wallet Performance */}
              <div className="analysis-section">
                <h3>Wallet Performance</h3>
                <div className="wallet-stats">
                  {Object.entries(analysisData.walletStats).map(([walletId, stats]) => (
                    <div key={walletId} className="wallet-stat-row">
                      <span className="wallet-id">{walletId.substring(0, 8)}...</span>
                      <div className="wallet-metrics">
                        <span className="wallet-successful">{stats.successful} success</span>
                        <span className="wallet-failed">{stats.failed} failed</span>
                        <span className="wallet-rate">
                          {Math.round((stats.successful / stats.total) * 100)}%
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {selectedMetric === 'gas' && (
            <div className="gas-analysis">
              <div className="analysis-section">
                <h3>Gas Usage Analysis</h3>
                <div className="gas-stats">
                  <div className="stat-row">
                    <span className="stat-label">Total Gas Used:</span>
                    <span className="stat-value">{formatGas(parseFloat(statistics.totalGasUsed))}</span>
                  </div>
                  <div className="stat-row">
                    <span className="stat-label">Average Gas per TX:</span>
                    <span className="stat-value">{statistics.averageGasUsed}</span>
                  </div>
                  <div className="stat-row">
                    <span className="stat-label">Total Cost:</span>
                    <span className="stat-value">{statistics.totalCost} BNB</span>
                  </div>
                  <div className="stat-row">
                    <span className="stat-label">Network Gas Price:</span>
                    <span className="stat-value">{gasTracker.networkGasPrice} wei</span>
                  </div>
                </div>
              </div>
            </div>
          )}

          {selectedMetric === 'timing' && (
            <div className="timing-analysis">
              <div className="analysis-section">
                <h3>Transaction Timing</h3>
                <div className="timing-stats">
                  <div className="stat-row">
                    <span className="stat-label">Average Confirmation Time:</span>
                    <span className="stat-value">{formatTime(analysisData.avgConfirmationTime)}</span>
                  </div>
                  <div className="stat-row">
                    <span className="stat-label">Fastest Transaction:</span>
                    <span className="stat-value">
                      {analysisData.confirmationTimes.length > 0 
                        ? formatTime(Math.min(...analysisData.confirmationTimes))
                        : 'N/A'}
                    </span>
                  </div>
                  <div className="stat-row">
                    <span className="stat-label">Slowest Transaction:</span>
                    <span className="stat-value">
                      {analysisData.confirmationTimes.length > 0 
                        ? formatTime(Math.max(...analysisData.confirmationTimes))
                        : 'N/A'}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          )}

          {selectedMetric === 'errors' && (
            <div className="error-analysis">
              <div className="analysis-section">
                <h3>Error Analysis</h3>
                {analysisData.failed.length === 0 ? (
                  <div className="no-errors">
                    <div className="no-errors-icon">üéâ</div>
                    <p>No failed transactions! Excellent execution.</p>
                  </div>
                ) : (
                  <div className="error-list">
                    {analysisData.failed.map(tx => (
                      <div key={tx.id} className="error-item">
                        <div className="error-header">
                          <span className="error-tx-id">{tx.id.substring(0, 8)}...</span>
                          <span className="error-wallet">{tx.walletId.substring(0, 8)}...</span>
                        </div>
                        <div className="error-message">{tx.error || 'Unknown error'}</div>
                        <div className="error-details">
                          Retries: {tx.retryCount}/{tx.maxRetries}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Recommendations */}
      {(status === 'failed' || analysisData.failed.length > 0) && (
        <div className="recommendations">
          <h3>Recommendations</h3>
          <div className="recommendation-list">
            {statistics.successRate < 90 && (
              <div className="recommendation">
                <span className="recommendation-icon">üí°</span>
                <span className="recommendation-text">
                  Consider increasing gas prices or reducing batch size to improve success rate.
                </span>
              </div>
            )}
            {analysisData.avgConfirmationTime > 300000 && (
              <div className="recommendation">
                <span className="recommendation-icon">‚ö°</span>
                <span className="recommendation-text">
                  Transaction confirmation times are slow. Consider using higher priority fees.
                </span>
              </div>
            )}
            {analysisData.failed.length > statistics.totalTransactions * 0.1 && (
              <div className="recommendation">
                <span className="recommendation-icon">üîß</span>
                <span className="recommendation-text">
                  High failure rate detected. Review transaction settings and network conditions.
                </span>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

src/components/BundleExecution/StatusDas.content:
/**
 * Status Dashboard Component
 * Real-time progress monitoring and statistics display for bundle execution
 */

import React, { useEffect, useState } from 'react';
import type { ExecutionStatus, ExecutionProgress, ExecutionStatistics } from '../../store/execution';
import type { GasTracker } from '../../store/transactions';
import type { ExecutionPlan } from '../../services/execution-engine';
import { gasManager } from '../../services/gas-manager';
import { stealthManager } from '../../services/stealth-manager';
import { useSessionStore } from '../../store/session';
import { useConfigStore } from '../../store/config';
import { useWalletStore } from '../../store/wallets';

interface StatusDashboardProps {
  status: ExecutionStatus;
  progress: ExecutionProgress;
  statistics: ExecutionStatistics;
  gasTracker: GasTracker;
  executionPlan: ExecutionPlan | null;
}

export const StatusDashboard: React.FC<StatusDashboardProps> = ({
  status,
  progress,
  statistics,
  gasTracker,
  executionPlan,
}) => {
  // Session store selectors
  const isUnlocked = useSessionStore(state => state.isUnlocked);
  const sessionId = useSessionStore(state => state.sessionId);
  const isSessionValid = useSessionStore(state => state.isSessionValid);
  const expiresAt = useSessionStore(state => state.expiresAt);
  const getTimeUntilExpiry = useSessionStore(state => state.getTimeUntilExpiry);

  // Config store selectors
  const isValidConfig = useConfigStore(state => state.isValidConfig);
  const validationErrors = useConfigStore(state => state.validationErrors);
  const currentConfig = useConfigStore(state => state.currentConfig);
  const validateConfig = useConfigStore(state => state.validateConfig);

  // Wallet store selectors
  const selectedWallets = useWalletStore(state => state.selectedWallets);

  const [networkConditions, setNetworkConditions] = useState(gasManager.getNetworkConditions());
  const [stealthMetrics, setStealthMetrics] = useState(stealthManager.analyzeStealthEffectiveness());
  const [realTimeData, setRealTimeData] = useState({
    currentTime: new Date(),
    executionDuration: 0,
    estimatedCompletion: null as Date | null,
    transactionsPerMinute: 0,
  });

  // Update real-time data
  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      setRealTimeData(prev => {
        const duration = progress.startTime ? now.getTime() - new Date(progress.startTime).getTime() : 0;
        const completedTransactions = statistics.successfulTransactions + statistics.failedTransactions;
        const transactionsPerMinute = duration > 0 ? (completedTransactions / (duration / 60000)) : 0;

        let estimatedCompletion: Date | null = null;
        if (progress.totalTransactions && progress.overallProgress && progress.overallProgress > 0) {
          const remainingProgress = 100 - progress.overallProgress;
          const estimatedRemainingTime = (duration / progress.overallProgress) * remainingProgress;
          estimatedCompletion = new Date(now.getTime() + estimatedRemainingTime);
        }

        return {
          currentTime: now,
          executionDuration: duration,
          estimatedCompletion,
          transactionsPerMinute,
        };
      });

      // Update network conditions
      setNetworkConditions(gasManager.getNetworkConditions());
      setStealthMetrics(stealthManager.analyzeStealthEffectiveness());
    }, 2000);

    return () => clearInterval(interval);
  }, [progress, statistics]);

  const formatDuration = (ms: number): string => {
    const totalSeconds = Math.floor(ms / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    if (hours > 0) {
      return `${hours}h ${minutes}m ${seconds}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const formatTime = (date: Date): string => {
    return date.toLocaleTimeString('en-US', { 
      hour12: false,
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const getProgressColor = (percentage: number): string => {
    if (percentage >= 90) return '#4caf50'; // Green
    if (percentage >= 70) return '#8bc34a'; // Light green
    if (percentage >= 50) return '#ffc107'; // Yellow
    if (percentage >= 30) return '#ff9800'; // Orange
    return '#f44336'; // Red
  };

  const getCongestionColor = (congestion: string): string => {
    switch (congestion) {
      case 'low': return '#4caf50';
      case 'medium': return '#ff9800';
      case 'high': return '#f44336';
      default: return '#9e9e9e';
    }
  };

  const getSecurityStatus = (): { status: 'Unlocked' | 'Locked'; reason: string; color: string } => {
    if (!sessionId) {
      return { status: 'Locked', reason: 'No session created', color: '#f44336' };
    }

    if (!isUnlocked) {
      return { status: 'Locked', reason: 'Not authenticated', color: '#f44336' };
    }

    if (!isSessionValid()) {
      return { status: 'Locked', reason: 'Session expired', color: '#f44336' };
    }

    return { status: 'Unlocked', reason: 'Authenticated and active', color: '#4caf50' };
  };

  const getTimeUntilExpiryText = (): string => {
    if (!expiresAt || !isUnlocked) return 'N/A';

    const timeLeft = getTimeUntilExpiry();
    if (timeLeft <= 0) return 'Expired';

    const minutes = Math.floor(timeLeft / 60000);
    const seconds = Math.floor((timeLeft % 60000) / 1000);

    if (minutes > 0) {
      return `${minutes}m ${seconds}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getConfigStatus = (): { status: 'Valid' | 'Invalid'; reason: string; color: string; errors: string[] } => {
    // Check wallet selection
    if (selectedWallets.length === 0) {
      return { 
        status: 'Invalid', 
        reason: 'No wallets selected', 
        color: '#f44336',
        errors: ['At least one wallet must be selected for execution']
      };
    }

    // Check basic config presence
    if (!currentConfig) {
      return { 
        status: 'Invalid', 
        reason: 'No configuration found', 
        color: '#f44336',
        errors: ['Bundle configuration is required']
      };
    }

    // Use store validation state
    if (isValidConfig) {
      return { 
        status: 'Valid', 
        reason: 'Configuration is complete and valid', 
        color: '#4caf50',
        errors: []
      };
    }

    // Show specific validation errors if available
    if (validationErrors.length > 0) {
      const primaryError = validationErrors[0];
      return { 
        status: 'Invalid', 
        reason: primaryError, 
        color: '#f44336',
        errors: validationErrors
      };
    }

    // Fallback to manual validation
    const errors = validateConfig();
    if (errors.length === 0) {
      return { 
        status: 'Valid', 
        reason: 'Configuration is complete and valid', 
        color: '#4caf50',
        errors: []
      };
    }

    return { 
      status: 'Invalid', 
      reason: errors[0] || 'Configuration incomplete', 
      color: '#f44336',
      errors
    };
  };

  return (
    <div className="status-dashboard">
      {/* Overview Cards */}
      <div className="overview-cards">
        <div className="overview-card progress-card">
          <div className="card-header">
            <h4>Overall Progress</h4>
            <span className="card-value">{progress.overallProgress?.toFixed(1) || 0}%</span>
          </div>
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ 
                width: `${progress.overallProgress || 0}%`,
                backgroundColor: getProgressColor(progress.overallProgress || 0)
              }}
            ></div>
          </div>
          <div className="card-details">
            <span>{progress.completedTransactions || 0} of {progress.totalTransactions || 0} transactions</span>
          </div>
        </div>

        <div className="overview-card stats-card">
          <div className="card-header">
            <h4>Success Rate</h4>
            <span className="card-value">{statistics.successRate?.toFixed(1) || 0}%</span>
          </div>
          <div className="stats-breakdown">
            <div className="stat-item success">
              <span className="stat-label">Successful</span>
              <span className="stat-value">{statistics.successfulTransactions || 0}</span>
            </div>
            <div className="stat-item failed">
              <span className="stat-label">Failed</span>
              <span className="stat-value">{statistics.failedTransactions || 0}</span>
            </div>
            <div className="stat-item pending">
              <span className="stat-label">Pending</span>
              <span className="stat-value">{statistics.pendingTransactions || 0}</span>
            </div>
          </div>
        </div>

        <div className="overview-card timing-card">
          <div className="card-header">
            <h4>Timing</h4>
            <span className="card-value">{formatDuration(realTimeData.executionDuration)}</span>
          </div>
          <div className="timing-details">
            <div className="timing-item">
              <span className="timing-label">Started:</span>
              <span className="timing-value">
                {progress.startTime ? formatTime(new Date(progress.startTime)) : 'Not started'}
              </span>
            </div>
            <div className="timing-item">
              <span className="timing-label">ETA:</span>
              <span className="timing-value">
                {realTimeData.estimatedCompletion ? formatTime(realTimeData.estimatedCompletion) : 'Calculating...'}
              </span>
            </div>
          </div>
        </div>

        <div className="overview-card rate-card">
          <div className="card-header">
            <h4>Transaction Rate</h4>
            <span className="card-value">{realTimeData.transactionsPerMinute.toFixed(1)}/min</span>
          </div>
          <div className="rate-details">
            <div className="rate-item">
              <span className="rate-label">Current Batch:</span>
              <span className="rate-value">{progress.currentBatch || 0}/{progress.totalBatches || 0}</span>
            </div>
            <div className="rate-item">
              <span className="rate-label">Phase:</span>
              <span className="rate-value">{progress.currentPhase || 'idle'}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Configuration Status */}
      <div className="config-status">
        <h3>Configuration Status</h3>
        <div className="config-grid">
          <div className="config-item">
            <div className="config-header">
              <span className="config-label">Config</span>
              <span 
                className="config-value"
                style={{ 
                  color: getConfigStatus().color,
                  fontWeight: 'bold'
                }}
              >
                {getConfigStatus().status === 'Valid' ? '‚úÖ' : '‚ùå'} {getConfigStatus().status}
              </span>
            </div>
            <div className="config-details">
              <span>{getConfigStatus().reason}</span>
              {getConfigStatus().errors.length > 1 && (
                <span> ‚Ä¢ {getConfigStatus().errors.length - 1} more issue{getConfigStatus().errors.length > 2 ? 's' : ''}</span>
              )}
            </div>
          </div>

          <div className="config-item">
            <div className="config-header">
              <span className="config-label">Wallets Selected</span>
              <span className="config-value">
                {selectedWallets.length}
              </span>
            </div>
            <div className="config-details">
              <span>{selectedWallets.length > 0 ? 'Ready for execution' : 'No wallets selected'}</span>
            </div>
          </div>

          <div className="config-item">
            <div className="config-header">
              <span className="config-label">Token Config</span>
              <span className="config-value">
                {currentConfig?.token?.address ? '‚úÖ' : '‚ùå'}
              </span>
            </div>
            <div className="config-details">
              <span>
                {currentConfig?.token?.address 
                  ? `${currentConfig.token.symbol || 'Token'} configured`
                  : 'Token not configured'
                }
              </span>
            </div>
          </div>

          <div className="config-item">
            <div className="config-header">
              <span className="config-label">Purchase Amount</span>
              <span className="config-value">
                {currentConfig?.purchaseAmount?.totalBnb ? `${currentConfig.purchaseAmount.totalBnb} BNB` : '‚ùå'}
              </span>
            </div>
            <div className="config-details">
              <span>
                {currentConfig?.purchaseAmount?.totalBnb 
                  ? 'Amount configured'
                  : 'Amount not set'
                }
              </span>
            </div>
          </div>

          {getConfigStatus().errors.length > 0 && (
            <div className="config-item validation-errors">
              <div className="config-header">
                <span className="config-label">Validation Issues</span>
                <span className="config-value error-count">
                  {getConfigStatus().errors.length}
                </span>
              </div>
              <div className="config-details">
                <div className="error-list">
                  {getConfigStatus().errors.slice(0, 3).map((error, index) => (
                    <div key={index} className="error-item">‚Ä¢ {error}</div>
                  ))}
                  {getConfigStatus().errors.length > 3 && (
                    <div className="error-item">‚Ä¢ And {getConfigStatus().errors.length - 3} more...</div>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Network Status */}
      <div className="network-status">
        <h3>Network Status</h3>
        <div className="network-grid">
          <div className="network-item">
            <div className="network-header">
              <span className="network-label">Network Congestion</span>
              <span 
                className="network-value"
                style={{ color: getCongestionColor(networkConditions.congestion) }}
              >
                {networkConditions.congestion.toUpperCase()}
              </span>
            </div>
            <div className="network-details">
              <span>Block Time: ~{(networkConditions.averageBlockTime / 1000).toFixed(1)}s</span>
            </div>
          </div>

          <div className="network-item">
            <div className="network-header">
              <span className="network-label">Gas Prices</span>
              <span className="network-value">
                {(parseInt(networkConditions.gasPrice.standard) / 1e9).toFixed(1)} gwei
              </span>
            </div>
            <div className="gas-price-range">
              <div className="gas-item">
                <span>Slow: {(parseInt(networkConditions.gasPrice.slow) / 1e9).toFixed(1)}</span>
              </div>
              <div className="gas-item">
                <span>Fast: {(parseInt(networkConditions.gasPrice.fast) / 1e9).toFixed(1)}</span>
              </div>
              <div className="gas-item">
                <span>Instant: {(parseInt(networkConditions.gasPrice.instant) / 1e9).toFixed(1)}</span>
              </div>
            </div>
          </div>

          <div className="network-item">
            <div className="network-header">
              <span className="network-label">Gas Usage</span>
              <span className="network-value">
                {(parseFloat(statistics.totalGasUsed || '0') / 1e18).toFixed(6)} BNB
              </span>
            </div>
            <div className="network-details">
              <span>Avg: {statistics.averageGasUsed || '0'} gas/tx</span>
            </div>
          </div>

          <div className="network-item">
            <div className="network-header">
              <span className="network-label">Confirmations</span>
              <span className="network-value">
                ~{gasTracker.estimatedConfirmationTime || 0}s
              </span>
            </div>
            <div className="network-details">
              <span>Network recommended</span>
            </div>
          </div>
        </div>
      </div>

      {/* Execution Plan Status */}
      {executionPlan && (
        <div className="execution-plan-status">
          <h3>Execution Plan</h3>
          <div className="plan-grid">
            <div className="plan-item">
              <span className="plan-label">Plan ID:</span>
              <span className="plan-value">{executionPlan.id}</span>
            </div>
            <div className="plan-item">
              <span className="plan-label">Total Transactions:</span>
              <span className="plan-value">{executionPlan.totalTransactions}</span>
            </div>
            <div className="plan-item">
              <span className="plan-label">Wallets:</span>
              <span className="plan-value">{executionPlan.walletIds.length}</span>
            </div>
            <div className="plan-item">
              <span className="plan-label">Total Value:</span>
              <span className="plan-value">{executionPlan.totalValue} BNB</span>
            </div>
            <div className="plan-item">
              <span className="plan-label">Estimated Duration:</span>
              <span className="plan-value">{Math.ceil(executionPlan.estimatedDuration / 60000)} minutes</span>
            </div>
            <div className="plan-item">
              <span className="plan-label">Est. Gas Cost:</span>
              <span className="plan-value">
                {(parseFloat(executionPlan.estimatedGasCost) / 1e18).toFixed(6)} BNB
              </span>
            </div>
          </div>
        </div>
      )}

      {/* Stealth & Security Status */}
      <div className="stealth-status">
        <h3>Stealth & Security</h3>
        <div className="stealth-grid">
          <div className="stealth-item">
            <div className="stealth-header">
              <span className="stealth-label">Security</span>
              <span 
                className="stealth-value" 
                style={{ 
                  color: getSecurityStatus().color,
                  fontWeight: 'bold'
                }}
              >
                {getSecurityStatus().status === 'Unlocked' ? 'üîì' : 'üîí'} {getSecurityStatus().status}
              </span>
            </div>
            <div className="stealth-details">
              <span>{getSecurityStatus().reason}</span>
              {isUnlocked && expiresAt && (
                <span> ‚Ä¢ Expires in {getTimeUntilExpiryText()}</span>
              )}
            </div>
          </div>

          <div className="stealth-item">
            <div className="stealth-header">
              <span className="stealth-label">Detection Risk</span>
              <span className={`stealth-value risk-${stealthMetrics.detectionRisk}`}>
                {stealthMetrics.detectionRisk.toUpperCase()}
              </span>
            </div>
            <div className="stealth-details">
              <span>Avg Delay: {stealthMetrics.averageDelay.toFixed(0)}ms</span>
            </div>
          </div>

          <div className="stealth-item">
            <div className="stealth-header">
              <span className="stealth-label">MEV Protection</span>
              <span className="stealth-value">
                {stealthMetrics.successRate.toFixed(1)}%
              </span>
            </div>
            <div className="stealth-details">
              <span>Evaded: {stealthMetrics.mevEvaded}</span>
            </div>
          </div>

          <div className="stealth-item">
            <div className="stealth-header">
              <span className="stealth-label">Timing Variation</span>
              <span className="stealth-value">
                {stealthMetrics.delayVariation.toFixed(0)}ms
              </span>
            </div>
            <div className="stealth-details">
              <span>Randomization active</span>
            </div>
          </div>

          <div className="stealth-item">
            <div className="stealth-header">
              <span className="stealth-label">Frontrun Attempts</span>
              <span className="stealth-value">
                {stealthMetrics.frontrunningAttempts}
              </span>
            </div>
            <div className="stealth-details">
              <span>Detected & blocked</span>
            </div>
          </div>
        </div>
      </div>

      {/* Current Phase Indicator */}
      <div className="current-phase">
        <h3>Current Phase</h3>
        <div className="phase-indicator">
          <div className="phase-steps">
            <div className={`phase-step ${['idle', 'preparing', 'executing', 'paused', 'stopping', 'completed', 'failed', 'aborted'].indexOf(status) >= 0 ? 'completed' : ''}`}>
              <div className="step-number">1</div>
              <div className="step-label">Initialize</div>
            </div>
            <div className={`phase-step ${['preparing', 'executing', 'paused', 'stopping', 'completed', 'failed', 'aborted'].indexOf(status) >= 1 ? 'completed' : ''} ${status === 'preparing' ? 'active' : ''}`}>
              <div className="step-number">2</div>
              <div className="step-label">Prepare</div>
            </div>
            <div className={`phase-step ${['executing', 'paused', 'stopping', 'completed'].indexOf(status) >= 0 ? 'completed' : ''} ${status === 'executing' ? 'active' : ''}`}>
              <div className="step-number">3</div>
              <div className="step-label">Execute</div>
            </div>
            <div className={`phase-step ${status === 'completed' ? 'completed' : ''} ${status === 'stopping' ? 'active' : ''}`}>
              <div className="step-number">4</div>
              <div className="step-label">Complete</div>
            </div>
          </div>
          <div className="phase-description">
            {progress.currentPhase && (
              <span>Current: {progress.currentPhase} ({progress.phaseProgress?.toFixed(1) || 0}%)</span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default StatusDashboard;

src/components/BundleExecution/Transacti.content:
/**
 * Transaction Queue Component  
 * Queue management and monitoring interface for bundle execution
 */

import React, { useState, useMemo } from 'react';
import type { EnhancedTransaction, TransactionQueue as ITransactionQueue } from '../../store/transactions';
import { TransactionRow } from './TransactionRow';

interface TransactionQueueProps {
  transactions: EnhancedTransaction[];
  queue: ITransactionQueue;
  onRetryTransaction: (txId: string) => void;
  onCancelTransaction: (txId: string) => void;
}

export const TransactionQueue: React.FC<TransactionQueueProps> = ({
  transactions,
  queue,
  onRetryTransaction,
  onCancelTransaction,
}) => {
  const [filter, setFilter] = useState<'all' | 'queued' | 'pending' | 'confirmed' | 'failed'>('all');
  const [sortBy, setSortBy] = useState<'time' | 'status' | 'priority'>('time');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');

  const filteredAndSortedTransactions = useMemo(() => {
    let filtered = transactions;

    // Apply status filter
    if (filter !== 'all') {
      filtered = transactions.filter(tx => tx.status === filter);
    }

    // Apply sorting
    filtered.sort((a, b) => {
      let comparison = 0;

      switch (sortBy) {
        case 'time':
          comparison = new Date(a.queuedAt).getTime() - new Date(b.queuedAt).getTime();
          break;
        case 'status':
          comparison = a.status.localeCompare(b.status);
          break;
        case 'priority':
          const priorityOrder = { critical: 4, high: 3, normal: 2, low: 1 };
          comparison = priorityOrder[a.priority] - priorityOrder[b.priority];
          break;
        default:
          break;
      }

      return sortOrder === 'asc' ? comparison : -comparison;
    });

    return filtered;
  }, [transactions, filter, sortBy, sortOrder]);

  const getQueueStats = () => {
    const stats = {
      total: transactions.length,
      queued: transactions.filter(tx => tx.status === 'queued').length,
      pending: transactions.filter(tx => tx.status === 'pending').length,
      confirmed: transactions.filter(tx => tx.status === 'confirmed').length,
      failed: transactions.filter(tx => tx.status === 'failed').length,
    };
    return stats;
  };

  const stats = getQueueStats();

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'critical': return '#f44336';
      case 'high': return '#ff9800';
      case 'normal': return '#2196f3';
      case 'low': return '#4caf50';
      default: return '#9e9e9e';
    }
  };

  return (
    <div className="transaction-queue">
      {/* Queue Header */}
      <div className="queue-header">
        <div className="queue-stats">
          <div className="stat-item">
            <span className="stat-label">Total</span>
            <span className="stat-value">{stats.total}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Queued</span>
            <span className="stat-value queued">{stats.queued}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Pending</span>
            <span className="stat-value pending">{stats.pending}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Confirmed</span>
            <span className="stat-value confirmed">{stats.confirmed}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Failed</span>
            <span className="stat-value failed">{stats.failed}</span>
          </div>
        </div>

        <div className="queue-controls">
          <div className="control-group">
            <label>Filter:</label>
            <select value={filter} onChange={(e) => setFilter(e.target.value as any)}>
              <option value="all">All Transactions</option>
              <option value="queued">Queued</option>
              <option value="pending">Pending</option>
              <option value="confirmed">Confirmed</option>
              <option value="failed">Failed</option>
            </select>
          </div>

          <div className="control-group">
            <label>Sort by:</label>
            <select value={sortBy} onChange={(e) => setSortBy(e.target.value as any)}>
              <option value="time">Time</option>
              <option value="status">Status</option>
              <option value="priority">Priority</option>
            </select>
          </div>

          <div className="control-group">
            <label>Order:</label>
            <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value as any)}>
              <option value="desc">Newest First</option>
              <option value="asc">Oldest First</option>
            </select>
          </div>
        </div>
      </div>

      {/* Queue Status */}
      {queue.isPaused && (
        <div className="queue-status paused">
          <div className="status-icon">‚è∏Ô∏è</div>
          <div className="status-message">
            <span className="status-title">Queue Paused</span>
            <span className="status-description">Transaction processing is temporarily stopped</span>
          </div>
        </div>
      )}

      {queue.activeTransactions.length > 0 && (
        <div className="queue-status active">
          <div className="status-icon">‚ö°</div>
          <div className="status-message">
            <span className="status-title">Processing {queue.activeTransactions.length} transactions</span>
            <span className="status-description">
              Current position: {queue.currentIndex + 1} of {queue.transactions.length}
            </span>
          </div>
        </div>
      )}

      {/* Transaction List */}
      <div className="transaction-list">
        {filteredAndSortedTransactions.length === 0 ? (
          <div className="empty-queue">
            <div className="empty-icon">üìù</div>
            <div className="empty-message">
              <span className="empty-title">No transactions found</span>
              <span className="empty-description">
                {filter === 'all' ? 'The queue is currently empty' : `No ${filter} transactions`}
              </span>
            </div>
          </div>
        ) : (
          <div className="transaction-rows">
            {filteredAndSortedTransactions.map((transaction, index) => (
              <TransactionRow
                key={transaction.id}
                transaction={transaction}
                index={index}
                isInQueue={queue.transactions.includes(transaction.id)}
                isActive={queue.activeTransactions.includes(transaction.id)}
                queuePosition={queue.transactions.indexOf(transaction.id) + 1}
                onRetry={() => onRetryTransaction(transaction.id)}
                onCancel={() => onCancelTransaction(transaction.id)}
              />
            ))}
          </div>
        )}
      </div>

      {/* Queue Progress */}
      {queue.transactions.length > 0 && (
        <div className="queue-progress">
          <div className="progress-header">
            <span className="progress-label">Queue Progress</span>
            <span className="progress-fraction">
              {queue.currentIndex} / {queue.transactions.length}
            </span>
          </div>
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ 
                width: `${(queue.currentIndex / queue.transactions.length) * 100}%` 
              }}
            ></div>
          </div>
          <div className="progress-details">
            <span>Concurrent Limit: {queue.concurrentLimit}</span>
            <span>Active: {queue.activeTransactions.length}</span>
          </div>
        </div>
      )}
    </div>
  );
};

/**
 * Transaction Row Component
 * Individual transaction status display with detailed information and actions
 */

import React, { useState } from 'react';
import { BSCScanButton } from '../BSCScanButton';
import type { EnhancedTransaction } from '../../store/transactions';

interface TransactionRowProps {
  transaction: EnhancedTransaction;
  index: number;
  isInQueue: boolean;
  isActive: boolean;
  queuePosition: number;
  onRetry: () => void;
  onCancel: () => void;
}

export const TransactionRow: React.FC<TransactionRowProps> = ({
  transaction,
  index,
  isInQueue,
  isActive,
  queuePosition,
  onRetry,
  onCancel,
}) => {
  const [expanded, setExpanded] = useState(false);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'queued': return '#9e9e9e';
      case 'pending': return '#ff9800';
      case 'submitted': return '#2196f3';
      case 'confirming': return '#03a9f4';
      case 'confirmed': return '#4caf50';
      case 'failed': return '#f44336';
      case 'cancelled': return '#607d8b';
      default: return '#9e9e9e';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'critical': return '#f44336';
      case 'high': return '#ff9800';
      case 'normal': return '#2196f3';
      case 'low': return '#4caf50';
      default: return '#9e9e9e';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'buy': return 'üü¢';
      case 'sell': return 'üî¥';
      case 'approve': return '‚úÖ';
      case 'transfer': return '‚ÜîÔ∏è';
      case 'funding': return 'üí∞';
      default: return 'üìù';
    }
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString('en-US', { 
      hour12: false,
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const formatDuration = (startTime: string, endTime?: string) => {
    const start = new Date(startTime).getTime();
    const end = endTime ? new Date(endTime).getTime() : Date.now();
    const duration = end - start;

    if (duration < 1000) return `${duration}ms`;
    if (duration < 60000) return `${Math.round(duration / 1000)}s`;
    return `${Math.round(duration / 60000)}m`;
  };

  const canRetry = transaction.status === 'failed' && transaction.retryCount < transaction.maxRetries;
  const canCancel = ['queued', 'pending'].includes(transaction.status);

  return (
    <div className={`transaction-row ${expanded ? 'expanded' : ''} ${isActive ? 'active' : ''}`}>
      <div className="transaction-main" onClick={() => setExpanded(!expanded)}>
        {/* Queue Position */}
        {isInQueue && (
          <div className="queue-position">
            #{queuePosition}
          </div>
        )}

        {/* Transaction Type Icon */}
        <div className="transaction-icon">
          {getTypeIcon(transaction.type)}
        </div>

        {/* Basic Information */}
        <div className="transaction-basic">
          <div className="transaction-id">
            <span className="id-label">TX:</span>
            <span className="id-value">{transaction.id.substring(0, 8)}...</span>
          </div>
          <div className="transaction-type">
            {transaction.type.toUpperCase()}
          </div>
        </div>

        {/* Status */}
        <div className="transaction-status">
          <div 
            className="status-indicator" 
            style={{ backgroundColor: getStatusColor(transaction.status) }}
          ></div>
          <span className="status-text">{transaction.status}</span>
          {transaction.confirmations > 0 && (
            <span className="confirmations">
              {transaction.confirmations}/{transaction.requiredConfirmations}
            </span>
          )}
        </div>

        {/* Priority */}
        <div className="transaction-priority">
          <div 
            className="priority-dot" 
            style={{ backgroundColor: getPriorityColor(transaction.priority) }}
          ></div>
          <span className="priority-text">{transaction.priority}</span>
        </div>

        {/* Value */}
        <div className="transaction-value">
          {parseFloat(transaction.value).toFixed(4)} BNB
        </div>

        {/* Gas */}
        <div className="transaction-gas">
          {transaction.gasUsedActual || transaction.gasLimit} gas
        </div>

        {/* Timing */}
        <div className="transaction-timing">
          <div className="queued-time">{formatTime(transaction.queuedAt)}</div>
          {transaction.submittedAt && (
            <div className="duration">
              {formatDuration(transaction.queuedAt, transaction.submittedAt)}
            </div>
          )}
        </div>

        {/* Expand Icon */}
        <div className="expand-icon">
          {expanded ? '‚ñº' : '‚ñ∂'}
        </div>
      </div>

      {/* Expanded Details */}
      {expanded && (
        <div className="transaction-details">
          <div className="details-grid">
            {/* Transaction Details */}
            <div className="detail-section">
              <h4>Transaction Details</h4>
              <div className="detail-rows">
                <div className="detail-row">
                  <span className="detail-label">Hash:</span>
                  <span className="detail-value monospace">
                    {transaction.hash ? transaction.hash.substring(0, 20) + '...' : 'Pending'}
                    {transaction.hash && (
                      <BSCScanButton
                        txHash={transaction.hash}
                        type="transaction"
                        variant="icon"
                        className="detail-bscscan-btn"
                      />
                    )}
                  </span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">From:</span>
                  <span className="detail-value monospace">
                    {transaction.from.substring(0, 10)}...{transaction.from.slice(-8)}
                  </span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">To:</span>
                  <span className="detail-value monospace">
                    {transaction.to.substring(0, 10)}...{transaction.to.slice(-8)}
                  </span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Nonce:</span>
                  <span className="detail-value">{transaction.nonce || 'TBD'}</span>
                </div>
              </div>
            </div>

            {/* Gas Details */}
            <div className="detail-section">
              <h4>Gas Information</h4>
              <div className="detail-rows">
                <div className="detail-row">
                  <span className="detail-label">Gas Limit:</span>
                  <span className="detail-value">{transaction.gasLimit}</span>
                </div>
                {transaction.maxFeePerGas && (
                  <div className="detail-row">
                    <span className="detail-label">Max Fee:</span>
                    <span className="detail-value">{transaction.maxFeePerGas} gwei</span>
                  </div>
                )}
                {transaction.effectiveGasPrice && (
                  <div className="detail-row">
                    <span className="detail-label">Effective Price:</span>
                    <span className="detail-value">{transaction.effectiveGasPrice} gwei</span>
                  </div>
                )}
                {transaction.gasUsedActual && (
                  <div className="detail-row">
                    <span className="detail-label">Gas Used:</span>
                    <span className="detail-value">{transaction.gasUsedActual}</span>
                  </div>
                )}
              </div>
            </div>

            {/* Timing Details */}
            <div className="detail-section">
              <h4>Timing Information</h4>
              <div className="detail-rows">
                <div className="detail-row">
                  <span className="detail-label">Queued:</span>
                  <span className="detail-value">{formatTime(transaction.queuedAt)}</span>
                </div>
                {transaction.submittedAt && (
                  <div className="detail-row">
                    <span className="detail-label">Submitted:</span>
                    <span className="detail-value">{formatTime(transaction.submittedAt)}</span>
                  </div>
                )}
                {transaction.confirmedAt && (
                  <div className="detail-row">
                    <span className="detail-label">Confirmed:</span>
                    <span className="detail-value">{formatTime(transaction.confirmedAt)}</span>
                  </div>
                )}
                {transaction.staggerDelay && (
                  <div className="detail-row">
                    <span className="detail-label">Stagger Delay:</span>
                    <span className="detail-value">{transaction.staggerDelay}ms</span>
                  </div>
                )}
              </div>
            </div>

            {/* Error Information */}
            {transaction.error && (
              <div className="detail-section error-section">
                <h4>Error Information</h4>
                <div className="error-message">
                  {transaction.error}
                </div>
                <div className="retry-info">
                  Retry {transaction.retryCount} of {transaction.maxRetries}
                </div>
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="transaction-actions">
            {canRetry && (
              <button onClick={onRetry} className="action-button retry-button">
                üîÑ Retry Transaction
              </button>
            )}
            {canCancel && (
              <button onClick={onCancel} className="action-button cancel-button">
                ‚ùå Cancel Transaction
              </button>
            )}
            {transaction.hash && (
              <BSCScanButton
                txHash={transaction.hash}
                type="transaction"
                variant="text"
                customText="üîç View on BSCScan"
                className="action-button explorer-button"
              />
            )}
          </div>
        </div>
      )}
    </div>
  );
};

src/components/NetworkSwitcher.css.content:
/**
 * NetworkSwitcher Component Styles
 * Comprehensive styling for network switching interface
 */

.network-switcher {
  display: flex;
  flex-direction: column;
  gap: 16px;
  width: 100%;
  max-width: 600px;
}

.network-switcher.compact {
  flex-direction: row;
  align-items: center;
  gap: 12px;
  max-width: none;
}

/* Network Status Card */
.network-status-card {
  background: var(--bg-secondary, #1a1a1a);
  border: 2px solid var(--border-color, #333);
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3);
  transition: all 0.3s ease;
  color: var(--text-primary, #fff);
}

.network-status-card.testnet {
  border-color: var(--accent-green, #00ff88);
  background: linear-gradient(135deg, rgba(0, 255, 136, 0.1) 0%, var(--bg-secondary, #1a1a1a) 100%);
}

.network-status-card.mainnet {
  border-color: var(--accent-red, #ff4444);
  background: linear-gradient(135deg, rgba(255, 68, 68, 0.1) 0%, var(--bg-secondary, #1a1a1a) 100%);
}

/* Network Header */
.network-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
}

.network-info {
  display: flex;
  align-items: center;
  gap: 12px;
}

.network-indicator {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  animation: pulse 2s infinite;
}

.network-indicator.connected {
  background-color: #22c55e;
}

.network-indicator.connecting {
  background-color: #f59e0b;
  animation: pulse 1s infinite;
}

.network-indicator.disconnected {
  background-color: #ef4444;
}

.network-indicator.error {
  background-color: #ef4444;
  animation: none;
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.7;
    transform: scale(1.1);
  }
}

.network-details {
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.network-name {
  font-size: 18px;
  font-weight: 600;
  margin: 0;
  color: var(--text-primary, #fff);
}

.network-type {
  font-size: 14px;
  color: var(--text-secondary, #888);
}

.network-actions {
  display: flex;
  gap: 8px;
}

.refresh-btn {
  background: var(--bg-tertiary, #2a2a2a);
  border: 1px solid var(--border-color, #333);
  border-radius: 6px;
  padding: 8px 12px;
  cursor: pointer;
  font-size: 14px;
  color: var(--text-primary, #fff);
  transition: all 0.2s ease;
}

.refresh-btn:hover {
  background-color: #3a3a3a;
  border-color: #444;
}

.refresh-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Network Warning */
.network-warning {
  padding: 12px 16px;
  border-radius: 8px;
  margin-bottom: 16px;
  font-weight: 500;
  text-align: center;
}

.network-warning.mainnet {
  background-color: #fef2f2;
  border: 1px solid #fecaca;
  color: #dc2626;
}

/* Connection Status */
.connection-status {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 16px;
  background-color: var(--status-background, #f9fafb);
  border-radius: 8px;
  margin-bottom: 16px;
}

.status-indicator {
  font-weight: 500;
}

.status-indicator.connected {
  color: #059669;
}

.status-indicator.connecting {
  color: #d97706;
}

.status-indicator.disconnected,
.status-indicator.error {
  color: #dc2626;
}

.last-update {
  font-size: 12px;
  color: var(--text-secondary, #6b7280);
}

/* Network Statistics */
.network-stats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 12px;
  margin-bottom: 20px;
}

.stat-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 8px 12px;
  background-color: var(--stat-background, #f9fafb);
  border-radius: 6px;
}

.stat-label {
  font-size: 14px;
  color: var(--text-secondary, #6b7280);
  font-weight: 500;
}

.stat-value {
  font-size: 14px;
  color: var(--text-primary, #1f2937);
  font-weight: 600;
}

.explorer-link {
  color: var(--primary-color, #3b82f6);
  text-decoration: none;
  font-weight: 500;
  transition: color 0.2s ease;
}

.explorer-link:hover {
  color: var(--primary-hover, #2563eb);
  text-decoration: underline;
}

/* Network Switcher Controls */
.network-switcher-controls {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.switch-label {
  font-size: 16px;
  font-weight: 600;
  color: var(--text-primary, #1f2937);
}

.network-options {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.network-option {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 16px;
  border: 2px solid var(--border-color, #e5e7eb);
  border-radius: 8px;
  background: var(--card-background, #ffffff);
  cursor: pointer;
  transition: all 0.2s ease;
  position: relative;
}

.network-option:hover:not(:disabled) {
  border-color: var(--border-hover, #d1d5db);
  background-color: var(--hover-background, #f9fafb);
}

.network-option:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.network-option.active {
  border-width: 3px;
  font-weight: 600;
}

.network-option.testnet.active {
  border-color: #22c55e;
  background-color: #f0fdf4;
}

.network-option.mainnet.active {
  border-color: #ef4444;
  background-color: #fef2f2;
}

.network-option-indicator {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  flex-shrink: 0;
}

.network-option-details {
  display: flex;
  flex-direction: column;
  gap: 2px;
  flex: 1;
}

.network-option-name {
  font-size: 16px;
  font-weight: 500;
  color: var(--text-primary, #1f2937);
}

.network-option-type {
  font-size: 12px;
  color: var(--text-secondary, #6b7280);
}

.mainnet-badge {
  background-color: #fef2f2;
  color: #dc2626;
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 10px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* Compact Mode Styles */
.network-switcher.compact .network-indicator {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 12px;
  border-radius: 6px;
  background-color: var(--card-background, #ffffff);
  border: 1px solid var(--border-color, #e5e7eb);
}

.network-switcher.compact .network-status {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  flex-shrink: 0;
}

.network-switcher.compact .network-name {
  font-size: 14px;
  font-weight: 500;
  margin: 0;
}

.network-switcher.compact .mainnet-warning {
  color: #dc2626;
  font-size: 12px;
}

.network-select {
  padding: 8px 12px;
  border: 1px solid var(--border-color, #333);
  border-radius: 6px;
  background-color: var(--bg-tertiary, #2a2a2a);
  color: var(--text-primary, #fff);
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;
  min-height: 36px;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  background-image: url('data:image/svg+xml;charset=US-ASCII,<svg xmlns="http://www.w3.org/2000/svg" width="4" height="5" viewBox="0 0 4 5"><path fill="%23fff" d="m0 1 2 2 2-2z"/></svg>');
  background-repeat: no-repeat;
  background-position: right 8px center;
  background-size: 12px;
  padding-right: 32px;
}

.network-select:focus {
  outline: none;
  border-color: var(--primary-color, #3b82f6);
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.network-select:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Mainnet Warning Content */
.mainnet-warning-content {
  text-align: left;
}

.mainnet-warning-content p {
  margin: 8px 0;
}

.warning-details {
  background-color: #fef2f2;
  border: 1px solid #fecaca;
  border-radius: 6px;
  padding: 12px;
  margin: 12px 0;
}

.warning-details p {
  margin: 4px 0;
  font-size: 14px;
}

.confirmation-text {
  font-weight: 600;
  margin: 16px 0 !important;
}

.network-switch-details {
  background-color: #f9fafb;
  border-radius: 6px;
  padding: 12px;
  margin: 12px 0;
}

.from-network,
.to-network {
  font-weight: 600;
}

.from-network {
  color: #059669;
}

.to-network {
  color: #dc2626;
}

/* Responsive Design */
@media (max-width: 768px) {
  .network-status-card {
    padding: 16px;
  }

  .network-header {
    flex-direction: column;
    align-items: flex-start;
    gap: 12px;
  }

  .network-stats {
    grid-template-columns: 1fr;
  }

  .network-option {
    padding: 12px;
    min-height: 48px; /* Better touch targets */
  }

  .network-option-name {
    font-size: 14px;
  }

  /* Enhanced compact mode for mobile */
  .network-switcher.compact {
    width: 100%;
    max-width: 180px;
  }

  .network-switcher.compact .network-select {
    width: 100%;
    padding: 10px 12px;
    font-size: 13px;
    min-height: 40px; /* Better touch target */
  }

  .network-switcher.compact .network-indicator {
    padding: 10px 12px;
    min-height: 40px;
    align-items: center;
  }

  .network-switcher.compact .network-name {
    font-size: 13px;
  }
}

/* Additional mobile breakpoint for very small screens */
@media (max-width: 480px) {
  .network-status-card {
    padding: 12px;
    margin: 8px 0;
  }

  .network-header {
    gap: 8px;
  }

  .network-name {
    font-size: 16px;
  }

  .network-stats {
    gap: 8px;
  }

  .stat-item {
    padding: 6px 10px;
  }

  .network-option {
    padding: 10px;
    min-height: 44px;
  }

  .network-option-name {
    font-size: 13px;
  }

  .network-option-type {
    font-size: 11px;
  }

  /* Compact mode optimizations for very small screens */
  .network-switcher.compact {
    max-width: 160px;
  }

  .network-switcher.compact .network-select {
    padding: 8px 10px;
    font-size: 12px;
    min-height: 36px;
  }

  .network-switcher.compact .network-indicator {
    padding: 8px 10px;
    min-height: 36px;
  }

  .network-switcher.compact .network-name {
    font-size: 12px;
  }

  .refresh-btn {
    padding: 6px 10px;
    font-size: 12px;
    min-height: 32px;
  }
}

/* Dark Theme Support */
@media (prefers-color-scheme: dark) {
  .network-status-card {
    background: var(--dark-card-background, #1f2937);
    border-color: var(--dark-border-color, #374151);
  }

  .network-status-card.testnet {
    background: linear-gradient(135deg, rgba(34, 197, 94, 0.1) 0%, var(--dark-card-background, #1f2937) 100%);
  }

  .network-status-card.mainnet {
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.1) 0%, var(--dark-card-background, #1f2937) 100%);
  }

  .network-name {
    color: var(--dark-text-primary, #f9fafb);
  }

  .network-type {
    color: var(--dark-text-secondary, #d1d5db);
  }
}

src/components/NetworkSwitcher.tsx.content:
/**
 * NetworkSwitcher Component
 * Prominent UI toggle for switching between BSC testnet and mainnet
 * Includes safety warnings and visual indicators
 */

import React, { useState, useEffect } from 'react';
import { useNetworkStore } from '../store/network';
import { ConfirmDialog } from './Dialogs/ConfirmDialog';
import type { NetworkConfig } from '../types';
import './NetworkSwitcher.css';

interface NetworkSwitcherProps {
  compact?: boolean;
  showDetails?: boolean;
  className?: string;
}

export const NetworkSwitcher: React.FC<NetworkSwitcherProps> = ({
  compact = false,
  showDetails = true,
  className = '',
}) => {
  // Store state
  const {
    currentNetwork,
    availableNetworks,
    isConnected,
    isConnecting,
    blockNumber,
    gasPrice,
    error,
    switchNetwork,
    updateNetworkStats,
    isMainnet,
    validateNetworkSwitch,
  } = useNetworkStore();

  // Local state
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [pendingNetwork, setPendingNetwork] = useState<NetworkConfig | null>(null);
  const [lastUpdateTime, setLastUpdateTime] = useState<Date | null>(null);

  // Update last update time when network stats change
  useEffect(() => {
    if (blockNumber) {
      setLastUpdateTime(new Date());
    }
  }, [blockNumber, gasPrice]);

  // Handle network switch with confirmation for mainnet
  const handleNetworkSwitch = (networkId: string) => {
    const targetNetwork = availableNetworks.find(n => n.id === networkId);
    if (!targetNetwork || targetNetwork.id === currentNetwork.id) {
      return;
    }

    // Validate the switch and check for warnings
    const warnings = validateNetworkSwitch(networkId);
    const hasMainnetWarnings = warnings.some(w => w.includes('MAINNET') || w.includes('DANGER'));

    if (hasMainnetWarnings) {
      // Show confirmation dialog for mainnet switches
      setPendingNetwork(targetNetwork);
      setShowConfirmDialog(true);
    } else {
      // Direct switch for safe operations
      performNetworkSwitch(targetNetwork);
    }
  };

  // Perform the actual network switch
  const performNetworkSwitch = async (network: NetworkConfig) => {
    try {
      await switchNetwork(network.id);
      console.log(`Successfully switched to ${network.displayName}`);
    } catch (error) {
      console.error('Failed to switch network:', error);
      alert(`Failed to switch to ${network.displayName}: ${error instanceof Error ? error.message : 'Unknown error'}`);
    } finally {
      setShowConfirmDialog(false);
      setPendingNetwork(null);
    }
  };

  // Handle confirmation dialog
  const handleConfirmSwitch = () => {
    if (pendingNetwork) {
      performNetworkSwitch(pendingNetwork);
    }
  };

  const handleCancelSwitch = () => {
    setShowConfirmDialog(false);
    setPendingNetwork(null);
  };

  // Refresh network status
  const handleRefresh = async () => {
    try {
      await updateNetworkStats();
    } catch (error) {
      console.error('Failed to refresh network status:', error);
    }
  };

  // Format gas price for display
  const formatGasPrice = (gasPrice: string): string => {
    try {
      const gwei = parseFloat(gasPrice) / 1e9;
      return `${gwei.toFixed(1)} gwei`;
    } catch {
      return 'Unknown';
    }
  };

  // Get status indicator class
  const getStatusClass = (): string => {
    if (isConnecting) return 'connecting';
    if (!isConnected) return 'disconnected';
    if (error) return 'error';
    return 'connected';
  };

  // Get network warning message
  const getNetworkWarning = (): string | null => {
    if (isMainnet()) {
      return '‚ö†Ô∏è MAINNET ACTIVE - Real funds at risk!';
    }
    return null;
  };

  if (compact) {
    return (
      <div className={`network-switcher compact ${className}`}>
        <div className={`network-indicator ${currentNetwork.type}`}>
          <div 
            className={`network-status ${getStatusClass()}`}
            style={{ backgroundColor: currentNetwork.iconColor }}
          />
          {isMainnet() && <span className="mainnet-warning">‚ö†Ô∏è</span>}
        </div>

        <select
          value={currentNetwork.id}
          onChange={(e) => handleNetworkSwitch(e.target.value)}
          disabled={isConnecting}
          className="network-select"
        >
          {availableNetworks.map((network) => (
            <option key={network.id} value={network.id}>
              {network.displayName}
            </option>
          ))}
        </select>
      </div>
    );
  }

  return (
    <div className={`network-switcher ${className}`}>
      {/* Main Network Status Card */}
      <div className={`network-status-card ${currentNetwork.type}`}>
        {/* Header */}
        <div className="network-header">
          <div className="network-info">
            <div 
              className={`network-indicator ${getStatusClass()}`}
              style={{ backgroundColor: currentNetwork.iconColor }}
            />
            <div className="network-details">
              <h3 className="network-name">{currentNetwork.displayName}</h3>
              <span className="network-type">Chain ID: {currentNetwork.chainId}</span>
            </div>
          </div>

          <div className="network-actions">
            <button
              onClick={handleRefresh}
              disabled={isConnecting}
              className="refresh-btn"
              title="Refresh network status"
            >
              üîÑ
            </button>
          </div>
        </div>

        {/* Network Warning */}
        {getNetworkWarning() && (
          <div className="network-warning mainnet">
            {getNetworkWarning()}
          </div>
        )}

        {/* Connection Status */}
        <div className="connection-status">
          <div className={`status-indicator ${getStatusClass()}`}>
            {isConnecting && 'Connecting...'}
            {!isConnecting && isConnected && 'Connected'}
            {!isConnecting && !isConnected && 'Disconnected'}
            {error && `Error: ${error}`}
          </div>

          {lastUpdateTime && isConnected && (
            <div className="last-update">
              Last updated: {lastUpdateTime.toLocaleTimeString()}
            </div>
          )}
        </div>

        {/* Network Statistics */}
        {showDetails && isConnected && (
          <div className="network-stats">
            <div className="stat-item">
              <span className="stat-label">Block Number:</span>
              <span className="stat-value">{blockNumber?.toLocaleString() || 'Loading...'}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">Gas Price:</span>
              <span className="stat-value">
                {gasPrice ? formatGasPrice(gasPrice) : 'Loading...'}
              </span>
            </div>
            <div className="stat-item">
              <span className="stat-label">Explorer:</span>
              <a 
                href={currentNetwork.blockExplorerUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="explorer-link"
              >
                View on {currentNetwork.blockExplorerUrl.includes('testnet') ? 'TestNet' : ''} BSCScan
              </a>
            </div>
          </div>
        )}

        {/* Network Switcher */}
        <div className="network-switcher-controls">
          <label className="switch-label">Switch Network:</label>
          <div className="network-options">
            {availableNetworks.map((network) => (
              <button
                key={network.id}
                onClick={() => handleNetworkSwitch(network.id)}
                disabled={isConnecting || network.id === currentNetwork.id}
                className={`network-option ${network.type} ${
                  network.id === currentNetwork.id ? 'active' : ''
                }`}
                style={{
                  borderColor: network.iconColor,
                  ...(network.id === currentNetwork.id && {
                    backgroundColor: network.iconColor + '20',
                  })
                }}
              >
                <div 
                  className="network-option-indicator"
                  style={{ backgroundColor: network.iconColor }}
                />
                <div className="network-option-details">
                  <span className="network-option-name">{network.displayName}</span>
                  <span className="network-option-type">
                    {network.isTestnet ? 'Testnet' : 'Mainnet'} ‚Ä¢ Chain {network.chainId}
                  </span>
                </div>
                {network.type === 'mainnet' && (
                  <span className="mainnet-badge">‚ö†Ô∏è REAL FUNDS</span>
                )}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Confirmation Dialog for Mainnet Switch */}
      {showConfirmDialog && pendingNetwork && (
        <ConfirmDialog
          isOpen={showConfirmDialog}
          title="‚ö†Ô∏è CRITICAL WARNING: Switch to Mainnet"
          message={`You are about to switch to BSC MAINNET where REAL FUNDS are at risk! This will use real BNB for all transactions. Are you absolutely sure you want to proceed with switching to ${pendingNetwork.displayName}?`}
          onConfirm={handleConfirmSwitch}
          onCancel={handleCancelSwitch}
          isDangerous={true}
          confirmText="YES, SWITCH TO MAINNET"
          cancelText="Cancel (Recommended)"
        >
          <div className="mainnet-warning-content">
            <div className="warning-details">
              <p>üî¥ <strong>REAL BNB will be used for all transactions</strong></p>
              <p>üî¥ <strong>All wallet operations will affect real balances</strong></p>
              <p>üî¥ <strong>Failed transactions will consume real gas fees</strong></p>
              <p>üî¥ <strong>This is NOT a test environment</strong></p>
            </div>
            <div className="network-switch-details">
              <p>From: <span className="from-network">{currentNetwork.displayName}</span></p>
              <p>To: <span className="to-network">{pendingNetwork.displayName}</span></p>
            </div>
          </div>
        </ConfirmDialog>
      )}
    </div>
  );
};

export default NetworkSwitcher;

src/services/bsc-rpc.ts.content:
/**
 * BSC RPC Client Service
 * Handles real blockchain interactions with BSC testnet and mainnet
 * Replaces mock server calls with actual RPC calls
 */

import { JsonRpcProvider, formatEther, parseEther, Wallet, parseUnits } from 'ethers';
import { config } from '../config/env';
import type { NetworkConfig } from '../types';
import { validatePrivateKey } from '../utils/crypto';

export interface BlockchainStats {
  blockNumber: number;
  gasPrice: string;
  networkVersion: string;
  isConnected: boolean;
  lastUpdate: Date;
}

export interface WalletBalance {
  address: string;
  balance: number; // in BNB
  formattedBalance: string;
  blockNumber: number;
}

export interface GasPriceInfo {
  slow: string;
  standard: string;
  fast: string;
  estimatedConfirmationTime: {
    slow: number;
    standard: number;
    fast: number;
  };
}

export interface TransactionRequest {
  to: string;
  value?: string;
  data?: string;
  gasLimit?: string;
  gasPrice?: string;
  nonce?: number;
}

export interface TransactionResult {
  hash: string;
  from: string;
  to: string;
  value: string;
  gasUsed?: string;
  gasPrice: string;
  status: 'pending' | 'success' | 'failed';
  blockNumber?: number;
  confirmations: number;
  timestamp?: number;
}

class BSCRPCClient {
  private provider: JsonRpcProvider | null = null;
  private currentNetwork: NetworkConfig;
  private connectionRetries = 0;
  private maxRetries = 3;
  private lastGasPriceUpdate = 0;
  private cachedGasPrice: string | null = null;

  constructor() {
    // Initialize with testnet by default
    this.currentNetwork = config.networks['bsc-testnet'];
    this.initializeProvider();
  }

  /**
   * Initialize the RPC provider for the current network
   */
  private initializeProvider(): void {
    try {
      this.provider = new JsonRpcProvider(this.currentNetwork.rpcUrl, {
        name: this.currentNetwork.displayName,
        chainId: this.currentNetwork.chainId,
      });

      // Set up provider event listeners
      this.setupProviderListeners();

      console.log(`BSC RPC Client initialized for ${this.currentNetwork.displayName}`);
    } catch (error) {
      console.error('Failed to initialize BSC RPC provider:', error);
      this.handleProviderError(error);
    }
  }

  /**
   * Set up provider event listeners for monitoring
   */
  private setupProviderListeners(): void {
    if (!this.provider) return;

    this.provider.on('error', (error) => {
      console.error('BSC RPC Provider error:', error);
      this.handleProviderError(error);
    });

    this.provider.on('network', (newNetwork, oldNetwork) => {
      if (oldNetwork) {
        console.log(`BSC network changed from ${oldNetwork.chainId} to ${newNetwork.chainId}`);
      }
    });
  }

  /**
   * Handle provider errors with retry logic
   */
  private async handleProviderError(error: any): Promise<void> {
    console.error('BSC RPC error:', error);

    if (this.connectionRetries < this.maxRetries) {
      this.connectionRetries++;
      console.log(`Retrying BSC connection (attempt ${this.connectionRetries}/${this.maxRetries})`);

      // Try backup RPC URLs if available
      const backupUrls = this.currentNetwork.backupRpcUrls || [];
      if (backupUrls.length > 0 && this.connectionRetries <= backupUrls.length) {
        const backupUrl = backupUrls[this.connectionRetries - 1];
        console.log(`Switching to backup RPC: ${backupUrl}`);

        this.provider = new JsonRpcProvider(backupUrl, {
          name: this.currentNetwork.displayName,
          chainId: this.currentNetwork.chainId,
        });

        this.setupProviderListeners();
      } else {
        // Wait before retrying with the same URL
        await new Promise(resolve => setTimeout(resolve, config.rpc.retryDelay));
        this.initializeProvider();
      }
    } else {
      console.error('Max retries reached for BSC RPC connection');
      throw new Error(`Failed to connect to BSC network after ${this.maxRetries} attempts`);
    }
  }

  /**
   * Switch to a different network
   */
  async switchNetwork(networkId: string): Promise<void> {
    const networks = config.networks as Record<string, NetworkConfig>;
    if (!networks[networkId]) {
      throw new Error(`Unknown network: ${networkId}`);
    }

    const newNetwork = networks[networkId];
    console.log(`Switching BSC network from ${this.currentNetwork.displayName} to ${newNetwork.displayName}`);

    // Clean up existing provider
    if (this.provider) {
      this.provider.removeAllListeners();
    }

    // Update current network and reinitialize
    this.currentNetwork = newNetwork;
    this.connectionRetries = 0;
    this.cachedGasPrice = null;
    this.lastGasPriceUpdate = 0;

    this.initializeProvider();

    // Verify the network switch was successful
    await this.verifyNetworkConnection();
  }

  /**
   * Verify network connection and chain ID
   */
  private async verifyNetworkConnection(): Promise<void> {
    if (!this.provider) {
      throw new Error('No provider available');
    }

    try {
      const network = await this.provider.getNetwork();
      if (Number(network.chainId) !== this.currentNetwork.chainId) {
        throw new Error(
          `Chain ID mismatch: expected ${this.currentNetwork.chainId}, got ${network.chainId}`
        );
      }
      console.log(`Network verification successful: ${this.currentNetwork.displayName} (${network.chainId})`);
    } catch (error) {
      console.error('Network verification failed:', error);
      throw error;
    }
  }

  /**
   * Get current network information
   */
  getCurrentNetwork(): NetworkConfig {
    return this.currentNetwork;
  }

  /**
   * Check if connected to the blockchain
   */
  async isConnected(): Promise<boolean> {
    if (!this.provider) return false;

    try {
      await this.provider.getBlockNumber();
      return true;
    } catch (error) {
      console.error('Connection check failed:', error);
      return false;
    }
  }

  /**
   * Get blockchain statistics
   */
  async getBlockchainStats(): Promise<BlockchainStats> {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    try {
      const [blockNumber, feeData, network] = await Promise.all([
        this.provider.getBlockNumber(),
        this.provider.getFeeData(),
        this.provider.getNetwork(),
      ]);

      return {
        blockNumber,
        gasPrice: feeData.gasPrice?.toString() || '0',
        networkVersion: network.chainId.toString(),
        isConnected: true,
        lastUpdate: new Date(),
      };
    } catch (error) {
      console.error('Failed to get blockchain stats:', error);
      throw error;
    }
  }

  /**
   * Get wallet balance from the blockchain
   */
  async getWalletBalance(address: string): Promise<WalletBalance> {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    try {
      const [balance, blockNumber] = await Promise.all([
        this.provider.getBalance(address),
        this.provider.getBlockNumber(),
      ]);

      const balanceInBNB = parseFloat(formatEther(balance));

      return {
        address,
        balance: balanceInBNB,
        formattedBalance: formatEther(balance),
        blockNumber,
      };
    } catch (error) {
      console.error(`Failed to get balance for ${address}:`, error);
      throw error;
    }
  }

  /**
   * Get multiple wallet balances efficiently
   */
  async getBulkWalletBalances(addresses: string[]): Promise<WalletBalance[]> {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    try {
      console.log(`Fetching balances for ${addresses.length} wallets from ${this.currentNetwork.displayName}`);

      // Batch requests with concurrency limit
      const concurrencyLimit = config.rpc.maxConcurrentRequests;
      const results: WalletBalance[] = [];

      for (let i = 0; i < addresses.length; i += concurrencyLimit) {
        const batch = addresses.slice(i, i + concurrencyLimit);
        const batchPromises = batch.map(address => this.getWalletBalance(address));

        const batchResults = await Promise.allSettled(batchPromises);

        batchResults.forEach((result, index) => {
          if (result.status === 'fulfilled') {
            results.push(result.value);
          } else {
            console.error(`Failed to get balance for ${batch[index]}:`, result.reason);
            // Add zero balance for failed requests
            results.push({
              address: batch[index],
              balance: 0,
              formattedBalance: '0.0',
              blockNumber: 0,
            });
          }
        });

        // Add small delay between batches to avoid rate limiting
        if (i + concurrencyLimit < addresses.length) {
          await new Promise(resolve => setTimeout(resolve, 100));
        }
      }

      console.log(`Successfully fetched ${results.length} wallet balances`);
      return results;
    } catch (error) {
      console.error('Failed to get bulk wallet balances:', error);
      throw error;
    }
  }

  /**
   * Get current gas price information
   */
  async getGasPriceInfo(): Promise<GasPriceInfo> {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    // Use cached gas price if recent
    const now = Date.now();
    if (this.cachedGasPrice && (now - this.lastGasPriceUpdate) < config.gas.gasPriceRefreshInterval) {
      const gasPrice = this.cachedGasPrice;
      return {
        slow: gasPrice,
        standard: (BigInt(gasPrice) * BigInt(120) / BigInt(100)).toString(), // 20% higher
        fast: (BigInt(gasPrice) * BigInt(150) / BigInt(100)).toString(), // 50% higher
        estimatedConfirmationTime: {
          slow: 60, // 1 minute
          standard: 30, // 30 seconds
          fast: 15, // 15 seconds
        },
      };
    }

    try {
      const feeData = await this.provider.getFeeData();
      const gasPrice = feeData.gasPrice?.toString() || config.gas.defaultGasPrice;

      // Update cache
      this.cachedGasPrice = gasPrice;
      this.lastGasPriceUpdate = now;

      return {
        slow: gasPrice,
        standard: (BigInt(gasPrice) * BigInt(120) / BigInt(100)).toString(),
        fast: (BigInt(gasPrice) * BigInt(150) / BigInt(100)).toString(),
        estimatedConfirmationTime: {
          slow: 60,
          standard: 30,
          fast: 15,
        },
      };
    } catch (error) {
      console.error('Failed to get gas price:', error);

      // Return default gas prices as fallback
      const defaultGasPrice = config.gas.defaultGasPrice;
      return {
        slow: defaultGasPrice,
        standard: (BigInt(defaultGasPrice) * BigInt(120) / BigInt(100)).toString(),
        fast: (BigInt(defaultGasPrice) * BigInt(150) / BigInt(100)).toString(),
        estimatedConfirmationTime: {
          slow: 60,
          standard: 30,
          fast: 15,
        },
      };
    }
  }

  /**
   * Estimate gas for a transaction
   */
  async estimateGas(transaction: {
    to: string;
    value?: string;
    data?: string;
  }): Promise<string> {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    try {
      const gasEstimate = await this.provider.estimateGas(transaction);

      // Add buffer using configured multiplier
      const gasWithBuffer = BigInt(gasEstimate) * BigInt(Math.floor(config.gas.gasLimitMultiplier * 100)) / BigInt(100);

      return gasWithBuffer.toString();
    } catch (error) {
      console.error('Failed to estimate gas:', error);
      return config.gas.defaultGasLimit.toString();
    }
  }

  /**
   * Get transaction by hash
   */
  async getTransaction(hash: string) {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    try {
      return await this.provider.getTransaction(hash);
    } catch (error) {
      console.error(`Failed to get transaction ${hash}:`, error);
      throw error;
    }
  }

  /**
   * Get transaction receipt
   */
  async getTransactionReceipt(hash: string) {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    try {
      return await this.provider.getTransactionReceipt(hash);
    } catch (error) {
      console.error(`Failed to get transaction receipt ${hash}:`, error);
      throw error;
    }
  }

  /**
   * Monitor transaction status
   */
  async waitForTransaction(hash: string, confirmations = 1, timeout = 60000) {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    try {
      return await this.provider.waitForTransaction(hash, confirmations, timeout);
    } catch (error) {
      console.error(`Failed to wait for transaction ${hash}:`, error);
      throw error;
    }
  }

  /**
   * Send a signed transaction to the network
   */
  async sendTransaction(signedTx: string): Promise<TransactionResult> {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    // Network validation: Support both BSC Testnet and Mainnet
    const allowedChainIds = [56, 97]; // BSC Mainnet (56) and BSC Testnet (97)
    if (!allowedChainIds.includes(this.currentNetwork.chainId)) {
      throw new Error(`Unsupported network: Only BSC networks are allowed, but connected to chainId ${this.currentNetwork.chainId}`);
    }

    try {
      console.log('Broadcasting transaction to', this.currentNetwork.displayName);
      const txResponse = await this.provider.broadcastTransaction(signedTx);

      return {
        hash: txResponse.hash,
        from: txResponse.from || '',
        to: txResponse.to || '',
        value: txResponse.value.toString(),
        gasPrice: txResponse.gasPrice?.toString() || '0',
        status: 'pending',
        confirmations: 0,
        timestamp: Date.now(),
      };
    } catch (error) {
      console.error('Failed to broadcast transaction:', error);
      throw error;
    }
  }

  /**
   * Create and send a transaction (requires private key)
   */
  async createAndSendTransaction({
    privateKey,
    to,
    value,
    data,
    gasLimit,
    gasPrice,
  }: {
    privateKey: string;
    to: string;
    value?: string;
    data?: string;
    gasLimit?: string;
    gasPrice?: string;
  }): Promise<TransactionResult> {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    // Network validation: Support both BSC Testnet and Mainnet
    const allowedChainIds = [56, 97]; // BSC Mainnet (56) and BSC Testnet (97)
    if (!allowedChainIds.includes(this.currentNetwork.chainId)) {
      throw new Error(`Unsupported network: Only BSC networks are allowed, but connected to chainId ${this.currentNetwork.chainId}`);
    }

    try {
      // Validate and format private key
      const validatedPrivateKey = validatePrivateKey(privateKey);

      // Create wallet instance
      const wallet = new Wallet(validatedPrivateKey, this.provider);

      // Get current nonce
      const nonce = await this.provider.getTransactionCount(wallet.address, 'pending');

      // Use provided gas price or fetch current
      let finalGasPrice = gasPrice;
      if (!finalGasPrice) {
        const gasPriceInfo = await this.getGasPriceInfo();
        finalGasPrice = gasPriceInfo.standard;
      }

      // Prepare transaction
      const txRequest = {
        to,
        value: value ? parseEther(value) : 0,
        data: data || '0x',
        gasLimit: gasLimit ? parseInt(gasLimit) : 21000,
        gasPrice: parseUnits(finalGasPrice, 'gwei'),
        nonce,
        chainId: this.currentNetwork.chainId,
      };

      // Estimate gas if not provided
      if (!gasLimit) {
        try {
          const estimatedGas = await wallet.estimateGas(txRequest);
          txRequest.gasLimit = Number(estimatedGas) * 1.2; // Add 20% buffer
        } catch (gasError) {
          console.warn('Gas estimation failed, using default:', gasError);
        }
      }

      console.log('Sending transaction:', {
        from: wallet.address,
        to: txRequest.to,
        value: formatEther(txRequest.value),
        gasLimit: txRequest.gasLimit,
        gasPrice: formatEther(txRequest.gasPrice * BigInt(1000000000)),
        nonce: txRequest.nonce,
        network: this.currentNetwork.displayName,
      });

      // Send transaction
      const txResponse = await wallet.sendTransaction(txRequest);

      console.log(`Transaction sent: ${txResponse.hash}`);
      console.log(`View on BSCScan: ${this.currentNetwork.blockExplorerUrl}/tx/${txResponse.hash}`);

      return {
        hash: txResponse.hash,
        from: txResponse.from,
        to: txResponse.to || '',
        value: txResponse.value.toString(),
        gasPrice: txResponse.gasPrice?.toString() || '0',
        gasUsed: txResponse.gasLimit?.toString(),
        status: 'pending',
        confirmations: 0,
        timestamp: Date.now(),
      };
    } catch (error) {
      console.error('Failed to create and send transaction:', error);
      throw error;
    }
  }

  /**
   * Wait for transaction confirmation and return final result with enhanced verification
   */
  async confirmTransaction(hash: string, requiredConfirmations = 1, timeoutMs = 120000): Promise<TransactionResult> {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    // Network validation: Support both BSC Testnet and Mainnet
    const allowedChainIds = [56, 97]; // BSC Mainnet (56) and BSC Testnet (97)
    if (!allowedChainIds.includes(this.currentNetwork.chainId)) {
      throw new Error(`Unsupported network: Only BSC networks are allowed, but connected to chainId ${this.currentNetwork.chainId}`);
    }

    try {
      console.log(`Waiting for transaction confirmation: ${hash}`);
      console.log(`Required confirmations: ${requiredConfirmations}, Timeout: ${timeoutMs}ms`);

      // Use enhanced receipt verification with timeout
      const receipt = await this.waitForTransactionWithEnhancedVerification(hash, requiredConfirmations, timeoutMs);

      if (!receipt) {
        throw new Error('Transaction receipt not available after waiting');
      }

      // Get the original transaction details
      const transaction = await this.getTransaction(hash);
      if (!transaction) {
        throw new Error('Transaction not found on blockchain');
      }

      // Additional verification: Double-check receipt matches transaction
      if (receipt.hash.toLowerCase() !== hash.toLowerCase()) {
        throw new Error(`Receipt hash mismatch: expected ${hash}, got ${receipt.hash}`);
      }

      // Get actual confirmation count
      const currentBlock = await this.provider.getBlockNumber();
      const actualConfirmations = Math.max(0, currentBlock - receipt.blockNumber + 1);

      const result: TransactionResult = {
        hash: receipt.hash,
        from: transaction.from,
        to: transaction.to || '',
        value: transaction.value.toString(),
        gasUsed: receipt.gasUsed?.toString(),
        gasPrice: transaction.gasPrice?.toString() || '0',
        status: receipt.status === 1 ? 'success' : 'failed',
        blockNumber: receipt.blockNumber,
        confirmations: actualConfirmations,
        timestamp: Date.now(),
      };

      console.log(`Transaction confirmed with enhanced verification:`, {
        hash: result.hash,
        status: result.status,
        gasUsed: result.gasUsed,
        blockNumber: result.blockNumber,
        actualConfirmations: actualConfirmations,
        requiredConfirmations: requiredConfirmations,
        bscScanLink: `${this.currentNetwork.blockExplorerUrl}/tx/${result.hash}`,
      });

      // Verify we have sufficient confirmations
      if (actualConfirmations < requiredConfirmations) {
        console.warn(`Transaction has ${actualConfirmations} confirmations, but ${requiredConfirmations} required`);
      }

      return result;
    } catch (error) {
      console.error(`Failed to confirm transaction ${hash}:`, error);
      throw error;
    }
  }

  /**
   * Enhanced transaction waiting with better verification and timeout handling
   */
  private async waitForTransactionWithEnhancedVerification(
    hash: string, 
    requiredConfirmations: number, 
    timeoutMs: number
  ) {
    if (!this.provider) {
      throw new Error('Provider not initialized');
    }

    const startTime = Date.now();

    // First, try the built-in waitForTransaction with timeout
    try {
      const receipt = await this.provider.waitForTransaction(hash, requiredConfirmations, timeoutMs);
      if (receipt) {
        // Additional verification: Check if receipt is actually valid
        const directReceipt = await this.provider.getTransactionReceipt(hash);
        if (!directReceipt) {
          throw new Error('Receipt verification failed: could not retrieve receipt directly');
        }
        return receipt;
      }
    } catch (error) {
      console.warn(`Standard waitForTransaction failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    // Fallback: Manual polling with enhanced checks
    console.log(`Falling back to manual transaction polling for ${hash}`);

    while (Date.now() - startTime < timeoutMs) {
      try {
        const receipt = await this.provider.getTransactionReceipt(hash);
        if (receipt) {
          const currentBlock = await this.provider.getBlockNumber();
          const confirmations = currentBlock - receipt.blockNumber + 1;

          console.log(`Transaction ${hash}: block ${receipt.blockNumber}, current block ${currentBlock}, confirmations: ${confirmations}`);

          if (confirmations >= requiredConfirmations) {
            return receipt;
          }
        }
      } catch (error) {
        console.warn(`Receipt check failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }

      // Wait before next check
      await new Promise(resolve => setTimeout(resolve, 2000));
    }

    throw new Error(`Transaction confirmation timeout after ${timeoutMs}ms`);
  }

  /**
   * Clean up provider resources
   */
  destroy(): void {
    if (this.provider) {
      this.provider.removeAllListeners();
      this.provider = null;
    }
    this.cachedGasPrice = null;
    this.lastGasPriceUpdate = 0;
    console.log('BSC RPC Client destroyed');
  }
}

// Create singleton instance
export const bscRpcClient = new BSCRPCClient();

// Export for testing and advanced usage
export { BSCRPCClient };
export default bscRpcClient;

