  border-radius: 4px;
  overflow: hidden;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #4facfe, #00f2fe);
  transition: width 0.3s ease;
}

.progress-text {
  font-size: 12px;
  color: #4a5568;
  margin-top: 4px;
}

.bulk-action {
  display: flex;
  justify-content: center;
}

.bulk-request-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 24px;
  background: linear-gradient(135deg, #4facfe 0%, #00f2fe 100%);
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
  min-width: 200px;
  justify-content: center;
}

.bulk-request-btn:hover:not(:disabled) {
  transform: translateY(-1px);
  box-shadow: 0 4px 8px rgba(79, 172, 254, 0.3);
}

.bulk-request-btn:disabled {
  background: #cbd5e0;
  color: #a0aec0;
  cursor: not-allowed;
  opacity: 0.6;
}

.bulk-request-btn.requesting {
  background: linear-gradient(135deg, #ffeaa7 0%, #fab1a0 100%);
  animation: pulse 2s infinite;
}

.bulk-warning {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px;
  background: #fffaf0;
  border: 1px solid #fbd38d;
  border-radius: 6px;
  color: #c05621;
  font-size: 14px;
}

.status.running {
  color: #3182ce;
}

.status.completed {
  color: #38a169;
}

.status.cancelled {
  color: #e53e3e;
}
`;

export default BulkFaucet;

