/**
 * Mock Testing Panel Component
 * Interface for simulated bundler testing without real blockchain interactions
 */

import React, { useState, useCallback } from 'react';
import { BSCScanButton } from './BSCScanButton';

// Mock interfaces for testing
interface MockTestWallet {
  address: string;
  privateKey: string;
  balance: number;
  created: Date;
}

interface MockTestResult {
  walletAddress: string;
  transactionHash: string;
  bscScanLink: string;
  status: 'success' | 'failed' | 'pending';
  gasUsed?: string;
  value: string;
  timestamp: Date;
  error?: string;
}

interface MockBundleTestSuite {
  testId: string;
  scenario: string;
  wallets: MockTestWallet[];
  transactions: MockTestResult[];
  startTime: Date;
  endTime?: Date;
  totalGasUsed: string;
  totalValue: string;
  successRate: number;
  bscScanReportLink: string;
}

interface TestState {
  status: 'idle' | 'initializing' | 'generating' | 'funding' | 'testing' | 'completed' | 'error';
  message: string;
  wallets: MockTestWallet[];
  currentTest: MockBundleTestSuite | null;
  allTests: MockBundleTestSuite[];
  showFundingInstructions: boolean;
}

// Mock testing service
class MockBundlerTestService {
  private testSuites: MockBundleTestSuite[] = [];

  async initializeTestEnvironment(): Promise<void> {
    // Simulate initialization delay
    await new Promise(resolve => setTimeout(resolve, 2000));
    console.log('‚úÖ Mock BSC environment initialized (simulation mode)');
  }

  generateTestWallets(count: number): MockTestWallet[] {
    const wallets: MockTestWallet[] = [];
    for (let i = 0; i < count; i++) {
      // Generate mock wallet addresses
      const randomBytes = Array.from({ length: 20 }, () => Math.floor(Math.random() * 256));
      const address = '0x' + randomBytes.map(b => b.toString(16).padStart(2, '0')).join('');

      wallets.push({
        address,
        privateKey: '0x' + Array.from({ length: 32 }, () => Math.floor(Math.random() * 256).toString(16).padStart(2, '0')).join(''),
        balance: Math.random() * 0.1, // Random mock balance
        created: new Date(),
      });
    }
    return wallets;
  }

  async executeTestBundle(params: {
    wallets: MockTestWallet[];
    scenario: string;
    transactionAmount: string;
  }): Promise<MockBundleTestSuite> {
    const testId = `mock_test_${Date.now()}`;
    const startTime = new Date();

    // Simulate test execution delay
    await new Promise(resolve => setTimeout(resolve, 3000));

    const transactions: MockTestResult[] = params.wallets.map((wallet, index) => {
      const isSuccess = Math.random() > 0.1; // 90% success rate for mock
      const txHash = '0x' + Array.from({ length: 32 }, () => Math.floor(Math.random() * 256).toString(16).padStart(2, '0')).join('');

      return {
        walletAddress: wallet.address,
        transactionHash: isSuccess ? txHash : 'FAILED',
        bscScanLink: `https://testnet.bscscan.com/tx/${txHash}`,
        status: isSuccess ? 'success' : 'failed',
        gasUsed: isSuccess ? Math.floor(Math.random() * 50000 + 21000).toString() : undefined,
        value: params.transactionAmount,
        timestamp: new Date(),
        error: isSuccess ? undefined : 'Mock transaction failure for testing',
      };
    });

    const successfulTxs = transactions.filter(tx => tx.status === 'success');
    const testSuite: MockBundleTestSuite = {
      testId,
      scenario: params.scenario,
      wallets: params.wallets,
      transactions,
      startTime,
      endTime: new Date(),
      totalGasUsed: successfulTxs.reduce((sum, tx) => sum + parseInt(tx.gasUsed || '0'), 0).toString(),
      totalValue: (parseFloat(params.transactionAmount) * successfulTxs.length).toString(),
      successRate: (successfulTxs.length / transactions.length) * 100,
      bscScanReportLink: `https://testnet.bscscan.com/address/${params.wallets[0]?.address}`,
    };

    this.testSuites.push(testSuite);
    return testSuite;
  }

  exportTestReport(): string {
    return JSON.stringify({
      summary: {
        totalTests: this.testSuites.length,
        averageSuccessRate: this.testSuites.reduce((sum, test) => sum + test.successRate, 0) / this.testSuites.length,
        totalTransactions: this.testSuites.reduce((sum, test) => sum + test.transactions.length, 0),
      },
      tests: this.testSuites,
      generatedAt: new Date().toISOString(),
      mode: 'MOCK_SIMULATION',
    }, null, 2);
  }

  getFaucetInstructions() {
    return {
      faucetUrls: [
        { name: 'Mock Faucet 1 (Simulation)', url: '#mock-faucet-1' },
        { name: 'Mock Faucet 2 (Simulation)', url: '#mock-faucet-2' },
      ],
      wallets: [],
      warnings: [
        'This is simulation mode - no real funds needed',
        'All transactions are mocked for testing purposes',
        'BSCScan links will not work in simulation mode',
      ],
    };
  }
}

const mockBundlerTest = new MockBundlerTestService();

export const RealTestingPanel: React.FC = () => {
  const [testState, setTestState] = useState<TestState>({
    status: 'idle',
    message: 'Ready to start mock bundler testing (simulation mode)',
    wallets: [],
    currentTest: null,
    allTests: [],
    showFundingInstructions: false,
  });

  const updateStatus = useCallback((status: TestState['status'], message: string) => {
    setTestState(prev => ({ ...prev, status, message }));
  }, []);

  const initializeTestEnvironment = useCallback(async () => {
    updateStatus('initializing', 'Initializing mock environment...');

    try {
      await mockBundlerTest.initializeTestEnvironment();
      updateStatus('idle', '‚úÖ Mock environment ready - Simulation mode active');
    } catch (error) {
      console.error('Mock initialization failed:', error);
      updateStatus('error', `‚ùå Failed to initialize: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }, [updateStatus]);

  const generateTestWallets = useCallback(async (count: number = 5) => {
    updateStatus('generating', `Generating ${count} mock test wallets...`);

    try {
      const wallets = mockBundlerTest.generateTestWallets(count);
      setTestState(prev => ({ ...prev, wallets, status: 'idle', message: `‚úÖ Generated ${count} mock test wallets` }));
    } catch (error) {
      console.error('Mock wallet generation failed:', error);
      updateStatus('error', `‚ùå Failed to generate wallets: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }, [updateStatus]);

  const showFundingInstructions = useCallback(() => {
    if (testState.wallets.length === 0) {
      updateStatus('error', '‚ùå No wallets generated yet. Generate wallets first.');
      return;
    }

    setTestState(prev => ({
      ...prev, 
      showFundingInstructions: true,
      status: 'funding',
      message: 'Mock funding instructions shown (simulation mode - no real funding needed)'
    }));
  }, [testState.wallets]);

  const copyToClipboard = useCallback(async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      updateStatus('idle', 'üìã Mock address copied to clipboard!');
    } catch (error) {
      console.error('Failed to copy to clipboard:', error);
      updateStatus('error', '‚ùå Failed to copy to clipboard');
    }
  }, [updateStatus]);

  const hideFundingInstructions = useCallback(() => {
    setTestState(prev => ({ ...prev, showFundingInstructions: false }));
    updateStatus('idle', 'Ready for mock testing');
  }, [updateStatus]);

  const executeTestBundle = useCallback(async (scenario: string) => {
    if (testState.wallets.length === 0) {
      updateStatus('error', '‚ùå No wallets available. Generate wallets first.');
      return;
    }

    updateStatus('testing', `üöÄ Executing mock ${scenario} test bundle...`);

    try {
      const testSuite = await mockBundlerTest.executeTestBundle({
        wallets: testState.wallets,
        scenario,
        transactionAmount: '0.001', // Mock test amount
      });

      setTestState(prev => ({
        ...prev,
        currentTest: testSuite,
        allTests: [...prev.allTests, testSuite],
        status: 'completed',
        message: `‚úÖ Mock ${scenario} test completed! Success rate: ${testSuite.successRate.toFixed(1)}%`,
      }));

    } catch (error) {
      console.error('Mock test execution failed:', error);
      updateStatus('error', `‚ùå Mock test failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }, [testState.wallets, updateStatus]);

  const exportTestReport = useCallback(() => {
    try {
      const report = mockBundlerTest.exportTestReport();
      const blob = new Blob([report], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `justjewit-mock-test-report-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      updateStatus('idle', '‚úÖ Mock test report exported successfully');
    } catch (error) {
      console.error('Export failed:', error);
      updateStatus('error', `‚ùå Export failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }, [updateStatus]);

  const getStatusIcon = () => {
    switch (testState.status) {
      case 'idle': return '‚ö°';
      case 'initializing': return 'üîÑ';
      case 'generating': return 'üîë';
      case 'funding': return 'üö∞';
      case 'testing': return 'üöÄ';
      case 'completed': return '‚úÖ';
      case 'error': return '‚ùå';
      default: return '‚ö°';
    }
  };

  return (
    <div className="real-testing-panel">
      <div className="panel-header">
        <h2>üéØ Mock Bundler Testing</h2>
        <p><strong>SIMULATION MODE:</strong> Test bundler functionality without real blockchain interactions</p>
      </div>

      {/* Status Display */}
      <div className={`status-display ${testState.status}`}>
        <div className="status-icon">{getStatusIcon()}</div>
        <div className="status-message">{testState.message}</div>
      </div>

      {/* Control Buttons */}
      <div className="control-section">
        <h3>Mock Test Controls</h3>
        <div className="button-grid">
          <button
            onClick={initializeTestEnvironment}
            disabled={testState.status === 'initializing'}
            className="control-button primary"
          >
            üîó Initialize Mock Environment
          </button>

          <button
            onClick={() => generateTestWallets(5)}
            disabled={testState.status !== 'idle'}
            className="control-button"
          >
            üîë Generate 5 Mock Wallets
          </button>

          <button
            onClick={showFundingInstructions}
            disabled={testState.wallets.length === 0}
            className="control-button"
          >
            üö∞ Show Mock Instructions
          </button>

          <button
            onClick={() => executeTestBundle('Mock Transaction Bundle')}
            disabled={testState.status === 'testing' || testState.wallets.length === 0}
            className="control-button success"
          >
            üöÄ Execute Mock Test Bundle
          </button>

          <button
            onClick={exportTestReport}
            disabled={testState.allTests.length === 0}
            className="control-button"
          >
            üìÑ Export Mock Report
          </button>
        </div>
      </div>

      {/* Wallet Display */}
      {testState.wallets.length > 0 && (
        <div className="wallets-section">
          <h3>üîë Mock Test Wallets ({testState.wallets.length})</h3>
          <div className="wallets-list">
            {testState.wallets.map((wallet, index) => (
              <div key={wallet.address} className="wallet-item">
                <div className="wallet-info">
                  <span className="wallet-number">#{index + 1}</span>
                  <span className="wallet-address" title={wallet.address}>
                    {wallet.address.substring(0, 10)}...{wallet.address.slice(-6)}
                  </span>
                  <span className="wallet-balance">{wallet.balance.toFixed(6)} tBNB (mock)</span>
                  <span className="mock-indicator" style={{ color: '#ffa500', fontWeight: 'bold' }}>MOCK</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Current Test Results */}
      {testState.currentTest && (
        <div className="current-test-section">
          <h3>üìä Mock Test Results</h3>
          <div className="test-summary">
            <div className="summary-stats">
              <div className="stat-item">
                <span className="stat-label">Scenario:</span>
                <span className="stat-value">{testState.currentTest.scenario}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Success Rate:</span>
                <span className="stat-value">{testState.currentTest.successRate.toFixed(1)}%</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Transactions:</span>
                <span className="stat-value">{testState.currentTest.transactions.length}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Total Value:</span>
                <span className="stat-value">{testState.currentTest.totalValue} tBNB (mock)</span>
              </div>
            </div>
          </div>

          {/* Transaction Results */}
          <div className="transactions-results">
            <h4>üîó Mock Transaction Results</h4>
            <div className="transactions-list">
              {testState.currentTest.transactions.map((tx, index) => (
                <div key={index} className={`transaction-item ${tx.status}`}>
                  <div className="transaction-info">
                    <span className="tx-number">#{index + 1}</span>
                    <span className="tx-status-icon">
                      {tx.status === 'success' ? '‚úÖ' : tx.status === 'failed' ? '‚ùå' : '‚è≥'}
                    </span>
                    <span className="tx-from">
                      {tx.walletAddress.substring(0, 8)}...
                    </span>
                    <span className="tx-value">{tx.value} tBNB (mock)</span>
                    <span className="mock-indicator" style={{ color: '#ffa500', fontWeight: 'bold' }}>SIMULATED</span>
                  </div>
                  {tx.error && (
                    <div className="tx-error">
                      ‚ùå Mock Error: {tx.error}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Mock Funding Instructions */}
      {testState.showFundingInstructions && testState.wallets.length > 0 && (
        <div className="funding-instructions-section">
          <div className="funding-header">
            <h3>üö∞ Mock Funding Instructions</h3>
            <button 
              className="close-instructions-btn"
              onClick={hideFundingInstructions}
              title="Hide mock instructions"
              style={{ background: 'none', border: 'none', fontSize: '24px', cursor: 'pointer' }}
            >
              √ó
            </button>
          </div>

          <div className="funding-content">
            <div className="mock-notice" style={{ 
              padding: '20px', 
              backgroundColor: '#e8f5e8', 
              border: '2px solid #4CAF50', 
              borderRadius: '10px', 
              marginBottom: '20px',
              textAlign: 'center'
            }}>
              <h4 style={{ color: '#2E7D32' }}>üé≠ SIMULATION MODE ACTIVE</h4>
              <p>No real funding required! This is a mock environment for testing purposes.</p>
              <p>All wallets are pre-funded with simulated test tokens.</p>
            </div>

            <div className="wallet-addresses" style={{ marginTop: '20px' }}>
              <h4>Mock Wallet Addresses (for reference only)</h4>
              <div className="wallet-list">
                {testState.wallets.map((wallet, index) => (
                  <div key={wallet.address} className="wallet-funding-item" style={{ margin: '10px 0', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '5px' }}>
                    <div className="wallet-info">
                      <div className="wallet-label" style={{ fontWeight: 'bold', marginBottom: '5px' }}>Mock Wallet {index + 1}</div>
                      <div className="wallet-address-container" style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '5px' }}>
                        <code className="wallet-address" style={{ flex: 1, backgroundColor: '#e9ecef', padding: '5px', borderRadius: '3px', fontSize: '12px' }}>
                          {wallet.address}
                        </code>
                        <button 
                          className="copy-btn"
                          onClick={() => copyToClipboard(wallet.address)}
                          title="Copy mock address to clipboard"
                          style={{ padding: '5px 10px', cursor: 'pointer', border: '1px solid #ccc', borderRadius: '3px', backgroundColor: '#fff' }}
                        >
                          üìã Copy
                        </button>
                      </div>
                      <span style={{ color: '#ffa500', fontWeight: 'bold' }}>MOCK ADDRESS - NOT ON REAL BLOCKCHAIN</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="funding-warnings" style={{ marginTop: '20px', padding: '15px', backgroundColor: '#fff3cd', borderRadius: '5px', border: '1px solid #ffeaa7' }}>
              <h4>‚ÑπÔ∏è Mock Mode Information</h4>
              <ul className="warning-list">
                <li>‚úÖ This is simulation mode - no real funds needed</li>
                <li>‚úÖ All transactions are mocked for testing purposes</li>
                <li>‚úÖ BSCScan links will not work in simulation mode</li>
                <li>‚úÖ Perfect for testing UI and bundler logic without costs</li>
              </ul>
            </div>
          </div>
        </div>
      )}

      {/* Test History */}
      {testState.allTests.length > 0 && (
        <div className="test-history-section">
          <h3>üìà Mock Test History ({testState.allTests.length} tests)</h3>
          <div className="history-list">
            {testState.allTests.map((test, index) => (
              <div key={test.testId} className="history-item">
                <div className="history-summary">
                  <span className="history-index">#{index + 1}</span>
                  <span className="history-scenario">{test.scenario}</span>
                  <span className="history-success-rate">{test.successRate.toFixed(1)}%</span>
                  <span className="history-timestamp">
                    {test.startTime.toLocaleTimeString()}
                  </span>
                  <span className="history-transactions">
                    {test.transactions.filter(tx => tx.status === 'success').length}/{test.transactions.length} TX (mock)
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Instructions */}
      <div className="instructions-section">
        <h3>üìù Mock Testing Instructions</h3>
        <ol className="instructions-list">
          <li>Click <strong>"Initialize Mock Environment"</strong> to start simulation mode</li>
          <li>Click <strong>"Generate 5 Mock Wallets"</strong> to create simulated wallet addresses</li>
          <li>Click <strong>"Show Mock Instructions"</strong> to view simulation information</li>
          <li>Click <strong>"Execute Mock Test Bundle"</strong> to run simulated transactions</li>
          <li>Review mock transaction results and success rates</li>
          <li>Export mock test report for documentation</li>
        </ol>
        <div className="important-note">
          ‚ÑπÔ∏è <strong>Mock Mode:</strong> This panel simulates bundler functionality without real blockchain interactions.
          Perfect for testing UI components and bundler logic safely.
        </div>
      </div>
    </div>
  );
};

export default RealTestingPanel;

