.access-key-login-container {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  background: #0a0a0a;
}

.background-swirl {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(ellipse at center, rgba(20, 25, 40, 0.8) 0%, rgba(5, 8, 15, 0.95) 70%);
  z-index: 1;
}

.background-swirl::before {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: 
    radial-gradient(circle at 20% 20%, rgba(59, 130, 246, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 80%, rgba(147, 51, 234, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 40% 60%, rgba(16, 185, 129, 0.05) 0%, transparent 50%);
  animation: swirl 20s linear infinite;
}

.background-swirl::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: 
    url('data:image/svg+xml,<svg width="100" height="100" xmlns="http://www.w3.org/2000/svg"><defs><pattern id="swirl" x="0" y="0" width="100" height="100" patternUnits="userSpaceOnUse"><path d="M20,50 Q50,20 80,50 Q50,80 20,50" stroke="rgba(59,130,246,0.03)" stroke-width="1" fill="none"/></pattern></defs><rect width="100" height="100" fill="url(%23swirl)"/></svg>');
  opacity: 0.6;
  animation: drift 30s ease-in-out infinite;
}

@keyframes swirl {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes drift {
  0%, 100% { transform: translateX(0) translateY(0); }
  25% { transform: translateX(-20px) translateY(-10px); }
  50% { transform: translateX(20px) translateY(-20px); }
  75% { transform: translateX(-10px) translateY(10px); }
}

.login-card {
  background: rgba(15, 18, 25, 0.9);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 24px;
  backdrop-filter: blur(20px);
  padding: 48px 40px;
  width: 100%;
  max-width: 480px;
  position: relative;
  z-index: 2;
  box-shadow: 
    0 25px 50px -12px rgba(0, 0, 0, 0.7),
    0 0 0 1px rgba(255, 255, 255, 0.05),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

.login-header {
  text-align: center;
  margin-bottom: 48px;
}

.login-header h1 {
  font-size: 2.5rem;
  font-weight: 300;
  color: #ffffff;
  margin: 0 0 16px 0;
  letter-spacing: -0.5px;
  background: linear-gradient(135deg, #ffffff 0%, #e5e7eb 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.login-header p {
  font-size: 1.1rem;
  color: rgba(255, 255, 255, 0.6);
  margin: 0;
  font-weight: 400;
}

.access-key-form {
  margin-bottom: 32px;
}

.loading-dots {
  display: flex;
  justify-content: center;
  gap: 8px;
  margin-bottom: 32px;
}

.dot {
  width: 8px;
  height: 8px;
  background: rgba(59, 130, 246, 0.6);
  border-radius: 50%;
  animation: pulse-dot 2s ease-in-out infinite;
}

.dot:nth-child(2) {
  animation-delay: 0.5s;
  background: rgba(147, 51, 234, 0.6);
}

.dot:nth-child(3) {
  animation-delay: 1s;
  background: rgba(16, 185, 129, 0.6);
}

@keyframes pulse-dot {
  0%, 80%, 100% {
    transform: scale(1);
    opacity: 0.6;
  }
  40% {
    transform: scale(1.3);
    opacity: 1;
  }
}

.access-key-input-container {
  margin-bottom: 24px;
}

.access-key-input {
  width: 100%;
  padding: 20px 24px;
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  font-size: 1.1rem;
  color: #ffffff;
  text-align: center;
  letter-spacing: 1px;
  font-family: 'SF Mono', 'Monaco', 'Inconsolata', 'Roboto Mono', monospace;
  transition: all 0.3s ease;
  box-sizing: border-box;
}

.access-key-input::placeholder {
  color: rgba(255, 255, 255, 0.3);
  letter-spacing: 0.5px;
}

.access-key-input:focus {
  outline: none;
  border-color: rgba(59, 130, 246, 0.5);
  background: rgba(255, 255, 255, 0.05);
  box-shadow: 
    0 0 0 4px rgba(59, 130, 246, 0.1),
    0 0 20px rgba(59, 130, 246, 0.2);
}

.access-key-input.error {
  border-color: rgba(239, 68, 68, 0.6);
  background: rgba(239, 68, 68, 0.05);
}

.error-message {
  color: rgba(239, 68, 68, 0.9);
  text-align: center;
  font-size: 0.9rem;
  margin-bottom: 16px;
  padding: 12px;
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid rgba(239, 68, 68, 0.2);
  border-radius: 12px;
}

.enter-button {
  width: 100%;
  padding: 18px 32px;
  background: linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%);
  border: none;
  border-radius: 16px;
  font-size: 1.1rem;
  font-weight: 600;
  color: #ffffff;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(99, 102, 241, 0.3);
}

.enter-button:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(99, 102, 241, 0.4);
  background: linear-gradient(135deg, #7c3aed 0%, #a855f7 100%);
}

.enter-button:active:not(:disabled) {
  transform: translateY(0);
}

.enter-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
  box-shadow: 0 4px 15px rgba(99, 102, 241, 0.1);
}

.security-info {
  text-align: center;
  padding-top: 24px;
  border-top: 1px solid rgba(255, 255, 255, 0.08);
}

.telegram-link {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.7);
  text-decoration: none;
  transition: all 0.3s ease;
  cursor: pointer;
}

.telegram-link:hover {
  color: rgba(255, 255, 255, 0.9);
  transform: translateY(-1px);
}

.telegram-link:active {
  transform: translateY(0);
}

.security-dot {
  width: 8px;
  height: 8px;
  background: #10b981;
  border-radius: 50%;
  box-shadow: 0 0 8px rgba(16, 185, 129, 0.5);
  animation: security-pulse 2s ease-in-out infinite;
}

@keyframes security-pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.6; }
}

@media (max-width: 520px) {
  .login-card {
    margin: 20px;
    padding: 36px 28px;
  }

  .login-header h1 {
    font-size: 2rem;
  }

  .access-key-input {
    font-size: 1rem;
    padding: 18px 20px;
  }
}

@media (max-height: 700px) {
  .login-card {
    max-width: 440px;
    padding: 32px 36px;
  }

  .login-header {
    margin-bottom: 32px;
  }

  .loading-dots {
    margin-bottom: 24px;
  }
}

