/**
 * AccessKeyLogin Component
 * Modern access key authentication interface matching the Proxima design
 */

import React, { useState, useRef, useEffect } from 'react';
import { useUserStore } from '../store/users';
import './AccessKeyLogin.css';

export const AccessKeyLogin: React.FC = () => {
  const [accessKey, setAccessKey] = useState('');
  const [isLogging, setIsLogging] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [attempts, setAttempts] = useState(0);
  const [isLocked, setIsLocked] = useState(false);

  const { loginWithAccessKey, error: userError, isLoading } = useUserStore();
  const accessKeyInputRef = useRef<HTMLInputElement>(null);

  // Focus access key input on mount
  useEffect(() => {
    accessKeyInputRef.current?.focus();
  }, []);

  // Handle access key change
  const handleAccessKeyChange = (value: string) => {
    setAccessKey(value.trim());
    setError(null);
  };

  // Handle enter key
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && accessKey.length >= 10) {
      handleLogin();
    }
  };

  // Handle login submission
  const handleLogin = async () => {
    if (!accessKey || accessKey.length < 10) {
      setError('Please enter a valid access key');
      return;
    }

    if (isLocked) {
      setError('Access temporarily restricted. Please try again later.');
      return;
    }

    setIsLogging(true);
    setError(null);

    try {
      const success = await loginWithAccessKey(accessKey);

      if (success) {
        console.log('Access granted!');
        // Component will unmount as user is now authenticated
      } else {
        setAttempts(prev => prev + 1);

        if (attempts >= 4) {
          setIsLocked(true);
          setError('Too many failed attempts. Access restricted for security.');
          setTimeout(() => {
            setIsLocked(false);
            setAttempts(0);
          }, 60000); // 1 minute lockout
        } else {
          setError(userError || `Invalid access key. ${5 - attempts} attempts remaining.`);
        }

        // Clear form on failure
        setAccessKey('');
        accessKeyInputRef.current?.focus();
      }
    } catch (error) {
      setError('Authentication failed. Please check your access key.');
      setAccessKey('');
      accessKeyInputRef.current?.focus();
    } finally {
      setIsLogging(false);
    }
  };

  return (
    <div className="access-key-login-container">
      <div className="background-swirl"></div>

      <div className="login-card">
        <div className="login-header">
          <h1>Welcome to JustJewIt.</h1>
          <p>Enter your access key to start</p>
        </div>

        <div className="access-key-form">
          <div className="loading-dots">
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
          </div>

          <div className="access-key-input-container">
            <input
              ref={accessKeyInputRef}
              type="text"
              value={accessKey}
              onChange={(e) => handleAccessKeyChange(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="WLSFX-MTWWGD2nn0RukqMlLw"
              className={`access-key-input ${error ? 'error' : ''}`}
              disabled={isLogging || isLoading || isLocked}
              autoComplete="off"
              spellCheck="false"
              maxLength={50}
            />
          </div>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          <button
            onClick={handleLogin}
            disabled={!accessKey || accessKey.length < 10 || isLogging || isLoading || isLocked}
            className="enter-button"
          >
            {isLogging || isLoading ? 'Authenticating...' : 'Enter'}
          </button>
        </div>

        <div className="security-info">
          <a 
            href="https://t.me/wlsfx" 
            target="_blank" 
            rel="noopener noreferrer" 
            className="telegram-link"
          >
            <span className="security-dot"></span>
            üì± @wlsfx
          </a>
        </div>
      </div>
    </div>
  );
};

src/components/AccessKeyManager.css.content:
.access-key-manager {
  padding: 24px;
  max-width: 1200px;
  margin: 0 auto;
  background: var(--bg-primary);
  min-height: 100vh;
}

.akm-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 32px;
  padding-bottom: 16px;
  border-bottom: 2px solid var(--border-color);
}

.akm-header h2 {
  color: var(--text-primary);
  font-size: 1.8rem;
  margin: 0;
}

.create-key-btn {
  background: linear-gradient(135deg, var(--accent-green) 0%, #00dd77 100%);
  color: var(--bg-primary);
  border: none;
  padding: 12px 24px;
  border-radius: 12px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(0, 255, 136, 0.2);
}

.create-key-btn:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(0, 255, 136, 0.3);
}

.create-key-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
}

.akm-error {
  background: rgba(255, 68, 68, 0.1);
  border: 1px solid var(--accent-red);
  color: var(--accent-red);
  padding: 16px;
  border-radius: 12px;
  margin-bottom: 24px;
  display: flex;
  align-items: center;
  gap: 8px;
}

.error-icon {
  font-size: 1.2rem;
}

.admin-key-section,
.created-keys-section {
  margin-bottom: 32px;
}

.admin-key-section h3,
.created-keys-section h3 {
  color: var(--text-primary);
  font-size: 1.4rem;
  margin-bottom: 16px;
  display: flex;
  align-items: center;
  gap: 8px;
}

.key-card {
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 16px;
  padding: 20px;
  margin-bottom: 16px;
  transition: all 0.3s ease;
  position: relative;
}

.key-card.admin-key {
  border-color: var(--accent-green);
  background: linear-gradient(135deg, var(--bg-secondary) 0%, rgba(0, 255, 136, 0.05) 100%);
}

.key-card.active {
  border-left: 4px solid var(--accent-green);
}

.key-card.inactive {
  border-left: 4px solid var(--accent-red);
  opacity: 0.7;
}

.key-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.3);
}

.key-info {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.key-label {
  display: flex;
  align-items: center;
  gap: 12px;
}

.key-label strong {
  color: var(--text-primary);
  font-size: 1.1rem;
}

.key-role {
  padding: 4px 12px;
  border-radius: 8px;
  font-size: 0.8rem;
  font-weight: 600;
  text-transform: uppercase;
}

.key-role.admin {
  background: var(--accent-orange);
  color: var(--bg-primary);
}

.key-role.user {
  background: var(--accent-blue);
  color: var(--bg-primary);
}

.key-value {
  display: flex;
  align-items: center;
  gap: 12px;
  background: var(--bg-tertiary);
  padding: 12px 16px;
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.key-display {
  flex: 1;
  font-family: 'SF Mono', 'Monaco', 'Inconsolata', 'Roboto Mono', monospace;
  font-size: 0.9rem;
  color: var(--text-primary);
  letter-spacing: 0.5px;
  overflow: hidden;
  text-overflow: ellipsis;
}

.key-actions {
  display: flex;
  gap: 8px;
}

.action-btn {
  background: var(--bg-tertiary);
  border: 1px solid var(--border-color);
  color: var(--text-primary);
  padding: 6px 8px;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s ease;
  font-size: 0.9rem;
}

.action-btn:hover {
  background: var(--border-color);
  transform: scale(1.05);
}

.action-btn.delete:hover {
  background: var(--accent-red);
  color: white;
}

.key-meta {
  display: flex;
  gap: 16px;
  flex-wrap: wrap;
  font-size: 0.85rem;
  color: var(--text-secondary);
}

.status {
  padding: 2px 8px;
  border-radius: 6px;
  font-weight: 500;
}

.status.active {
  background: rgba(0, 255, 136, 0.2);
  color: var(--accent-green);
}

.status.inactive {
  background: rgba(255, 68, 68, 0.2);
  color: var(--accent-red);
}

.no-keys {
  text-align: center;
  padding: 48px 24px;
  background: var(--bg-secondary);
  border: 2px dashed var(--border-color);
  border-radius: 16px;
  color: var(--text-secondary);
}

.no-keys p {
  font-size: 1.1rem;
  margin-bottom: 8px;
}

.no-keys small {
  font-size: 0.9rem;
}

.keys-list {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

/* Modal Styles */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  backdrop-filter: blur(4px);
}

.create-key-modal,
.success-modal {
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 20px;
  width: 90%;
  max-width: 500px;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.7);
}

.modal-header {
  padding: 24px 24px 16px 24px;
  border-bottom: 1px solid var(--border-color);
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.modal-header h3 {
  color: var(--text-primary);
  margin: 0;
  font-size: 1.3rem;
}

.modal-close {
  background: none;
  border: none;
  color: var(--text-secondary);
  font-size: 1.5rem;
  cursor: pointer;
  padding: 4px;
  border-radius: 4px;
}

.modal-close:hover {
  background: var(--border-color);
}

.modal-body {
  padding: 24px;
}

.form-group {
  margin-bottom: 20px;
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  color: var(--text-primary);
  font-weight: 500;
}

.form-group input,
.form-group select {
  width: 100%;
  padding: 12px 16px;
  background: var(--bg-tertiary);
  border: 1px solid var(--border-color);
  border-radius: 12px;
  color: var(--text-primary);
  font-size: 1rem;
  transition: all 0.3s ease;
  box-sizing: border-box;
}

.form-group input:focus,
.form-group select:focus {
  outline: none;
  border-color: var(--accent-green);
  box-shadow: 0 0 0 3px rgba(0, 255, 136, 0.1);
}

.form-group small {
  display: block;
  margin-top: 4px;
  color: var(--text-secondary);
  font-size: 0.85rem;
}

.modal-footer {
  padding: 16px 24px 24px 24px;
  display: flex;
  gap: 12px;
  justify-content: flex-end;
}

.btn-primary,
.btn-secondary {
  padding: 12px 24px;
  border-radius: 12px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  border: none;
}

.btn-primary {
  background: var(--accent-green);
  color: var(--bg-primary);
}

.btn-primary:hover:not(:disabled) {
  background: #00dd77;
  transform: translateY(-2px);
}

.btn-primary:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
}

.btn-secondary {
  background: var(--bg-tertiary);
  color: var(--text-primary);
  border: 1px solid var(--border-color);
}

.btn-secondary:hover {
  background: var(--border-color);
}

.created-key-display {
  background: var(--bg-tertiary);
  border: 2px solid var(--accent-green);
  border-radius: 12px;
  padding: 16px;
  margin: 16px 0;
  display: flex;
  align-items: center;
  gap: 12px;
}

.created-key-display code {
  flex: 1;
  font-family: 'SF Mono', 'Monaco', 'Inconsolata', 'Roboto Mono', monospace;
  font-size: 0.9rem;
  color: var(--accent-green);
  letter-spacing: 1px;
  word-break: break-all;
}

.copy-btn {
  background: var(--accent-green);
  color: var(--bg-primary);
  border: none;
  padding: 8px 16px;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 600;
  transition: all 0.2s ease;
}

.copy-btn:hover {
  background: #00dd77;
  transform: scale(1.05);
}

.security-warning {
  background: rgba(255, 165, 0, 0.1);
  border: 1px solid rgba(255, 165, 0, 0.3);
  color: #ffa500;
  padding: 12px;
  border-radius: 8px;
  margin-top: 16px;
  font-size: 0.9rem;
}

/* Responsive Design */
@media (max-width: 768px) {
  .access-key-manager {
    padding: 16px;
  }

  .akm-header {
    flex-direction: column;
    gap: 16px;
    align-items: stretch;
  }

  .key-value {
    flex-direction: column;
    align-items: stretch;
  }

  .key-actions {
    justify-content: center;
  }

  .key-meta {
    flex-direction: column;
    gap: 8px;
  }

  .modal-overlay {
    padding: 16px;
  }

  .create-key-modal,
  .success-modal {
    width: 100%;
    max-width: none;
  }
}

