/**
 * Four.meme API Client Service
 * Handles authentication, image upload, and token creation through four.meme platform
 */

import { ethers } from 'ethers';
import { bscRpcClient } from './bsc-rpc';
import { config } from '../config/env';
import { validatePrivateKey } from '../utils/crypto';

// Four.meme API endpoints
const FOURMEME_API_BASE = 'https://four.meme/meme-api';

// Four.meme API interfaces
export interface FourMemeNonceRequest {
  accountAddress: string;
  verifyType: 'LOGIN';
  networkCode: 'BSC';
}

export interface FourMemeNonceResponse {
  code: string;
  data: string;
}

export interface FourMemeLoginRequest {
  region: 'WEB';
  langType: 'EN';
  loginIp: '';
  inviteCode: '';
  verifyInfo: {
    address: string;
    networkCode: 'BSC';
    signature: string;
    verifyType: 'LOGIN';
  };
  walletName: 'MetaMask';
}

export interface FourMemeLoginResponse {
  code: string;
  data: string; // access_token
}

export interface FourMemeUploadResponse {
  code: string;
  data: string; // image URL
}

export interface FourMemeTokenCreateRequest {
  name: string;
  shortName: string;
  desc: string;
  imgUrl: string;
  launchTime: number;
  label: string;
  lpTradingFee: number;
  webUrl?: string;
  twitterUrl?: string;
  telegramUrl?: string;
  preSale: string;
  // Fixed parameters
  totalSupply: 1000000000;
  raisedAmount: 24;
  saleRate: 0.8;
  reserveRate: 0;
  funGroup: false;
  clickFun: false;
  symbol: 'BNB';
  raisedToken: {
    symbol: 'BNB';
    nativeSymbol: 'BNB';
    symbolAddress: '0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c';
    deployCost: '0';
    buyFee: '0.01';
    sellFee: '0.01';
    minTradeFee: '0';
    b0Amount: '8';
    totalBAmount: '24';
    totalAmount: '1000000000';
    logoUrl: 'https://static.four.meme/market/68b871b6-96f7-408c-b8d0-388d804b34275092658264263839640.png';
    tradeLevel: ['0.1', '0.5', '1'];
    status: 'PUBLISH';
    buyTokenLink: 'https://pancakeswap.finance/swap';
    reservedNumber: 10;
    saleRate: '0.8';
    networkCode: 'BSC';
    platform: 'MEME';
  };
}

export interface FourMemeTokenCreateResponse {
  code: string;
  data: {
    createArg: string; // Hex data for contract call
    signature: string; // Signature for contract call
  };
}

export interface TokenCreationResult {
  contractAddress: string;
  transactionHash: string;
  blockNumber: number;
  gasUsed: string;
  status: 'success' | 'failed';
  fourMemeUrl?: string;
}

class FourMemeClient {
  private accessToken: string | null = null;
  private tokenManagerAddress = '0x5c952063c7fc8610FFDB798152D69F0B9550762b'; // BSC TokenManager2

  constructor() {
    console.log('Four.meme client initialized');
  }

  /**
   * Generate nonce for wallet authentication
   */
  private async generateNonce(walletAddress: string): Promise<string> {
    const request: FourMemeNonceRequest = {
      accountAddress: walletAddress,
      verifyType: 'LOGIN',
      networkCode: 'BSC'
    };

    try {
      const response = await fetch(`${FOURMEME_API_BASE}/v1/private/user/nonce/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(request),
      });

      if (!response.ok) {
        throw new Error(`Failed to generate nonce: ${response.status} ${response.statusText}`);
      }

      const result: FourMemeNonceResponse = await response.json();

      console.log('Nonce API Response:', {
        code: result.code,
        data: result.data,
        fullResponse: result
      });

      if (result.code !== '0') {
        throw new Error(`Four.meme API error: ${result.code}`);
      }

      if (!result.data) {
        throw new Error('Four.meme API returned empty nonce data');
      }

      return result.data;
    } catch (error) {
      console.error('Failed to generate nonce:', error);
      throw new Error(`Nonce generation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Sign message with wallet and login to four.meme
   */
  async login(walletPrivateKey: string): Promise<string> {
    try {
      // Validate and format private key
      const validatedPrivateKey = validatePrivateKey(walletPrivateKey);

      // Create wallet instance
      const wallet = new ethers.Wallet(validatedPrivateKey);
      const walletAddress = wallet.address;

      console.log(`Authenticating with four.meme using wallet: ${walletAddress}`);

      // Step 1: Generate nonce
      const nonce = await this.generateNonce(walletAddress);
      console.log('Nonce generated successfully');

      // Step 2: Sign the message
      const message = `You are sign in Meme ${nonce}`;
      const signature = await wallet.signMessage(message);

      // Step 3: Login with signature
      const loginRequest: FourMemeLoginRequest = {
        region: 'WEB',
        langType: 'EN',
        loginIp: '',
        inviteCode: '',
        verifyInfo: {
          address: walletAddress,
          networkCode: 'BSC',
          signature,
          verifyType: 'LOGIN'
        },
        walletName: 'MetaMask'
      };

      const response = await fetch(`${FOURMEME_API_BASE}/v1/private/user/login/dex`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginRequest),
      });

      if (!response.ok) {
        throw new Error(`Login failed: ${response.status} ${response.statusText}`);
      }

      const result: FourMemeLoginResponse = await response.json();

      if (result.code !== '0') {
        throw new Error(`Four.meme login error: ${result.code}`);
      }

      this.accessToken = result.data;
      console.log('Successfully authenticated with four.meme');
      return this.accessToken;
    } catch (error) {
      console.error('Four.meme login failed:', error);
      throw new Error(`Authentication failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Upload token image to four.meme platform
   */
  async uploadImage(imageFile: File): Promise<string> {
    if (!this.accessToken) {
      throw new Error('Not authenticated. Please login first.');
    }

    try {
      const formData = new FormData();
      formData.append('file', imageFile);

      const response = await fetch(`${FOURMEME_API_BASE}/v1/private/token/upload`, {
        method: 'POST',
        headers: {
          'meme-web-access': this.accessToken,
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Image upload failed: ${response.status} ${response.statusText}`);
      }

      const result: FourMemeUploadResponse = await response.json();

      if (result.code !== '0') {
        throw new Error(`Four.meme upload error: ${result.code}`);
      }

      console.log('Image uploaded successfully:', result.data);
      return result.data;
    } catch (error) {
      console.error('Image upload failed:', error);
      throw new Error(`Image upload failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Create token parameters and get signature from four.meme API
   */
  async createTokenSignature(tokenData: {
    name: string;
    symbol: string;
    description: string;
    imageUrl: string;
    socialLinks: {
      website?: string;
      twitter?: string;
      telegram?: string;
    };
    launchTime?: number;
    preSaleBnb?: string;
  }): Promise<{ createArg: string; signature: string }> {
    if (!this.accessToken) {
      throw new Error('Not authenticated. Please login first.');
    }

    try {
      // Get config data for raisedToken (static for now)
      const raisedTokenConfig = {
        symbol: 'BNB' as const,
        nativeSymbol: 'BNB' as const,
        symbolAddress: '0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c' as const,
        deployCost: '0' as const,
        buyFee: '0.01' as const,
        sellFee: '0.01' as const,
        minTradeFee: '0' as const,
        b0Amount: '8' as const,
        totalBAmount: '24' as const,
        totalAmount: '1000000000' as const,
        logoUrl: 'https://static.four.meme/market/68b871b6-96f7-408c-b8d0-388d804b34275092658264263839640.png' as const,
        tradeLevel: ['0.1', '0.5', '1'],
        status: 'PUBLISH' as const,
        buyTokenLink: 'https://pancakeswap.finance/swap' as const,
        reservedNumber: 10 as const,
        saleRate: '0.8' as const,
        networkCode: 'BSC' as const,
        platform: 'MEME' as const
      };

      const createRequest: FourMemeTokenCreateRequest = {
        name: tokenData.name,
        shortName: tokenData.symbol,
        desc: tokenData.description,
        imgUrl: tokenData.imageUrl,
        launchTime: tokenData.launchTime || Date.now(),
        label: 'Meme', // Default to Meme category
        lpTradingFee: 0.0025, // Fixed trading fee
        webUrl: tokenData.socialLinks.website || '',
        twitterUrl: tokenData.socialLinks.twitter || '',
        telegramUrl: tokenData.socialLinks.telegram || '',
        preSale: tokenData.preSaleBnb || '0',
        // Fixed parameters that cannot be customized
        totalSupply: 1000000000,
        raisedAmount: 24,
        saleRate: 0.8,
        reserveRate: 0,
        funGroup: false,
        clickFun: false,
        symbol: 'BNB',
        raisedToken: raisedTokenConfig as any
      };

      console.log('Creating token signature with four.meme:', {
        name: createRequest.name,
        symbol: createRequest.shortName,
        description: createRequest.desc.substring(0, 50) + '...',
      });

      const response = await fetch(`${FOURMEME_API_BASE}/v1/private/token/create`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'meme-web-access': this.accessToken,
        },
        body: JSON.stringify(createRequest),
      });

      if (!response.ok) {
        throw new Error(`Token creation request failed: ${response.status} ${response.statusText}`);
      }

      const result: FourMemeTokenCreateResponse = await response.json();

      if (result.code !== '0') {
        throw new Error(`Four.meme token creation error: ${result.code}`);
      }

      console.log('Token signature created successfully');
      return result.data;
    } catch (error) {
      console.error('Token signature creation failed:', error);
      throw new Error(`Token signature creation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Deploy token to blockchain using TokenManager2 contract
   */
  async deployToken(
    walletPrivateKey: string,
    createArg: string,
    signature: string
  ): Promise<TokenCreationResult> {
    try {
      console.log('Starting token deployment to BSC...');

      // Get current network info
      const network = bscRpcClient.getCurrentNetwork();
      console.log(`Deploying on network: ${network.displayName}`);

      // Validate and format private key
      const validatedPrivateKey = validatePrivateKey(walletPrivateKey);

      // Create wallet instance with QuickNode provider
      const provider = new ethers.JsonRpcProvider(config.networks['bsc-testnet'].rpcUrl);
      const wallet = new ethers.Wallet(validatedPrivateKey, provider);

      console.log(`Deploying from wallet: ${wallet.address}`);

      // Create contract instance
      const tokenManagerABI = [
        'function createToken(bytes calldata createArg, bytes calldata sign) external payable',
        'event TokenCreate(address indexed creator, address indexed token, uint256 requestId, string name, string symbol, uint256 totalSupply, uint256 launchTime, uint256 launchFee)'
      ];

      const contract = new ethers.Contract(this.tokenManagerAddress, tokenManagerABI, wallet);

      // Convert hex strings to bytes
      const createArgBytes = ethers.getBytes(createArg);
      const signatureBytes = ethers.getBytes(signature);

      // Get gas price
      const gasPriceInfo = await bscRpcClient.getGasPriceInfo();

      // Estimate gas
      let gasLimit: bigint;
      try {
        gasLimit = await contract.createToken.estimateGas(createArgBytes, signatureBytes, {
          value: ethers.parseEther('0.005') // 0.005 BNB creation fee
        });
        gasLimit = gasLimit * BigInt(120) / BigInt(100); // Add 20% buffer
      } catch (gasError) {
        console.warn('Gas estimation failed, using default:', gasError);
        gasLimit = BigInt(500000); // Default gas limit
      }

      console.log('Transaction parameters:', {
        gasLimit: gasLimit.toString(),
        gasPrice: gasPriceInfo.standard,
        value: '0.005 BNB',
        to: this.tokenManagerAddress
      });

      // Send transaction
      const tx = await contract.createToken(createArgBytes, signatureBytes, {
        value: ethers.parseEther('0.005'),
        gasLimit,
        gasPrice: BigInt(gasPriceInfo.standard)
      });

      console.log(`Transaction sent: ${tx.hash}`);
      console.log(`View on BSCScan: ${network.blockExplorerUrl}/tx/${tx.hash}`);

      // Wait for confirmation
      const receipt = await tx.wait();

      if (!receipt) {
        throw new Error('Transaction receipt not available');
      }

      console.log(`Transaction confirmed in block ${receipt.blockNumber}`);

      // Parse TokenCreate event to get the new token address
      let tokenAddress = '';
      for (const log of receipt.logs) {
        try {
          const parsedLog = contract.interface.parseLog({
            topics: log.topics,
            data: log.data
          });

          if (parsedLog && parsedLog.name === 'TokenCreate') {
            tokenAddress = parsedLog.args.token;
            console.log(`New token created at: ${tokenAddress}`);
            break;
          }
        } catch (parseError) {
          // Continue to next log
        }
      }

      if (!tokenAddress) {
        throw new Error('Could not find TokenCreate event in transaction logs');
      }

      const result: TokenCreationResult = {
        contractAddress: tokenAddress,
        transactionHash: tx.hash,
        blockNumber: receipt.blockNumber,
        gasUsed: receipt.gasUsed.toString(),
        status: receipt.status === 1 ? 'success' : 'failed',
        fourMemeUrl: `https://four.meme/token/${tokenAddress}`
      };

      console.log('Token deployment successful:', result);
      return result;
    } catch (error) {
      console.error('Token deployment failed:', error);
      throw new Error(`Token deployment failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Complete token creation flow with enhanced progress tracking
   */
  async createToken(tokenData: {
    name: string;
    symbol: string;
    description: string;
    imageFile: File | null;
    socialLinks: {
      website?: string;
      twitter?: string;
      telegram?: string;
    };
    launchTime?: number;
    preSaleBnb?: string;
  }, walletPrivateKey: string, progressCallback?: (step: string, progress: number) => void): Promise<TokenCreationResult> {
    try {
      console.log('Starting complete token creation flow...');
      progressCallback?.('Initializing...', 10);

      // Step 1: Login to four.meme
      console.log('Authenticating with four.meme...');
      progressCallback?.('Authenticating with four.meme...', 20);
      try {
        await this.login(walletPrivateKey);
      } catch (authError) {
        throw new Error(`Authentication failed: ${authError instanceof Error ? authError.message : 'Unknown authentication error'}`);
      }

      // Step 2: Upload image if provided
      let imageUrl = '';
      if (tokenData.imageFile) {
        console.log('Uploading token image...');
        progressCallback?.('Uploading token image...', 35);
        try {
          imageUrl = await this.uploadImage(tokenData.imageFile);
        } catch (uploadError) {
          console.warn('Image upload failed, continuing without image:', uploadError);
          // Continue without image rather than failing completely
        }
      }

      // Step 3: Create token signature
      console.log('Getting token creation signature...');
      progressCallback?.('Preparing token deployment...', 50);
      let createArg: string;
      let signature: string;
      try {
        const result = await this.createTokenSignature({
          ...tokenData,
          imageUrl
        });
        createArg = result.createArg;
        signature = result.signature;
      } catch (signatureError) {
        throw new Error(`Token signature creation failed: ${signatureError instanceof Error ? signatureError.message : 'Unknown signature error'}`);
      }

      // Step 4: Deploy to blockchain
      console.log('Deploying token to blockchain...');
      progressCallback?.('Deploying to blockchain...', 75);
      let deployResult: TokenCreationResult;
      try {
        deployResult = await this.deployToken(walletPrivateKey, createArg, signature);
      } catch (deployError) {
        throw new Error(`Blockchain deployment failed: ${deployError instanceof Error ? deployError.message : 'Unknown deployment error'}`);
      }

      // Step 5: Monitor transaction completion
      progressCallback?.('Confirming transaction...', 90);
      try {
        // Add extra verification by checking the transaction status
        const monitorResult = await this.monitorTransaction(deployResult.transactionHash);
        if (monitorResult && monitorResult.status === 'failed') {
          throw new Error('Transaction failed on blockchain');
        }
      } catch (monitorError) {
        console.warn('Transaction monitoring failed, but deployment may have succeeded:', monitorError);
        // Don't fail the entire process if monitoring fails
      }

      progressCallback?.('Token creation completed!', 100);
      console.log('Token creation flow completed successfully:', deployResult);
      return deployResult;
    } catch (error) {
      console.error('Token creation flow failed:', error);

      // Enhanced error handling with specific error types
      if (error instanceof Error) {
        if (error.message.includes('authentication') || error.message.includes('login')) {
          throw new Error('Failed to authenticate with four.meme. Please check your wallet and try again.');
        } else if (error.message.includes('image') || error.message.includes('upload')) {
          throw new Error('Image upload failed. You can try again or proceed without an image.');
        } else if (error.message.includes('signature')) {
          throw new Error('Failed to prepare token for deployment. Please check your token details and try again.');
        } else if (error.message.includes('deployment') || error.message.includes('blockchain')) {
          throw new Error('Blockchain deployment failed. Please check your wallet balance and network connection.');
        } else if (error.message.includes('network') || error.message.includes('fetch')) {
          throw new Error('Network error. Please check your internet connection and try again.');
        }
      }

      throw error;
    }
  }

  /**
   * Monitor transaction status
   */
  async monitorTransaction(txHash: string): Promise<TokenCreationResult | null> {
    try {
      console.log(`Monitoring transaction: ${txHash}`);

      const receipt = await bscRpcClient.waitForTransaction(txHash, 1, 120000);

      if (!receipt) {
        throw new Error('Transaction not found or timed out');
      }

      // Parse logs to get token address
      const tokenManagerABI = [
        'event TokenCreate(address indexed creator, address indexed token, uint256 requestId, string name, string symbol, uint256 totalSupply, uint256 launchTime, uint256 launchFee)'
      ];

      const iface = new ethers.Interface(tokenManagerABI);
      let tokenAddress = '';

      for (const log of receipt.logs) {
        try {
          const parsedLog = iface.parseLog({
            topics: log.topics,
            data: log.data
          });

          if (parsedLog && parsedLog.name === 'TokenCreate') {
            tokenAddress = parsedLog.args.token;
            break;
          }
        } catch (parseError) {
          // Continue to next log
        }
      }

      return {
        contractAddress: tokenAddress,
        transactionHash: txHash,
        blockNumber: receipt.blockNumber,
        gasUsed: receipt.gasUsed.toString(),
        status: receipt.status === 1 ? 'success' : 'failed',
        fourMemeUrl: tokenAddress ? `https://four.meme/token/${tokenAddress}` : undefined
      };
    } catch (error) {
      console.error('Transaction monitoring failed:', error);
      return null;
    }
  }
}

// Export singleton instance
export const fourMemeClient = new FourMemeClient();
export default fourMemeClient;

src/components/StealthFunding.css.content:
/**
 * StealthFunding Component Styles
 * Professional styling for the stealth wallet funding interface
 */

.stealth-funding {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
  border-radius: 12px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  color: #ffffff;
  font-family: 'Inter', sans-serif;
}

/* Header Styles */
.stealth-funding-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  padding-bottom: 20px;
  border-bottom: 2px solid rgba(255, 255, 255, 0.1);
}

.stealth-funding-header h3 {
  margin: 0;
  font-size: 1.8rem;
  font-weight: 600;
  color: #ffffff;
  display: flex;
  align-items: center;
  gap: 12px;
}

.stealth-funding-header h3 i {
  color: #4ade80;
  font-size: 1.6rem;
}

.header-actions {
  display: flex;
  gap: 12px;
}

.refresh-btn, .clear-error-btn {
  padding: 8px 16px;
  background: rgba(74, 222, 128, 0.1);
  border: 1px solid #4ade80;
  border-radius: 6px;
  color: #4ade80;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.9rem;
  display: flex;
  align-items: center;
  gap: 8px;
}

.refresh-btn:hover, .clear-error-btn:hover {
  background: rgba(74, 222, 128, 0.2);
  transform: translateY(-1px);
}

.refresh-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Error Banner */
.error-banner {
  background: linear-gradient(135deg, #dc2626 0%, #991b1b 100%);
  border: 1px solid #ef4444;
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 20px;
  display: flex;
  align-items: center;
  gap: 12px;
  color: #ffffff;
  font-weight: 500;
}

.error-banner i {
  font-size: 1.2rem;
  color: #fef2f2;
}

/* Locked State */
.stealth-funding-locked {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 400px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  border: 2px dashed rgba(255, 255, 255, 0.2);
}

.lock-container {
  text-align: center;
  max-width: 300px;
}

.lock-icon {
  font-size: 3rem;
  color: #6b7280;
  margin-bottom: 20px;
}

.lock-container h3 {
  color: #ffffff;
  margin-bottom: 12px;
  font-size: 1.4rem;
}

.lock-container p {
  color: #9ca3af;
  margin-bottom: 0;
  line-height: 1.6;
}

/* Tab Navigation */
.stealth-tabs {
  display: flex;
  gap: 4px;
  margin-bottom: 30px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  padding: 4px;
}

.tab-button {
  flex: 1;
  padding: 12px 20px;
  background: transparent;
  border: none;
  border-radius: 6px;
  color: #9ca3af;
  cursor: pointer;
  transition: all 0.3s ease;
  font-weight: 500;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  font-size: 0.9rem;
}

.tab-button:hover {
  color: #ffffff;
  background: rgba(255, 255, 255, 0.1);
}

.tab-button.active {
  background: linear-gradient(135deg, #4ade80 0%, #22c55e 100%);
  color: #ffffff;
  box-shadow: 0 2px 8px rgba(74, 222, 128, 0.3);
}

.tab-button:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

.tab-button i {
  font-size: 1rem;
}

/* Tab Content */
.tab-content {
  min-height: 500px;
}

/* Configure Tab */
.configure-tab {
  display: flex;
  flex-direction: column;
  gap: 30px;
}

.quick-stats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
  margin-bottom: 10px;
}

.stat {
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  padding: 16px;
  text-align: center;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.stat label {
  display: block;
  color: #9ca3af;
  font-size: 0.85rem;
  margin-bottom: 8px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.stat-value {
  font-size: 1.4rem;
  font-weight: 600;
  color: #4ade80;
}

/* Configuration Sections */
.config-section {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.config-section h4 {
  margin: 0 0 20px 0;
  color: #ffffff;
  font-size: 1.1rem;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 8px;
}

/* Master Wallet Selection */
.master-wallet-selection {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.master-wallet-select {
  width: 100%;
  padding: 12px 16px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 6px;
  color: #ffffff;
  font-size: 0.95rem;
}

.master-wallet-select option {
  background: #1a1a2e;
  color: #ffffff;
}

.master-wallet-info {
  font-size: 0.9rem;
  padding: 8px 12px;
  background: rgba(74, 222, 128, 0.1);
  border-radius: 6px;
  border: 1px solid rgba(74, 222, 128, 0.3);
}

.master-wallet-info .sufficient {
  color: #4ade80;
}

.master-wallet-info .insufficient {
  color: #ef4444;
}

.master-wallet-info .warning {
  font-weight: 600;
}

/* Amount Configuration */
.amount-config {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.input-group {
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.input-group label {
  color: #e5e7eb;
  font-size: 0.9rem;
  font-weight: 500;
}

.amount-input, .delay-input, .batch-input {
  padding: 10px 14px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 6px;
  color: #ffffff;
  font-size: 0.95rem;
  transition: border-color 0.3s ease;
}

.amount-input:focus, .delay-input:focus, .batch-input:focus {
  outline: none;
  border-color: #4ade80;
  box-shadow: 0 0 0 3px rgba(74, 222, 128, 0.1);
}

.amount-mode {
  margin: 8px 0;
}

.checkbox-label {
  display: flex;
  align-items: center;
  gap: 8px;
  color: #e5e7eb;
  cursor: pointer;
  font-size: 0.9rem;
}

.checkbox-label input[type="checkbox"] {
  width: 16px;
  height: 16px;
  accent-color: #4ade80;
}

.amount-range {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
}

/* Preset Buttons */
.preset-buttons {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
}

.preset-btn {
  padding: 16px;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  color: #ffffff;
  cursor: pointer;
  transition: all 0.3s ease;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
}

.preset-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.2);
}

.preset-btn.conservative {
  border-color: #3b82f6;
}

.preset-btn.conservative:hover {
  background: rgba(59, 130, 246, 0.1);
  border-color: #60a5fa;
}

.preset-btn.moderate {
  border-color: #f59e0b;
}

.preset-btn.moderate:hover {
  background: rgba(245, 158, 11, 0.1);
  border-color: #fbbf24;
}

.preset-btn.aggressive {
  border-color: #ef4444;
}

.preset-btn.aggressive:hover {
  background: rgba(239, 68, 68, 0.1);
  border-color: #f87171;
}

.preset-btn i {
  font-size: 1.4rem;
}

.preset-btn small {
  font-size: 0.8rem;
  color: #9ca3af;
}

/* Timing Configuration */
.timing-config {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.timing-pattern {
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.pattern-select {
  padding: 10px 14px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 6px;
  color: #ffffff;
  font-size: 0.95rem;
}

.pattern-select option {
  background: #1a1a2e;
  color: #ffffff;
}

.delay-range {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
}

/* Advanced Settings */
.advanced-toggle {
  margin-bottom: 16px;
}

.toggle-btn {
  padding: 10px 16px;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 6px;
  color: #e5e7eb;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 0.9rem;
}

.toggle-btn:hover {
  background: rgba(255, 255, 255, 0.1);
}

.advanced-settings {
  display: flex;
  flex-direction: column;
  gap: 16px;
  padding-top: 16px;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.setting-group {
  margin-bottom: 8px;
}

.batch-config {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
  margin-top: 12px;
}

/* Action Buttons */
.config-actions, .preview-actions, .execute-actions {
  display: flex;
  justify-content: center;
  gap: 16px;
  margin-top: 20px;
}

.create-plan-btn, .proceed-btn, .execute-btn {
  padding: 14px 28px;
  background: linear-gradient(135deg, #4ade80 0%, #22c55e 100%);
  border: none;
  border-radius: 8px;
  color: #ffffff;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 1rem;
}

.create-plan-btn:hover, .proceed-btn:hover, .execute-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 16px rgba(74, 222, 128, 0.3);
}

.create-plan-btn:disabled, .proceed-btn:disabled, .execute-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  transform: none;
}

.back-btn, .cancel-btn {
  padding: 14px 28px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 8px;
  color: #e5e7eb;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
}

.back-btn:hover, .cancel-btn:hover {
  background: rgba(255, 255, 255, 0.15);
}

/* Preview Tab */
.preview-tab {
  display: flex;
  flex-direction: column;
  gap: 30px;
}

.plan-summary {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.summary-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
  margin-top: 16px;
}

.summary-item {
  text-align: center;
  padding: 16px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
}

.summary-item label {
  display: block;
  color: #9ca3af;
  font-size: 0.85rem;
  margin-bottom: 8px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.summary-value {
  font-size: 1.2rem;
  font-weight: 600;
  color: #4ade80;
}

/* Validation Results */
.validation-results {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.validation-status {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 16px;
  border-radius: 8px;
  margin-bottom: 20px;
  font-weight: 500;
}

.validation-status.valid {
  background: rgba(34, 197, 94, 0.1);
  border: 1px solid #22c55e;
  color: #4ade80;
}

.validation-status.invalid {
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid #ef4444;
  color: #f87171;
}

.validation-errors, .validation-warnings, .validation-recommendations {
  margin-bottom: 16px;
}

.validation-errors h5 {
  color: #ef4444;
  margin-bottom: 8px;
}

.validation-warnings h5 {
  color: #f59e0b;
  margin-bottom: 8px;
}

.validation-recommendations h5 {
  color: #3b82f6;
  margin-bottom: 8px;
}

.error-item, .warning-item, .recommendation-item {
  display: flex;
  align-items: flex-start;
  gap: 8px;
  margin-bottom: 8px;
  font-size: 0.9rem;
  line-height: 1.5;
}

.error-item {
  color: #fca5a5;
}

.warning-item {
  color: #fcd34d;
}

.recommendation-item {
  color: #93c5fd;
}

/* Transaction Preview */
.transaction-preview {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.transaction-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-top: 16px;
}

.transaction-item {
  display: grid;
  grid-template-columns: 40px 1fr auto auto;
  gap: 16px;
  align-items: center;
  padding: 12px 16px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 6px;
  font-size: 0.9rem;
}

.tx-index {
  color: #9ca3af;
  font-weight: 500;
}

.tx-address {
  color: #e5e7eb;
  font-family: 'JetBrains Mono', monospace;
  font-size: 0.85rem;
}

.tx-amount {
  color: #4ade80;
  font-weight: 600;
}

.tx-delay {
  color: #f59e0b;
  font-size: 0.85rem;
}

.more-transactions {
  text-align: center;
  color: #9ca3af;
  font-style: italic;
  padding: 12px;
}

/* Execute Tab */
.execute-tab {
  display: flex;
  flex-direction: column;
  gap: 30px;
}

.security-warning {
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid #ef4444;
  border-radius: 10px;
  padding: 24px;
  display: flex;
  gap: 16px;
}

.security-warning i {
  font-size: 1.5rem;
  color: #f87171;
  margin-top: 4px;
}

.warning-content h4 {
  color: #f87171;
  margin: 0 0 12px 0;
  font-size: 1.1rem;
}

.warning-content p {
  color: #fca5a5;
  margin-bottom: 8px;
  line-height: 1.6;
}

.passphrase-section {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.passphrase-input {
  width: 100%;
  padding: 14px 16px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 8px;
  color: #ffffff;
  font-size: 1rem;
  margin-top: 12px;
}

.passphrase-input:focus {
  outline: none;
  border-color: #4ade80;
  box-shadow: 0 0 0 3px rgba(74, 222, 128, 0.1);
}

.final-summary {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.summary-items {
  display: flex;
  flex-direction: column;
  gap: 12px;
  margin-top: 16px;
}

.summary-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 8px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.summary-row:last-child {
  border-bottom: none;
}

.summary-row span:first-child {
  color: #9ca3af;
  font-weight: 500;
}

.summary-row span:last-child {
  color: #e5e7eb;
  font-weight: 600;
  font-family: 'JetBrains Mono', monospace;
}

/* Monitor Tab */
.monitor-tab {
  display: flex;
  flex-direction: column;
  gap: 30px;
}

.operation-status {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.status-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}

.status-badge {
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 0.85rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  display: flex;
  align-items: center;
  gap: 8px;
}

.status-badge.executing {
  background: rgba(59, 130, 246, 0.1);
  color: #60a5fa;
  border: 1px solid #3b82f6;
}

.status-badge.completed {
  background: rgba(34, 197, 94, 0.1);
  color: #4ade80;
  border: 1px solid #22c55e;
}

.status-badge.failed {
  background: rgba(239, 68, 68, 0.1);
  color: #f87171;
  border: 1px solid #ef4444;
}

.status-badge.paused {
  background: rgba(245, 158, 11, 0.1);
  color: #fbbf24;
  border: 1px solid #f59e0b;
}

/* Progress Bar */
.progress-section {
  margin-bottom: 20px;
}

.progress-bar {
  width: 100%;
  height: 8px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 4px;
  overflow: hidden;
  margin-bottom: 8px;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #4ade80 0%, #22c55e 100%);
  border-radius: 4px;
  transition: width 0.3s ease;
}

.progress-text {
  text-align: center;
  color: #e5e7eb;
  font-size: 0.9rem;
  font-weight: 500;
}

.time-estimates {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;
  margin-top: 16px;
}

.time-item {
  text-align: center;
  padding: 12px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 6px;
}

.time-item label {
  display: block;
  color: #9ca3af;
  font-size: 0.85rem;
  margin-bottom: 4px;
}

.time-value {
  font-size: 1.1rem;
  font-weight: 600;
  color: #4ade80;
}

/* Statistics */
.operation-statistics {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.stats-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 16px;
  margin-top: 16px;
}

.stat-item {
  text-align: center;
  padding: 16px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
}

.stat-item label {
  display: block;
  color: #9ca3af;
  font-size: 0.85rem;
  margin-bottom: 8px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* Recent Events */
.recent-events {
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  padding: 24px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.events-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-top: 16px;
  max-height: 300px;
  overflow-y: auto;
}

.event-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 16px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 6px;
  border-left: 3px solid #4ade80;
  font-size: 0.9rem;
}

.event-time {
  color: #9ca3af;
  font-size: 0.8rem;
  min-width: 80px;
}

.event-message {
  display: flex;
  align-items: center;
  gap: 8px;
  color: #e5e7eb;
}

.text-success {
  color: #4ade80;
}

.text-error {
  color: #f87171;
}

/* Control Actions */
.control-actions {
  display: flex;
  justify-content: center;
  gap: 16px;
  padding: 20px;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 10px;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.pause-btn, .resume-btn {
  padding: 12px 24px;
  background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
  border: none;
  border-radius: 6px;
  color: #ffffff;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
}

.pause-btn:hover, .resume-btn:hover {
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(245, 158, 11, 0.3);
}

/* Confirmation Dialog */
.confirmation-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
}

.confirmation-dialog {
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
  border-radius: 12px;
  padding: 30px;
  max-width: 500px;
  width: 90%;
  border: 1px solid rgba(255, 255, 255, 0.1);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.5);
}

.dialog-header {
  margin-bottom: 20px;
}

.dialog-header h3 {
  color: #ffffff;
  margin: 0;
  display: flex;
  align-items: center;
  gap: 12px;
  font-size: 1.3rem;
}

.dialog-header i {
  color: #f59e0b;
}

.dialog-content p {
  color: #e5e7eb;
  margin-bottom: 16px;
  line-height: 1.6;
}

.confirmation-details {
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  padding: 16px;
  margin-bottom: 20px;
}

.confirmation-details div {
  color: #9ca3af;
  margin-bottom: 6px;
  font-size: 0.9rem;
}

.confirmation-details div:last-child {
  margin-bottom: 0;
}

.dialog-actions {
  display: flex;
  justify-content: flex-end;
  gap: 12px;
}

.confirm-btn {
  padding: 12px 24px;
  background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
  border: none;
  border-radius: 6px;
  color: #ffffff;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
}

.confirm-btn:hover {
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(239, 68, 68, 0.3);
}

/* Responsive Design */
@media (max-width: 768px) {
  .stealth-funding {
    padding: 16px;
  }

  .stealth-funding-header {
    flex-direction: column;
    gap: 16px;
    text-align: center;
  }

  .stealth-tabs {
    flex-direction: column;
  }

  .quick-stats {
    grid-template-columns: 1fr;
  }

  .summary-grid {
    grid-template-columns: 1fr;
  }

  .amount-range, .delay-range, .batch-config {
    grid-template-columns: 1fr;
  }

  .preset-buttons {
    grid-template-columns: 1fr;
  }

  .time-estimates, .stats-grid {
    grid-template-columns: 1fr;
  }

  .config-actions, .preview-actions, .execute-actions {
    flex-direction: column;
  }

  .confirmation-dialog {
    margin: 20px;
    width: auto;
  }
}

src/components/StealthFunding.tsx.content:
/**
 * StealthFunding Component
 * UI component for configuring and executing stealth wallet funding operations
 * Provides comprehensive controls for stealth distribution with real-time progress tracking
 */

import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useWalletFundingStore } from '../store/wallet-funding';
import { useWalletStore } from '../store/wallets';
import { useSessionStore } from '../store/session';
import type {
  StealthConfig,
  StealthFundingPlan,
  MasterWallet,
  StealthPattern,
  StealthOperationEvent,
  StealthTransaction
} from '../types/funding';
import './StealthFunding.css';

interface StealthFundingProps {
  onOperationComplete?: (result: any) => void;
  onError?: (error: string) => void;
}

export const StealthFunding: React.FC<StealthFundingProps> = ({
  onOperationComplete,
  onError
}) => {
  // Store hooks
  const {
    currentOperation,
    isExecuting,
    isPaused,
    error: storeError,
    preferences,
    availableMasterWallets,
    recentEvents,
    createStealthPlan,
    validatePlan,
    executeStealthOperationWithPlan,
    pauseOperation,
    resumeOperation,
    cancelOperation,
    loadAvailableMasterWallets,
    refreshMasterWalletBalances,
    updatePreferences,
    generateStealthConfig,
    clearError,
    getCurrentOperationStatus
  } = useWalletFundingStore();

  const { selectedWallets, wallets } = useWalletStore();
  const { isUnlocked } = useSessionStore();

  // Local state
  const [activeTab, setActiveTab] = useState<'configure' | 'preview' | 'execute' | 'monitor'>('configure');
  const [config, setConfig] = useState<StealthConfig>(preferences.defaultStealthConfig);
  const [selectedMasterWallet, setSelectedMasterWallet] = useState<string>('');
  const [totalAmount, setTotalAmount] = useState<number>(0.1);
  const [currentPlan, setCurrentPlan] = useState<StealthFundingPlan | null>(null);
  const [validationResult, setValidationResult] = useState<any>(null);
  const [showAdvancedSettings, setShowAdvancedSettings] = useState(false);
  const [passphrase, setPassphrase] = useState<string>('');
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);

  // Load master wallets on component mount
  useEffect(() => {
    if (isUnlocked) {
      loadAvailableMasterWallets();
    }
  }, [isUnlocked, loadAvailableMasterWallets]);

  // Auto-select first available master wallet
  useEffect(() => {
    if (availableMasterWallets.length > 0 && !selectedMasterWallet) {
      setSelectedMasterWallet(availableMasterWallets[0].id);
    }
  }, [availableMasterWallets, selectedMasterWallet]);

  // Calculate selected wallet details
  const selectedWalletDetails = useMemo(() => {
    return wallets.filter(w => selectedWallets.includes(w.id));
  }, [wallets, selectedWallets]);

  // Handle configuration changes
  const handleConfigChange = useCallback((updates: Partial<StealthConfig>) => {
    setConfig(prev => ({ ...prev, ...updates }));
  }, []);

  // Apply preset configuration
  const applyPreset = useCallback((preset: 'conservative' | 'moderate' | 'aggressive') => {
    const presetConfig = generateStealthConfig(preset);
    setConfig(presetConfig);
  }, [generateStealthConfig]);

  // Create stealth plan
  const createPlan = useCallback(async () => {
    try {
      if (!selectedMasterWallet) {
        throw new Error('Please select a master wallet');
      }

      if (selectedWallets.length === 0) {
        throw new Error('Please select target wallets');
      }

      const masterWallet = availableMasterWallets.find(w => w.id === selectedMasterWallet);
      if (!masterWallet) {
        throw new Error('Selected master wallet not found');
      }

      const plan = await createStealthPlan(
        selectedMasterWallet,
        selectedWallets,
        totalAmount,
        config
      );

      const validation = validatePlan(plan);
      setValidationResult(validation);
      setCurrentPlan(plan);
      setActiveTab('preview');

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Failed to create plan';
      onError?.(errorMessage);
    }
  }, [
    selectedMasterWallet,
    selectedWallets,
    totalAmount,
    config,
    availableMasterWallets,
    createStealthPlan,
    validatePlan,
    onError
  ]);

  // Execute stealth operation
  const executeOperation = useCallback(async () => {
    if (!currentPlan || !passphrase) {
      return;
    }

    try {
      setShowConfirmDialog(false);
      setActiveTab('monitor');

      await executeStealthOperationWithPlan(currentPlan, passphrase);
      onOperationComplete?.(currentOperation);

    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Operation failed';
      onError?.(errorMessage);
    } finally {
      setPassphrase(''); // Clear passphrase
    }
  }, [currentPlan, passphrase, executeStealthOperationWithPlan, currentOperation, onOperationComplete, onError]);

  // Format time duration
  const formatDuration = useCallback((seconds: number): string => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m ${secs}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  }, []);

  // Get operation status
  const operationStatus = getCurrentOperationStatus();

  // Session check
  if (!isUnlocked) {
    return (
      <div className="stealth-funding-locked">
        <div className="lock-container">
          <div className="lock-icon">
            <i className="fas fa-shield-alt"></i>
          </div>
          <h3>Session Required</h3>
          <p>Please unlock your session to access stealth funding operations.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="stealth-funding">
      {/* Header */}
      <div className="stealth-funding-header">
        <h3>
          <i className="fas fa-user-secret"></i>
          Stealth Wallet Funding
        </h3>
        <div className="header-actions">
          <button
            className="refresh-btn"
            onClick={refreshMasterWalletBalances}
            disabled={isExecuting}
          >
            <i className="fas fa-sync-alt"></i>
            Refresh
          </button>
          {storeError && (
            <button className="clear-error-btn" onClick={clearError}>
              <i className="fas fa-times"></i>
              Clear Error
            </button>
          )}
        </div>
      </div>

      {/* Error Display */}
      {storeError && (
        <div className="error-banner">
          <i className="fas fa-exclamation-triangle"></i>
          <span>{storeError}</span>
        </div>
      )}

      {/* Tab Navigation */}
      <div className="stealth-tabs">
        <button
          className={`tab-button ${activeTab === 'configure' ? 'active' : ''}`}
          onClick={() => setActiveTab('configure')}
          disabled={isExecuting}
        >
          <i className="fas fa-cogs"></i>
          Configure
        </button>
        <button
          className={`tab-button ${activeTab === 'preview' ? 'active' : ''}`}
          onClick={() => setActiveTab('preview')}
          disabled={!currentPlan || isExecuting}
        >
          <i className="fas fa-eye"></i>
          Preview
        </button>
        <button
          className={`tab-button ${activeTab === 'execute' ? 'active' : ''}`}
          onClick={() => setActiveTab('execute')}
          disabled={!currentPlan || !validationResult?.isValid || isExecuting}
        >
          <i className="fas fa-play"></i>
          Execute
        </button>
        <button
          className={`tab-button ${activeTab === 'monitor' ? 'active' : ''}`}
          onClick={() => setActiveTab('monitor')}
          disabled={!currentOperation}
        >
          <i className="fas fa-chart-line"></i>
          Monitor
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {/* Configuration Tab */}
        {activeTab === 'configure' && (
          <div className="configure-tab">
            {/* Quick Stats */}
            <div className="quick-stats">
              <div className="stat">
                <label>Selected Wallets</label>
                <span className="stat-value">{selectedWallets.length}</span>
              </div>
              <div className="stat">
                <label>Total Amount</label>
                <span className="stat-value">{totalAmount} BNB</span>
              </div>
              <div className="stat">
                <label>Per Wallet</label>
                <span className="stat-value">
                  {config.useFixedAmount 
                    ? `${config.fixedAmount} BNB` 
                    : `${config.minAmount}-${config.maxAmount} BNB`
                  }
                </span>
              </div>
            </div>

            {/* Master Wallet Selection */}
            <div className="config-section">
              <h4>Master Wallet</h4>
              <div className="master-wallet-selection">
                <select
                  value={selectedMasterWallet}
                  onChange={(e) => setSelectedMasterWallet(e.target.value)}
                  className="master-wallet-select"
                >
                  <option value="">Select master wallet...</option>
                  {availableMasterWallets.map(wallet => (
                    <option key={wallet.id} value={wallet.id}>
                      {wallet.alias} - {wallet.balance.toFixed(4)} BNB
                    </option>
                  ))}
                </select>
                {selectedMasterWallet && (
                  <div className="master-wallet-info">
                    {(() => {
                      const wallet = availableMasterWallets.find(w => w.id === selectedMasterWallet);
                      return wallet ? (
                        <span className={wallet.balance >= totalAmount ? 'sufficient' : 'insufficient'}>
                          Balance: {wallet.balance.toFixed(4)} BNB
                          {wallet.balance < totalAmount && (
                            <span className="warning"> (Insufficient)</span>
                          )}
                        </span>
                      ) : null;
                    })()}
                  </div>
                )}
              </div>
            </div>

            {/* Amount Configuration */}
            <div className="config-section">
              <h4>Distribution Amount</h4>
              <div className="amount-config">
                <div className="input-group">
                  <label>Total Amount (BNB)</label>
                  <input
                    type="number"
                    value={totalAmount}
                    onChange={(e) => setTotalAmount(parseFloat(e.target.value) || 0)}
                    min="0"
                    step="0.001"
                    className="amount-input"
                  />
                </div>

                <div className="amount-mode">
                  <label className="checkbox-label">
                    <input
                      type="checkbox"
                      checked={config.useFixedAmount}
                      onChange={(e) => handleConfigChange({ useFixedAmount: e.target.checked })}
                    />
                    Use fixed amount per wallet
                  </label>
                </div>

                {config.useFixedAmount ? (
                  <div className="input-group">
                    <label>Fixed Amount per Wallet (BNB)</label>
                    <input
                      type="number"
                      value={config.fixedAmount || 0}
                      onChange={(e) => handleConfigChange({ fixedAmount: parseFloat(e.target.value) || 0 })}
                      min="0"
                      step="0.001"
                      className="amount-input"
                    />
                  </div>
                ) : (
                  <div className="amount-range">
                    <div className="input-group">
                      <label>Min Amount (BNB)</label>
                      <input
                        type="number"
                        value={config.minAmount}
                        onChange={(e) => handleConfigChange({ minAmount: parseFloat(e.target.value) || 0 })}
                        min="0"
                        step="0.001"
                        className="amount-input"
                      />
                    </div>
                    <div className="input-group">
                      <label>Max Amount (BNB)</label>
                      <input
                        type="number"
                        value={config.maxAmount}
                        onChange={(e) => handleConfigChange({ maxAmount: parseFloat(e.target.value) || 0 })}
                        min="0"
                        step="0.001"
                        className="amount-input"
                      />
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Stealth Configuration Presets */}
            <div className="config-section">
              <h4>Stealth Presets</h4>
              <div className="preset-buttons">
                <button
                  className="preset-btn conservative"
                  onClick={() => applyPreset('conservative')}
                >
                  <i className="fas fa-shield-alt"></i>
                  Conservative
                  <small>High stealth, slower execution</small>
                </button>
                <button
                  className="preset-btn moderate"
                  onClick={() => applyPreset('moderate')}
                >
                  <i className="fas fa-balance-scale"></i>
                  Moderate
                  <small>Balanced stealth and speed</small>
                </button>
                <button
                  className="preset-btn aggressive"
                  onClick={() => applyPreset('aggressive')}
                >
                  <i className="fas fa-bolt"></i>
                  Aggressive
                  <small>Fast execution, lower stealth</small>
                </button>
              </div>
            </div>

            {/* Timing Configuration */}
            <div className="config-section">
              <h4>Timing Configuration</h4>
              <div className="timing-config">
                <div className="timing-pattern">
                  <label>Pattern</label>
                  <select
                    value={config.pattern}
                    onChange={(e) => handleConfigChange({ pattern: e.target.value as StealthPattern })}
                    className="pattern-select"
                  >
                    <option value="natural">Natural (Human-like)</option>
                    <option value="random">Random</option>
                    <option value="uniform">Uniform</option>
                    <option value="burst">Burst</option>
                    <option value="gradient">Gradient</option>
                  </select>
                </div>

                <div className="delay-range">
                  <div className="input-group">
                    <label>Min Delay (seconds)</label>
                    <input
                      type="number"
                      value={config.minDelay}
                      onChange={(e) => handleConfigChange({ minDelay: parseInt(e.target.value) || 1 })}
                      min="1"
                      className="delay-input"
                    />
                  </div>
                  <div className="input-group">
                    <label>Max Delay (seconds)</label>
                    <input
                      type="number"
                      value={config.maxDelay}
                      onChange={(e) => handleConfigChange({ maxDelay: parseInt(e.target.value) || 1 })}
                      min="1"
                      className="delay-input"
                    />
                  </div>
                </div>
              </div>
            </div>

            {/* Advanced Settings */}
            <div className="config-section">
              <div className="advanced-toggle">
                <button
                  className="toggle-btn"
                  onClick={() => setShowAdvancedSettings(!showAdvancedSettings)}
                >
                  <i className={`fas ${showAdvancedSettings ? 'fa-chevron-up' : 'fa-chevron-down'}`}></i>
                  Advanced Settings
                </button>
              </div>

              {showAdvancedSettings && (
                <div className="advanced-settings">
                  <div className="setting-group">
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        checked={config.randomizeOrder}
                        onChange={(e) => handleConfigChange({ randomizeOrder: e.target.checked })}
                      />
                      Randomize wallet order
                    </label>
                  </div>

                  <div className="setting-group">
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        checked={config.simulateHumanBehavior}
                        onChange={(e) => handleConfigChange({ simulateHumanBehavior: e.target.checked })}
                      />
                      Simulate human behavior
                    </label>
                  </div>

                  <div className="setting-group">
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        checked={config.varyTransactionSizes}
                        onChange={(e) => handleConfigChange({ varyTransactionSizes: e.target.checked })}
                      />
                      Vary transaction amounts
                    </label>
                  </div>

                  <div className="setting-group">
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        checked={config.useVariableGas}
                        onChange={(e) => handleConfigChange({ useVariableGas: e.target.checked })}
                      />
                      Use variable gas prices
                    </label>
                  </div>

                  <div className="batch-config">
                    <div className="input-group">
                      <label>Batch Size (0 = no batching)</label>
                      <input
                        type="number"
                        value={config.batchSize}
                        onChange={(e) => handleConfigChange({ batchSize: parseInt(e.target.value) || 0 })}
                        min="0"
                        className="batch-input"
                      />
                    </div>
                    <div className="input-group">
                      <label>Batch Delay (seconds)</label>
                      <input
                        type="number"
                        value={config.batchDelay}
                        onChange={(e) => handleConfigChange({ batchDelay: parseInt(e.target.value) || 0 })}
                        min="0"
                        className="batch-input"
                      />
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Create Plan Button */}
            <div className="config-actions">
              <button
                className="create-plan-btn"
                onClick={createPlan}
                disabled={!selectedMasterWallet || selectedWallets.length === 0 || totalAmount <= 0}
              >
                <i className="fas fa-magic"></i>
                Create Stealth Plan
              </button>
            </div>
          </div>
        )}

        {/* Preview Tab */}
        {activeTab === 'preview' && currentPlan && validationResult && (
          <div className="preview-tab">
            {/* Plan Summary */}
            <div className="plan-summary">
              <h4>Operation Summary</h4>
              <div className="summary-grid">
                <div className="summary-item">
                  <label>Target Wallets</label>
                  <span className="summary-value">{currentPlan.transactionCount}</span>
                </div>
                <div className="summary-item">
                  <label>Total Distribution</label>
                  <span className="summary-value">{currentPlan.totalAmount} BNB</span>
                </div>
                <div className="summary-item">
                  <label>Estimated Duration</label>
                  <span className="summary-value">{formatDuration(currentPlan.estimatedDuration)}</span>
                </div>
                <div className="summary-item">
                  <label>Pattern</label>
                  <span className="summary-value">{currentPlan.config.pattern}</span>
                </div>
              </div>
            </div>

            {/* Validation Results */}
            <div className="validation-results">
              <h4>Validation Results</h4>
              <div className={`validation-status ${validationResult.isValid ? 'valid' : 'invalid'}`}>
                <i className={`fas ${validationResult.isValid ? 'fa-check-circle' : 'fa-exclamation-triangle'}`}></i>
                {validationResult.isValid ? 'Plan is valid and ready for execution' : 'Plan has validation issues'}
              </div>

              {validationResult.errors.length > 0 && (
                <div className="validation-errors">
                  <h5>Errors:</h5>
                  <ul>
                    {validationResult.errors.map((error: string, index: number) => (
                      <li key={index} className="error-item">
                        <i className="fas fa-times-circle"></i>
                        {error}
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {validationResult.warnings.length > 0 && (
                <div className="validation-warnings">
                  <h5>Warnings:</h5>
                  <ul>
                    {validationResult.warnings.map((warning: string, index: number) => (
                      <li key={index} className="warning-item">
                        <i className="fas fa-exclamation-triangle"></i>
                        {warning}
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              {validationResult.recommendations.length > 0 && (
                <div className="validation-recommendations">
                  <h5>Recommendations:</h5>
                  <ul>
                    {validationResult.recommendations.map((recommendation: string, index: number) => (
                      <li key={index} className="recommendation-item">
                        <i className="fas fa-lightbulb"></i>
                        {recommendation}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>

            {/* Transaction Preview */}
            <div className="transaction-preview">
              <h4>Transaction Preview (First 5)</h4>
              <div className="transaction-list">
                {currentPlan.transactions.slice(0, 5).map((tx, index) => (
                  <div key={tx.id} className="transaction-item">
                    <div className="tx-index">#{index + 1}</div>
                    <div className="tx-address">{tx.walletAddress}</div>
                    <div className="tx-amount">{tx.actualAmount.toFixed(4)} BNB</div>
                    <div className="tx-delay">+{tx.executionDelay.toFixed(1)}s</div>
                  </div>
                ))}
                {currentPlan.transactions.length > 5 && (
                  <div className="more-transactions">
                    ... and {currentPlan.transactions.length - 5} more transactions
                  </div>
                )}
              </div>
            </div>

            {/* Preview Actions */}
            <div className="preview-actions">
              <button
                className="back-btn"
                onClick={() => setActiveTab('configure')}
              >
                <i className="fas fa-arrow-left"></i>
                Back to Configure
              </button>
              <button
                className="proceed-btn"
                onClick={() => setActiveTab('execute')}
                disabled={!validationResult.isValid}
              >
                <i className="fas fa-arrow-right"></i>
                Proceed to Execute
              </button>
            </div>
          </div>
        )}

        {/* Execute Tab */}
        {activeTab === 'execute' && currentPlan && (
          <div className="execute-tab">
            {/* Security Warning */}
            <div className="security-warning">
              <i className="fas fa-shield-alt"></i>
              <div className="warning-content">
                <h4>Security Confirmation Required</h4>
                <p>
                  You are about to execute a stealth funding operation that will distribute{' '}
                  <strong>{currentPlan.totalAmount} BNB</strong> across{' '}
                  <strong>{currentPlan.transactionCount} wallets</strong>.
                </p>
                <p>This operation cannot be undone once started.</p>
              </div>
            </div>

            {/* Passphrase Input */}
            <div className="passphrase-section">
              <h4>Enter Passphrase</h4>
              <p>Your passphrase is required to decrypt the master wallet private key.</p>
              <input
                type="password"
                value={passphrase}
                onChange={(e) => setPassphrase(e.target.value)}
                placeholder="Enter your passphrase..."
                className="passphrase-input"
                autoComplete="off"
              />
            </div>

            {/* Final Summary */}
            <div className="final-summary">
              <h4>Final Confirmation</h4>
              <div className="summary-items">
                <div className="summary-row">
                  <span>Master Wallet:</span>
                  <span>{currentPlan.masterWallet.address}</span>
                </div>
                <div className="summary-row">
                  <span>Total Amount:</span>
                  <span>{currentPlan.totalAmount} BNB</span>
                </div>
                <div className="summary-row">
                  <span>Target Wallets:</span>
                  <span>{currentPlan.transactionCount}</span>
                </div>
                <div className="summary-row">
                  <span>Estimated Time:</span>
                  <span>{formatDuration(currentPlan.estimatedDuration)}</span>
                </div>
                <div className="summary-row">
                  <span>Stealth Pattern:</span>
                  <span>{currentPlan.config.pattern}</span>
                </div>
              </div>
            </div>

            {/* Execute Actions */}
            <div className="execute-actions">
              <button
                className="cancel-btn"
                onClick={() => setActiveTab('preview')}
              >
                <i className="fas fa-arrow-left"></i>
                Back to Preview
              </button>
              <button
                className="execute-btn"
                onClick={() => setShowConfirmDialog(true)}
                disabled={!passphrase || passphrase.length < 8}
              >
                <i className="fas fa-rocket"></i>
                Execute Stealth Operation
              </button>
            </div>
          </div>
        )}

        {/* Monitor Tab */}
        {activeTab === 'monitor' && currentOperation && (
          <div className="monitor-tab">
            {/* Operation Status */}
            <div className="operation-status">
              <div className="status-header">
                <h4>Operation Status</h4>
                <div className={`status-badge ${currentOperation.status}`}>
                  {currentOperation.status === 'executing' && <i className="fas fa-spinner fa-spin"></i>}
                  {currentOperation.status === 'completed' && <i className="fas fa-check-circle"></i>}
                  {currentOperation.status === 'failed' && <i className="fas fa-exclamation-circle"></i>}
                  {currentOperation.status === 'paused' && <i className="fas fa-pause-circle"></i>}
                  {currentOperation.status}
                </div>
              </div>

              {/* Progress Bar */}
              <div className="progress-section">
                <div className="progress-bar">
                  <div 
                    className="progress-fill"
                    style={{ width: `${currentOperation.progress.percentage}%` }}
                  ></div>
                </div>
                <div className="progress-text">
                  {currentOperation.progress.completed} / {currentOperation.progress.total} transactions
                  ({currentOperation.progress.percentage.toFixed(1)}%)
                </div>
              </div>

              {/* Time Estimates */}
              <div className="time-estimates">
                <div className="time-item">
                  <label>Elapsed Time:</label>
                  <span className="time-value">
                    {currentOperation.startedAt && 
                      formatDuration((Date.now() - new Date(currentOperation.startedAt).getTime()) / 1000)
                    }
                  </span>
                </div>
                <div className="time-item">
                  <label>Estimated Remaining:</label>
                  <span className="time-value">{formatDuration(currentOperation.progress.estimatedTimeRemaining)}</span>
                </div>
              </div>
            </div>

            {/* Statistics */}
            <div className="operation-statistics">
              <h4>Statistics</h4>
              <div className="stats-grid">
                <div className="stat-item">
                  <label>Total Sent</label>
                  <span className="stat-value">{currentOperation.statistics.totalSent.toFixed(4)} BNB</span>
                </div>
                <div className="stat-item">
                  <label>Successful</label>
                  <span className="stat-value">{currentOperation.statistics.successfulTransactions}</span>
                </div>
                <div className="stat-item">
                  <label>Failed</label>
                  <span className="stat-value">{currentOperation.statistics.failedTransactions}</span>
                </div>
                <div className="stat-item">
                  <label>Gas Used</label>
                  <span className="stat-value">{currentOperation.statistics.gasUsed}</span>
                </div>
              </div>
            </div>

            {/* Recent Events */}
            <div className="recent-events">
              <h4>Recent Events</h4>
              <div className="events-list">
                {recentEvents.slice(0, 10).map((event, index) => (
                  <div key={index} className={`event-item ${event.type}`}>
                    <div className="event-time">
                      {new Date(event.timestamp).toLocaleTimeString()}
                    </div>
                    <div className="event-message">
                      {event.type === 'transaction_confirmed' && (
                        <>
                          <i className="fas fa-check text-success"></i>
                          Transaction confirmed
                        </>
                      )}
                      {event.type === 'transaction_failed' && (
                        <>
                          <i className="fas fa-times text-error"></i>
                          Transaction failed: {event.error}
                        </>
                      )}
                      {event.type === 'batch_completed' && (
                        <>
                          <i className="fas fa-layer-group"></i>
                          Batch {event.batchNumber} completed
                        </>
                      )}
                      {event.type === 'operation_started' && (
                        <>
                          <i className="fas fa-play"></i>
                          Operation started
                        </>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Control Actions */}
            {operationStatus.isRunning && (
              <div className="control-actions">
                {!isPaused ? (
                  <button
                    className="pause-btn"
                    onClick={() => pauseOperation(currentOperation.id)}
                  >
                    <i className="fas fa-pause"></i>
                    Pause Operation
                  </button>
                ) : (
                  <button
                    className="resume-btn"
                    onClick={() => resumeOperation(currentOperation.id)}
                  >
                    <i className="fas fa-play"></i>
                    Resume Operation
                  </button>
                )}
                <button
                  className="cancel-btn"
                  onClick={() => cancelOperation(currentOperation.id)}
                >
                  <i className="fas fa-stop"></i>
                  Cancel Operation
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Confirmation Dialog */}
      {showConfirmDialog && (
        <div className="confirmation-overlay">
          <div className="confirmation-dialog">
            <div className="dialog-header">
              <h3>
                <i className="fas fa-exclamation-triangle"></i>
                Confirm Stealth Operation
              </h3>
            </div>
            <div className="dialog-content">
              <p>
                Are you sure you want to execute this stealth funding operation?
              </p>
              <div className="confirmation-details">
                <div>‚Ä¢ {currentPlan?.transactionCount} wallets will be funded</div>
                <div>‚Ä¢ {currentPlan?.totalAmount} BNB will be distributed</div>
                <div>‚Ä¢ Operation will take approximately {currentPlan && formatDuration(currentPlan.estimatedDuration)}</div>
                <div>‚Ä¢ This action cannot be undone</div>
              </div>
            </div>
            <div className="dialog-actions">
              <button
                className="cancel-btn"
                onClick={() => setShowConfirmDialog(false)}
              >
                Cancel
              </button>
              <button
                className="confirm-btn"
                onClick={executeOperation}
              >
                <i className="fas fa-rocket"></i>
                Execute Operation
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

