/**
 * Session and Data Cleanup Utilities
 * Comprehensive cleanup functions to clear all user sessions and data
 * while preserving only the admin access key.
 */

import { 
  secureClearAll, 
  clearAllSessionKeys, 
  clearAllSessionPassphrases 
} from './crypto';
import { useSessionStore } from '../store/session';
import { useUserStore } from '../store/users';

// Default admin access key that should be preserved
const DEFAULT_ADMIN_ACCESS_KEY = 'WLSFX-ADM7WWGB2Dm0RuKqMLw';

// All localStorage keys used by the application
const LOCALSTORAGE_KEYS = [
  'bnb-bundler-session',
  'user-management-store', 
  'bnb-bundler-wallets',
  'bnb-bundler-config',
  'bnb-bundler-network',
  'bnb-bundler-analytics',
  'launch-store',
  'wallet-funding-store',
  'treasury-store',
  'bundle-presets-storage',
  'funding-store',
  'faucet-store',
  'execution-store',
  'launch-plan-storage',
  'transaction-store',
];

/**
 * Clear all localStorage data for the application
 */
export const clearAllLocalStorage = (): void => {
  console.log('üßπ Clearing all localStorage data...');

  // Clear specific application keys
  LOCALSTORAGE_KEYS.forEach(key => {
    try {
      localStorage.removeItem(key);
      console.log(`‚úÖ Cleared localStorage key: ${key}`);
    } catch (error) {
      console.warn(`‚ö†Ô∏è Failed to clear localStorage key ${key}:`, error);
    }
  });

  // Also clear any keys that might have been created with different naming
  const allKeys = Object.keys(localStorage);
  allKeys.forEach(key => {
    if (key.includes('bnb-bundler') || key.includes('wallet') || key.includes('user')) {
      try {
        localStorage.removeItem(key);
        console.log(`‚úÖ Cleared additional localStorage key: ${key}`);
      } catch (error) {
        console.warn(`‚ö†Ô∏è Failed to clear additional key ${key}:`, error);
      }
    }
  });

  console.log('‚úÖ All localStorage data cleared');
};

/**
 * Clear all IndexedDB data 
 */
export const clearAllIndexedDB = async (): Promise<void> => {
  console.log('üßπ Clearing all IndexedDB data...');

  try {
    await secureClearAll();
    console.log('‚úÖ All IndexedDB encrypted data cleared');
  } catch (error) {
    console.error('‚ùå Failed to clear IndexedDB data:', error);
    throw error;
  }
};

/**
 * Clear all in-memory session data
 */
export const clearAllInMemoryData = (): void => {
  console.log('üßπ Clearing all in-memory session data...');

  try {
    // Clear session keys and passphrases
    clearAllSessionKeys();
    clearAllSessionPassphrases();
    console.log('‚úÖ All in-memory session data cleared');
  } catch (error) {
    console.error('‚ùå Failed to clear in-memory data:', error);
    throw error;
  }
};

/**
 * Reset user store to initial state with only admin access key
 */
export const resetUserStoreToDefaults = (): void => {
  console.log('üßπ Resetting user store to defaults...');

  try {
    const userStore = useUserStore.getState();

    // Clear current session
    userStore.logout();

    // Reset to initial state using the store's reset method
    userStore.reset();

    console.log('‚úÖ User store reset to defaults with admin access key preserved');
  } catch (error) {
    console.error('‚ùå Failed to reset user store:', error);
    throw error;
  }
};

/**
 * Reset session store to initial state
 */
export const resetSessionStore = (): void => {
  console.log('üßπ Resetting session store...');

  try {
    const sessionStore = useSessionStore.getState();
    sessionStore.clearSession();
    console.log('‚úÖ Session store cleared');
  } catch (error) {
    console.error('‚ùå Failed to reset session store:', error);
    throw error;
  }
};

/**
 * Comprehensive cleanup of all user sessions and data
 * Preserves only the admin access key
 */
export const performCompleteCleanup = async (): Promise<void> => {
  console.log('üöÄ Starting complete system cleanup...');

  try {
    // 1. Clear in-memory data first (session keys, passphrases)
    clearAllInMemoryData();

    // 2. Clear persistent storage (localStorage)
    clearAllLocalStorage();

    // 3. Clear encrypted storage (IndexedDB) 
    await clearAllIndexedDB();

    // 4. Reset store states
    resetSessionStore();
    resetUserStoreToDefaults();

    console.log('üéâ Complete system cleanup successful!');
    console.log('‚ÑπÔ∏è Only admin access key preserved:', DEFAULT_ADMIN_ACCESS_KEY);

  } catch (error) {
    console.error('‚ùå Complete cleanup failed:', error);
    throw error;
  }
};

/**
 * Enhanced logout function that properly clears all session data
 */
export const performSecureLogout = async (): Promise<void> => {
  console.log('üîê Performing secure logout...');

  try {
    const sessionStore = useSessionStore.getState();
    const userStore = useUserStore.getState();

    // Get current session ID before clearing
    const currentSessionId = sessionStore.sessionId;

    // Clear in-memory session data
    if (currentSessionId) {
      clearAllSessionKeys();
      clearAllSessionPassphrases();
    }

    // Clear store sessions
    sessionStore.clearSession();
    userStore.logout();

    console.log('‚úÖ Secure logout completed');

  } catch (error) {
    console.error('‚ùå Secure logout failed:', error);
    throw error;
  }
};

/**
 * Verify cleanup was successful
 */
export const verifyCleanupSuccess = (): boolean => {
  console.log('üîç Verifying cleanup success...');

  try {
    const sessionStore = useSessionStore.getState();
    const userStore = useUserStore.getState();

    // Check session store is clean
    const sessionClean = !sessionStore.isUnlocked && 
                        !sessionStore.sessionId && 
                        !sessionStore.passphraseHash;

    // Check user store is clean  
    const userClean = !userStore.currentSession &&
                     userStore.users.length === 0 &&
                     userStore.adminAccessKey === DEFAULT_ADMIN_ACCESS_KEY;

    // Check localStorage is clean
    const hasSessionData = LOCALSTORAGE_KEYS.some(key => localStorage.getItem(key) !== null);

    const success = sessionClean && userClean && !hasSessionData;

    console.log('üìä Cleanup verification results:');
    console.log(`  Session store clean: ${sessionClean}`);
    console.log(`  User store clean: ${userClean}`);
    console.log(`  LocalStorage clean: ${!hasSessionData}`);
    console.log(`  Overall success: ${success}`);

    return success;

  } catch (error) {
    console.error('‚ùå Cleanup verification failed:', error);
    return false;
  }
};

// Export for easy access
export {
  DEFAULT_ADMIN_ACCESS_KEY,
  LOCALSTORAGE_KEYS,
};

src/utils/encrypted-vault.ts.content:
/**
 * EncryptedKeyVault - Production-ready encrypted storage for private keys
 * - Uses IndexedDB for persistent encrypted storage
 * - Keys are encrypted with session passphrases
 * - Auto-purges on session timeout/logout
 * - Keyed by walletId/sessionId for compartmentalization
 */

import { config } from '../config/env';
import { encryptData, decryptData } from './crypto';

interface VaultEntry {
  walletId: string;
  sessionId: string;
  encryptedKey: string; // Base64 encoded encrypted private key
  salt: string; // Base64 encoded salt
  iv: string; // Base64 encoded IV
  createdAt: number;
  lastAccessed: number;
  expiresAt: number;
}

interface EncryptedKeyVault {
  store: (walletId: string, privateKey: string, sessionId: string, passphrase: string) => Promise<void>;
  retrieve: (walletId: string, sessionId: string, passphrase: string) => Promise<string | null>;
  remove: (walletId: string, sessionId?: string) => Promise<void>;
  purgeExpired: () => Promise<void>;
  purgeSession: (sessionId: string) => Promise<void>;
  clear: () => Promise<void>;
  listWallets: (sessionId?: string) => Promise<string[]>;
}

class IndexedDBKeyVault implements EncryptedKeyVault {
  private dbName = 'bnb-bundler-vault';
  private storeName = 'encrypted-keys';
  private version = 1;
  private db: IDBDatabase | null = null;

  private async getDB(): Promise<IDBDatabase> {
    if (this.db) return this.db;

    return new Promise((resolve, reject) => {
      const request = indexedDB.open(this.dbName, this.version);

      request.onerror = () => reject(new Error('Failed to open IndexedDB'));

      request.onsuccess = () => {
        this.db = request.result;
        resolve(request.result);
      };

      request.onupgradeneeded = () => {
        const db = request.result;
        if (!db.objectStoreNames.contains(this.storeName)) {
          const store = db.createObjectStore(this.storeName, { keyPath: 'walletId' });
          store.createIndex('sessionId', 'sessionId', { unique: false });
          store.createIndex('expiresAt', 'expiresAt', { unique: false });
        }
      };
    });
  }

  async store(walletId: string, privateKey: string, sessionId: string, passphrase: string): Promise<void> {
    const db = await this.getDB();
    const transaction = db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);

    // Encrypt the private key
    const encryptionResult = await encryptData(privateKey, passphrase);

    const entry: VaultEntry = {
      walletId,
      sessionId,
      encryptedKey: btoa(String.fromCharCode(...new Uint8Array(encryptionResult.encrypted))),
      salt: btoa(String.fromCharCode(...encryptionResult.salt)),
      iv: btoa(String.fromCharCode(...encryptionResult.iv)),
      createdAt: Date.now(),
      lastAccessed: Date.now(),
      expiresAt: Date.now() + config.security.sessionTimeout
    };

    return new Promise((resolve, reject) => {
      const request = store.put(entry);
      request.onsuccess = () => resolve();
      request.onerror = () => reject(new Error('Failed to store encrypted key'));
    });
  }

  async retrieve(walletId: string, sessionId: string, passphrase: string): Promise<string | null> {
    const db = await this.getDB();
    const transaction = db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);

    return new Promise((resolve, reject) => {
      const request = store.get(walletId);

      request.onsuccess = async () => {
        const entry: VaultEntry | undefined = request.result;

        if (!entry) {
          resolve(null);
          return;
        }

        // Check if entry is expired
        if (Date.now() > entry.expiresAt) {
          await this.remove(walletId);
          resolve(null);
          return;
        }

        // Check if sessionId matches (security: prevent cross-session access)
        if (entry.sessionId !== sessionId) {
          resolve(null);
          return;
        }

        try {
          // Decrypt the private key
          const encrypted = new Uint8Array(atob(entry.encryptedKey).split('').map(c => c.charCodeAt(0)));
          const salt = new Uint8Array(atob(entry.salt).split('').map(c => c.charCodeAt(0)));
          const iv = new Uint8Array(atob(entry.iv).split('').map(c => c.charCodeAt(0)));

          const privateKey = await decryptData({ encrypted: encrypted.buffer, salt, iv, passphrase });

          // Update last accessed time
          entry.lastAccessed = Date.now();
          store.put(entry);

          resolve(privateKey);
        } catch (error) {
          console.error('Failed to decrypt private key:', error);
          resolve(null);
        }
      };

      request.onerror = () => reject(new Error('Failed to retrieve encrypted key'));
    });
  }

  async remove(walletId: string, sessionId?: string): Promise<void> {
    const db = await this.getDB();
    const transaction = db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);

    return new Promise((resolve, reject) => {
      if (sessionId) {
        // Only remove if sessionId matches (security)
        const getRequest = store.get(walletId);
        getRequest.onsuccess = () => {
          const entry: VaultEntry | undefined = getRequest.result;
          if (entry && entry.sessionId === sessionId) {
            const deleteRequest = store.delete(walletId);
            deleteRequest.onsuccess = () => resolve();
            deleteRequest.onerror = () => reject(new Error('Failed to remove encrypted key'));
          } else {
            resolve(); // Entry doesn't exist or sessionId doesn't match
          }
        };
        getRequest.onerror = () => reject(new Error('Failed to check encrypted key'));
      } else {
        // Remove without sessionId check (for cleanup)
        const request = store.delete(walletId);
        request.onsuccess = () => resolve();
        request.onerror = () => reject(new Error('Failed to remove encrypted key'));
      }
    });
  }

  async purgeExpired(): Promise<void> {
    const db = await this.getDB();
    const transaction = db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);
    const index = store.index('expiresAt');

    return new Promise((resolve, reject) => {
      const request = index.openCursor(IDBKeyRange.upperBound(Date.now()));

      request.onsuccess = () => {
        const cursor = request.result;
        if (cursor) {
          cursor.delete();
          cursor.continue();
        } else {
          resolve();
        }
      };

      request.onerror = () => reject(new Error('Failed to purge expired keys'));
    });
  }

  async purgeSession(sessionId: string): Promise<void> {
    const db = await this.getDB();
    const transaction = db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);
    const index = store.index('sessionId');

    return new Promise((resolve, reject) => {
      const request = index.openCursor(IDBKeyRange.only(sessionId));

      request.onsuccess = () => {
        const cursor = request.result;
        if (cursor) {
          cursor.delete();
          cursor.continue();
        } else {
          resolve();
        }
      };

      request.onerror = () => reject(new Error('Failed to purge session keys'));
    });
  }

  async clear(): Promise<void> {
    const db = await this.getDB();
    const transaction = db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);

    return new Promise((resolve, reject) => {
      const request = store.clear();
      request.onsuccess = () => resolve();
      request.onerror = () => reject(new Error('Failed to clear vault'));
    });
  }

  async listWallets(sessionId?: string): Promise<string[]> {
    const db = await this.getDB();
    const transaction = db.transaction([this.storeName], 'readonly');
    const store = transaction.objectStore(this.storeName);

    return new Promise((resolve, reject) => {
      const wallets: string[] = [];

      if (sessionId) {
        const index = store.index('sessionId');
        const request = index.openCursor(IDBKeyRange.only(sessionId));

        request.onsuccess = () => {
          const cursor = request.result;
          if (cursor) {
            const entry: VaultEntry = cursor.value;
            if (Date.now() <= entry.expiresAt) {
              wallets.push(entry.walletId);
            }
            cursor.continue();
          } else {
            resolve(wallets);
          }
        };

        request.onerror = () => reject(new Error('Failed to list wallets'));
      } else {
        const request = store.openCursor();

        request.onsuccess = () => {
          const cursor = request.result;
          if (cursor) {
            const entry: VaultEntry = cursor.value;
            if (Date.now() <= entry.expiresAt) {
              wallets.push(entry.walletId);
            }
            cursor.continue();
          } else {
            resolve(wallets);
          }
        };

        request.onerror = () => reject(new Error('Failed to list wallets'));
      }
    });
  }
}

// Global vault instance
let vaultInstance: EncryptedKeyVault | null = null;

/**
 * Get the global encrypted key vault instance
 */
export const getKeyVault = (): EncryptedKeyVault => {
  if (!vaultInstance) {
    vaultInstance = new IndexedDBKeyVault();
  }
  return vaultInstance;
};

/**
 * Store a private key in the encrypted vault
 */
export const vaultStoreKey = async (
  walletId: string, 
  privateKey: string, 
  sessionId: string, 
  passphrase: string
): Promise<void> => {
  const vault = getKeyVault();
  await vault.store(walletId, privateKey, sessionId, passphrase);
};

/**
 * Retrieve a private key from the encrypted vault
 */
export const vaultRetrieveKey = async (
  walletId: string, 
  sessionId: string, 
  passphrase: string
): Promise<string | null> => {
  const vault = getKeyVault();
  return await vault.retrieve(walletId, sessionId, passphrase);
};

/**
 * Remove a private key from the encrypted vault
 */
export const vaultRemoveKey = async (
  walletId: string, 
  sessionId?: string
): Promise<void> => {
  const vault = getKeyVault();
  await vault.remove(walletId, sessionId);
};

/**
 * Purge all keys for a specific session (called on logout)
 */
export const vaultPurgeSession = async (sessionId: string): Promise<void> => {
  const vault = getKeyVault();
  await vault.purgeSession(sessionId);
};

/**
 * Purge expired keys (called periodically)
 */
export const vaultPurgeExpired = async (): Promise<void> => {
  const vault = getKeyVault();
  await vault.purgeExpired();
};

/**
 * Clear entire vault (emergency use only)
 */
export const vaultClear = async (): Promise<void> => {
  const vault = getKeyVault();
  await vault.clear();
};

// Auto-cleanup expired vault entries every 10 minutes
setInterval(async () => {
