#!/usr/bin/env node
/**
 * WLSFX Test Token Deployment Script
 * Deploys a real token called "WLSFX Test" using Four.meme API integration
 */

const { ethers } = require('ethers');

// Four.meme API Configuration
const FOURMEME_API_BASE = 'https://four.meme/meme-api';
const TOKEN_MANAGER_ADDRESS = '0x5c952063c7fc8610FFDB798152D69F0B9550762b'; // BSC TokenManager2

// Test Wallet for Four.meme Authentication
const WALLET_PRIVATE_KEY = '0x92b383e4de7c08a24f11482ba736732d36a2b1a68674164eab1b68102fe9cc1d';
const WALLET_ADDRESS = '0x1A443c92b6939C96226893F68cfc58fd40778216';

// BSC Testnet Configuration
const BSC_TESTNET_RPC = 'https://data-seed-prebsc-1-s1.binance.org:8545';

class FourMemeDeployment {
  constructor() {
    this.accessToken = null;
    this.provider = new ethers.JsonRpcProvider(BSC_TESTNET_RPC);
    this.wallet = new ethers.Wallet(WALLET_PRIVATE_KEY, this.provider);
  }

  // Step 1: Generate nonce for authentication
  async generateNonce() {
    console.log('üîë Generating nonce for Four.meme authentication...');

    const request = {
      accountAddress: WALLET_ADDRESS,
      verifyType: 'LOGIN',
      networkCode: 'BSC'
    };

    const response = await fetch(`${FOURMEME_API_BASE}/v1/private/user/nonce/generate`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(request),
    });

    if (!response.ok) {
      throw new Error(`Failed to generate nonce: ${response.status} ${response.statusText}`);
    }

    const result = await response.json();

    if (result.code !== '0') {
      throw new Error(`Four.meme API error: ${result.code}`);
    }

    console.log('‚úÖ Nonce generated successfully');
    return result.data;
  }

  // Step 2: Login to Four.meme with wallet signature
  async login() {
    console.log('üîê Authenticating with Four.meme...');

    const nonce = await this.generateNonce();
    const message = `You are sign in Meme ${nonce}`;
    const signature = await this.wallet.signMessage(message);

    const loginRequest = {
      region: 'WEB',
      langType: 'EN',
      loginIp: '',
      inviteCode: '',
      verifyInfo: {
        address: WALLET_ADDRESS,
        networkCode: 'BSC',
        signature,
        verifyType: 'LOGIN'
      },
      walletName: 'MetaMask'
    };

    const response = await fetch(`${FOURMEME_API_BASE}/v1/private/user/login/dex`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(loginRequest),
    });

    if (!response.ok) {
      throw new Error(`Login failed: ${response.status} ${response.statusText}`);
    }

    const result = await response.json();

    if (result.code !== '0') {
      throw new Error(`Four.meme login error: ${result.code}`);
    }

    this.accessToken = result.data;
    console.log('‚úÖ Successfully authenticated with Four.meme');
    return this.accessToken;
  }

  // Step 3: Create token signature via Four.meme API
  async createTokenSignature() {
    console.log('üìù Creating token signature with Four.meme...');

    const tokenData = {
      name: 'WLSFX Test',
      shortName: 'WLSFX',
      desc: 'Test token for WLSFX verification - deployed via Four.meme API integration',
      imgUrl: 'https://static.four.meme/market/68b871b6-96f7-408c-b8d0-388d804b34275092658264263839640.png', // Default Four.meme logo
      launchTime: Date.now(),
      label: 'Meme',
      lpTradingFee: 0.0025,
      webUrl: '',
      twitterUrl: '',
      telegramUrl: '',
      preSale: '0',
      totalSupply: 1000000000,
      raisedAmount: 24,
      saleRate: 0.8,
      reserveRate: 0,
      funGroup: false,
      clickFun: false,
      symbol: 'BNB',
      raisedToken: {
        symbol: 'BNB',
        nativeSymbol: 'BNB',
        symbolAddress: '0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c',
        deployCost: '0',
        buyFee: '0.01',
        sellFee: '0.01',
        minTradeFee: '0',
        b0Amount: '8',
        totalBAmount: '24',
        totalAmount: '1000000000',
        logoUrl: 'https://static.four.meme/market/68b871b6-96f7-408c-b8d0-388d804b34275092658264263839640.png',
        tradeLevel: ['0.1', '0.5', '1'],
        status: 'PUBLISH',
        buyTokenLink: 'https://pancakeswap.finance/swap',
        reservedNumber: 10,
        saleRate: '0.8',
        networkCode: 'BSC',
        platform: 'MEME'
      }
    };

    const response = await fetch(`${FOURMEME_API_BASE}/v1/private/token/create`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'meme-web-access': this.accessToken,
      },
      body: JSON.stringify(tokenData),
    });

    if (!response.ok) {
      throw new Error(`Token creation request failed: ${response.status} ${response.statusText}`);
    }

    const result = await response.json();

    if (result.code !== '0') {
      throw new Error(`Four.meme token creation error: ${result.code}`);
    }

    console.log('‚úÖ Token signature created successfully');
    return result.data;
  }

  // Step 4: Deploy token to BSC Testnet
  async deployToken(createArg, signature) {
    console.log('üöÄ Deploying token to BSC Testnet...');
    console.log(`üìç Deploying from wallet: ${this.wallet.address}`);

    const tokenManagerABI = [
      'function createToken(bytes calldata createArg, bytes calldata sign) external payable',
      'event TokenCreate(address indexed creator, address indexed token, uint256 requestId, string name, string symbol, uint256 totalSupply, uint256 launchTime, uint256 launchFee)'
    ];

    const contract = new ethers.Contract(TOKEN_MANAGER_ADDRESS, tokenManagerABI, this.wallet);

    // Convert hex strings to bytes
    const createArgBytes = ethers.getBytes(createArg);
    const signatureBytes = ethers.getBytes(signature);

    // Get gas price
    const feeData = await this.provider.getFeeData();
    console.log(`‚õΩ Current gas price: ${ethers.formatUnits(feeData.gasPrice, 'gwei')} gwei`);

    // Estimate gas
    let gasLimit;
    try {
      gasLimit = await contract.createToken.estimateGas(createArgBytes, signatureBytes, {
        value: ethers.parseEther('0.005') // 0.005 BNB creation fee
      });
      gasLimit = gasLimit * BigInt(120) / BigInt(100); // Add 20% buffer
      console.log(`‚õΩ Estimated gas limit: ${gasLimit.toString()}`);
    } catch (gasError) {
      console.warn('‚ö†Ô∏è Gas estimation failed, using default:', gasError.message);
      gasLimit = BigInt(500000); // Default fallback
    }

    // Send transaction
    console.log('üì° Sending deployment transaction...');
    const tx = await contract.createToken(createArgBytes, signatureBytes, {
      value: ethers.parseEther('0.005'), // 0.005 BNB creation fee
      gasLimit: gasLimit,
      gasPrice: feeData.gasPrice
    });

    console.log(`üìã Transaction hash: ${tx.hash}`);
    console.log('‚è≥ Waiting for confirmation...');

    // Wait for confirmation
    const receipt = await tx.wait();

    if (receipt.status === 1) {
      console.log('‚úÖ Token deployed successfully!');
      console.log(`üìã Transaction ID: ${receipt.hash}`);
      console.log(`üè∑Ô∏è Block number: ${receipt.blockNumber}`);
      console.log(`‚õΩ Gas used: ${receipt.gasUsed.toString()}`);
      console.log(`üí∞ Transaction cost: ${ethers.formatEther(receipt.gasUsed * receipt.gasPrice)} BNB`);

      // Parse events to get token address
      const logs = receipt.logs;
      for (const log of logs) {
        try {
          const parsedLog = contract.interface.parseLog(log);
          if (parsedLog.name === 'TokenCreate') {
            console.log(`üéØ Token contract address: ${parsedLog.args.token}`);
            console.log(`üìõ Token name: ${parsedLog.args.name}`);
            console.log(`üè∑Ô∏è Token symbol: ${parsedLog.args.symbol}`);
            break;
          }
        } catch (e) {
          // Skip logs that don't match our interface
        }
      }

      return {
        success: true,
        transactionHash: receipt.hash,
        blockNumber: receipt.blockNumber,
        gasUsed: receipt.gasUsed.toString(),
        cost: ethers.formatEther(receipt.gasUsed * receipt.gasPrice),
        status: 'confirmed'
      };
    } else {
      throw new Error('Transaction failed');
    }
  }

  // Main execution flow
  async execute() {
    try {
      console.log('üéØ Starting WLSFX Test Token Deployment');
      console.log('=====================================');
      console.log('üìã Token Details:');
      console.log('   Name: WLSFX Test');
      console.log('   Symbol: WLSFX'); 
      console.log('   Description: Test token for WLSFX verification');
      console.log('   Platform: Four.meme');
      console.log('   Network: BSC Testnet');
      console.log('=====================================');

      // Step 1: Authenticate with Four.meme
      await this.login();

      // Step 2: Create token signature
      const { createArg, signature } = await this.createTokenSignature();

      // Step 3: Deploy to blockchain
      const result = await this.deployToken(createArg, signature);

      console.log('=====================================');
      console.log('üéâ DEPLOYMENT SUCCESSFUL!');
      console.log('=====================================');
      console.log(`üìã TRANSACTION ID: ${result.transactionHash}`);
      console.log(`üåê BSCScan: https://testnet.bscscan.com/tx/${result.transactionHash}`);
      console.log('=====================================');

      return result;

    } catch (error) {
      console.error('‚ùå Deployment failed:', error.message);
      throw error;
    }
  }
}

// Execute deployment
if (require.main === module) {
  const deployment = new FourMemeDeployment();
  deployment.execute()
    .then(result => {
      console.log('\n‚úÖ Token deployment completed successfully!');
      process.exit(0);
    })
    .catch(error => {
      console.error('\n‚ùå Token deployment failed:', error.message);
      process.exit(1);
    });
}

module.exports = FourMemeDeployment;

